// School Card Component
struct SchoolCard: View {
    let initial: String
    let name: String
    let location: String
    let division: String
    let conference: String
    let ranking: String
    @Binding var showDetail: Bool
    @Binding var detailType: SearchView.DetailType

    var body: some View {
        Button(action: {
            detailType = .school(SearchView.SchoolData(
                initial: initial,
                name: name,
                location: location,
                division: division,
                conference: conference,
                ranking: ranking
            ))
            showDetail = true
        }) {
            HStack(spacing: 16) {
                // School Initial
                Text(initial)
                    .font(.headline)
                    .fontWeight(.bold)
                    .foregroundColor(.blue)
                    .frame(width: 60, height: 60)
                    .background(
                        RoundedRectangle(cornerRadius: 12)
                            .fill(Color.blue.opacity(0.2))
                    )

                VStack(alignment: .leading, spacing: 6) {
                    HStack {
                        Text(name)
                            .font(.headline)
                            .foregroundColor(.white)

                        Spacer()

                        Text(ranking)
                            .font(.subheadline)
                            .fontWeight(.semibold)
                            .foregroundColor(.blue)
                            .padding(.horizontal, 8)
                            .padding(.vertical, 4)
                            .background(
                                Capsule()
                                    .fill(Color.blue.opacity(0.2))
                            )
                    }

                    Text(location)
                        .font(.subheadline)
                        .foregroundColor(.gray)

                    HStack(spacing: 8) {
                        Text(division)
                            .font(.caption)
                            .foregroundColor(.gray)

                        Text("•")
                            .foregroundColor(.gray)

                        Text(conference)
                            .font(.caption)
                            .foregroundColor(.gray)
                    }
                }
            }
            .padding(16)
            .background(
                RoundedRectangle(cornerRadius: 16)
                    .fill(Color.black.opacity(0.4))
                    .overlay(
                        RoundedRectangle(cornerRadius: 16)
                            .stroke(Color.white.opacity(0.3), lineWidth: 1)
                    )
            )
        }
        .buttonStyle(PlainButtonStyle())
    }
}

