// Coach Card Component
struct CoachCard: View {
    let name: String
    let title: String
    let school: String
    let schoolInitial: String
    let yearsExperience: String
    @Binding var showDetail: Bool
    @Binding var detailType: SearchView.DetailType

    var body: some View {
        Button(action: {
            detailType = .coach(SearchView.CoachData(
                name: name,
                title: title,
                school: school,
                schoolInitial: schoolInitial,
                yearsExperience: yearsExperience
            ))
            showDetail = true
        }) {
            HStack(spacing: 16) {
                // Coach Avatar
                Image(systemName: "person.crop.circle.fill")
                    .resizable()
                    .frame(width: 60, height: 60)
                    .foregroundColor(.gray)

                VStack(alignment: .leading, spacing: 6) {
                    Text(name)
                        .font(.headline)
                        .foregroundColor(.white)

                    Text(title)
                        .font(.subheadline)
                        .foregroundColor(.blue)

                    HStack(spacing: 6) {
                        Text(schoolInitial)
                            .font(.caption)
                            .fontWeight(.semibold)
                            .foregroundColor(.blue)

                        Text(school)
                            .font(.caption)
                            .foregroundColor(.gray)

                        Text("•")
                            .foregroundColor(.gray)

                        Text(yearsExperience)
                            .font(.caption)
                            .foregroundColor(.gray)
                    }
                }

                Spacer()

                Image(systemName: "chevron.right")
                    .foregroundColor(.gray)
            }
            .padding(16)
            .background(
                RoundedRectangle(cornerRadius: 16)
                    .fill(Color.black.opacity(0.4))
                    .overlay(
                        RoundedRectangle(cornerRadius: 16)
                            .stroke(Color.white.opacity(0.3), lineWidth: 1)
                    )
            )
        }
        .buttonStyle(PlainButtonStyle())
    }
}

// School Detail View
struct SchoolDetailView: View {
    let initial: String
    let name: String
    let location: String
    let division: String
    let conference: String
    let ranking: String
    @Binding var navigationStack: [SearchView.DetailType]

    var body: some View {
        ZStack {
            LinearGradient(
                gradient: Gradient(colors: [
                    Color(red: 0.1, green: 0.1, blue: 0.2),
                    Color(red: 0.05, green: 0.05, blue: 0.15)
                ]),
                startPoint: .topLeading,
                endPoint: .bottomTrailing
            )
            .ignoresSafeArea()

            ScrollView {
                VStack(spacing: 20) {
                    // School Header
                    VStack(spacing: 16) {
                        Text(initial)
                            .font(.system(size: 48))
                            .fontWeight(.bold)
                            .foregroundColor(.blue)
                            .frame(width: 120, height: 120)
                            .background(
                                RoundedRectangle(cornerRadius: 24)
                                    .fill(Color.blue.opacity(0.2))
                            )

                        Text(name)
                            .font(.title)
                            .fontWeight(.bold)
                            .foregroundColor(.white)

                        Text(location)
                            .font(.subheadline)
                            .foregroundColor(.gray)

                        HStack(spacing: 16) {
                            VStack {
                                Text(division)
                                    .font(.caption)
                                    .foregroundColor(.gray)
                                Text(conference)
                                    .font(.subheadline)
                                    .fontWeight(.semibold)
                                    .foregroundColor(.white)
                            }

                            Divider()
                                .frame(height: 30)

                            VStack {
                                Text("Ranking")
                                    .font(.caption)
                                    .foregroundColor(.gray)
                                Text(ranking)
                                    .font(.subheadline)
                                    .fontWeight(.semibold)
                                    .foregroundColor(.blue)
                            }
                        }
                    }
                    .padding(24)
                    .background(
                        RoundedRectangle(cornerRadius: 20)
                            .fill(Color.black.opacity(0.4))
                            .overlay(
                                RoundedRectangle(cornerRadius: 20)
                                    .stroke(Color.white.opacity(0.3), lineWidth: 1)
                            )
                    )
                    .padding(.horizontal)

                    // Coaching Staff Section
                    VStack(alignment: .leading, spacing: 16) {
                        Text("Coaching Staff")
                            .font(.title3)
                            .fontWeight(.bold)
                            .foregroundColor(.white)
                            .padding(.horizontal)

                        VStack(spacing: 12) {
                            NavigationLink(value: SearchView.DetailType.coach(SearchView.CoachData(
                                name: "John Smith",
                                title: "Head Coach",
                                school: name,
                                schoolInitial: initial,
                                yearsExperience: "15 years"
                            ))) {
                                CoachCardContent(
                                    name: "John Smith",
                                    title: "Head Coach",
                                    school: name,
                                    schoolInitial: initial,
                                    yearsExperience: "15 years"
                                )
                            }

                            NavigationLink(value: SearchView.DetailType.coach(SearchView.CoachData(
                                name: "Lisa Anderson",
                                title: "Assistant Coach",
                                school: name,
                                schoolInitial: initial,
                                yearsExperience: "10 years"
                            ))) {
                                CoachCardContent(
                                    name: "Lisa Anderson",
                                    title: "Assistant Coach",
                                    school: name,
                                    schoolInitial: initial,
                                    yearsExperience: "10 years"
                                )
                            }
                        }
                        .padding(.horizontal)
                    }

                    // Program Info
                    VStack(alignment: .leading, spacing: 16) {
                        Text("Program Information")
                            .font(.title3)
                            .fontWeight(.bold)
                            .foregroundColor(.white)

                        VStack(alignment: .leading, spacing: 12) {
                            DetailInfoRow(label: "Athletic Website", value: "athletics.school.edu")
                            DetailInfoRow(label: "Facility", value: "Main Arena (5,000 capacity)")
                            DetailInfoRow(label: "Championships", value: "3 National Titles")
                            DetailInfoRow(label: "Recent Record", value: "28-6 (2023)")
                        }
                    }
                    .padding(20)
                    .background(
                        RoundedRectangle(cornerRadius: 16)
                            .fill(Color.black.opacity(0.4))
                            .overlay(
                                RoundedRectangle(cornerRadius: 16)
                                    .stroke(Color.white.opacity(0.3), lineWidth: 1)
                            )
                    )
                    .padding(.horizontal)
                }
                .padding(.vertical, 20)
            }
        }
        .navigationBarTitleDisplayMode(.inline)
        .toolbar {
            ToolbarItem(placement: .navigationBarTrailing) {
                Button(action: {}) {
                    Image(systemName: "bookmark")
                        .foregroundColor(.blue)
                }
            }
        }
    }
}

// Coach Detail View
struct CoachDetailView: View {
    let name: String
    let title: String
    let school: String
    let schoolInitial: String
    let yearsExperience: String
    @Binding var navigationStack: [SearchView.DetailType]

    var body: some View {
        let backgroundGradient = LinearGradient(
            gradient: Gradient(colors: [
                Color(red: 0.1, green: 0.1, blue: 0.2),
                Color(red: 0.05, green: 0.05, blue: 0.15)
            ]),
            startPoint: .topLeading,
            endPoint: .bottomTrailing
        )

        return ZStack {
            backgroundGradient
                .ignoresSafeArea()

            ScrollView {
                VStack(spacing: 20) {
                    coachHeaderSection
                    coachInfoSection
                    achievementsSection
                }
                .padding(.vertical, 20)
            }
        }
        .navigationBarTitleDisplayMode(.inline)
        .toolbar {
            ToolbarItem(placement: .navigationBarTrailing) {
                Button(action: {}) {
                    Image(systemName: "star")
                        .foregroundColor(.white)
                }
            }
        }
    }

    private var coachHeaderSection: some View {
        VStack(spacing: 16) {
            Image(systemName: "person.crop.circle.fill")
                .resizable()
                .frame(width: 120, height: 120)
                .foregroundColor(.gray)

            Text(name)
                .font(.title)
                .fontWeight(.bold)
                .foregroundColor(.white)

            Text(title)
                .font(.headline)
                .foregroundColor(.blue)

            schoolLinkButton
        }
        .padding(24)
        .background(
            RoundedRectangle(cornerRadius: 20)
                .fill(Color.black.opacity(0.4))
                .overlay(
                    RoundedRectangle(cornerRadius: 20)
                        .stroke(Color.white.opacity(0.3), lineWidth: 1)
                )
        )
        .padding(.horizontal)
    }

    private var schoolLinkButton: some View {
        NavigationLink(value: SearchView.DetailType.school(SearchView.SchoolData(
            initial: schoolInitial,
            name: school,
            location: "Location",
            division: "Division I",
            conference: "Conference",
            ranking: "#1"
        ))) {
            HStack(spacing: 8) {
                Text(schoolInitial)
                    .font(.caption)
                    .fontWeight(.semibold)
                    .foregroundColor(.blue)
                    .frame(width: 40, height: 40)
                    .background(
                        RoundedRectangle(cornerRadius: 8)
                            .fill(Color.blue.opacity(0.2))
                    )

                Text(school)
                    .font(.subheadline)
                    .foregroundColor(.white)

                Image(systemName: "chevron.right")
                    .font(.caption)
                    .foregroundColor(.gray)
            }
            .padding(.horizontal, 16)
            .padding(.vertical, 12)
            .background(
                RoundedRectangle(cornerRadius: 12)
                    .fill(Color.black.opacity(0.4))
                    .overlay(
                        RoundedRectangle(cornerRadius: 12)
                            .stroke(Color.white.opacity(0.3), lineWidth: 1)
                    )
            )
        }
    }

    private var coachInfoSection: some View {
        VStack(alignment: .leading, spacing: 16) {
            Text("About")
                .font(.title3)
                .fontWeight(.bold)
                .foregroundColor(.white)

            VStack(alignment: .leading, spacing: 12) {
                DetailInfoRow(label: "Experience", value: yearsExperience)
                DetailInfoRow(label: "Email", value: "coach@school.edu")
                DetailInfoRow(label: "Phone", value: "(555) 123-4567")
                DetailInfoRow(label: "Specialization", value: "Middle Blocker Development")
            }
        }
        .padding(20)
        .background(
            RoundedRectangle(cornerRadius: 16)
                .fill(Color.black.opacity(0.4))
                .overlay(
                    RoundedRectangle(cornerRadius: 16)
                        .stroke(Color.white.opacity(0.3), lineWidth: 1)
                )
        )
        .padding(.horizontal)
    }

    private var achievementsSection: some View {
        VStack(alignment: .leading, spacing: 16) {
            Text("Achievements")
                .font(.title3)
                .fontWeight(.bold)
                .foregroundColor(.white)

            VStack(alignment: .leading, spacing: 12) {
                AchievementRow(text: "3x Conference Coach of the Year")
                AchievementRow(text: "National Championship (2021)")
                AchievementRow(text: "15+ Players to Professional League")
            }
        }
        .padding(20)
        .background(
            RoundedRectangle(cornerRadius: 16)
                .fill(Color.black.opacity(0.4))
                .overlay(
                    RoundedRectangle(cornerRadius: 16)
                        .stroke(Color.white.opacity(0.3), lineWidth: 1)
                )
        )
        .padding(.horizontal)
    }
}

// Coach Card Content (reusable component for inline coach cards)
