}

// Detail Info Row Component (reusable)
struct DetailInfoRow: View {
    let label: String
    let value: String

    var body: some View {
        HStack {
            Text(label)
                .font(.subheadline)
                .foregroundColor(.gray)
            Spacer()
            Text(value)
                .font(.subheadline)
                .foregroundColor(.white)
        }
    }
}

// Achievement Row Component
struct AchievementRow: View {
    let text: String

    var body: some View {
        HStack(spacing: 12) {
            Image(systemName: "star.fill")
                .foregroundColor(.yellow)
            Text(text)
                .font(.subheadline)
                .foregroundColor(.white)
        }
    }
}

// Athletes List View
struct AthletesListView: View {
    var body: some View {
        ScrollView {
            VStack(spacing: 12) {
                NavigationLink(destination: PublicProfileView(user: User.sampleUser)) {
