import Foundation
import PostgresNIO

@MainActor
class DatabaseService: ObservableObject {
    static let shared = DatabaseService()

    @Published var schools: [School] = []
    @Published var coaches: [Coach] = []
    @Published var isLoading = false
    @Published var errorMessage: String?

    private init() {}

    // MARK: - Fetch Schools
    func fetchSchools() async {
        isLoading = true
        errorMessage = nil

        do {
            let connection = try await PostgresConnection.connect(
                configuration: try parseConnectionString(Config.neonDatabaseURL),
                id: 1,
                logger: .init(label: "postgres")
            )

            defer {
                Task {
                    try? await connection.close()
                }
            }

            let query = """
                SELECT id, name, location, division, conference, ranking, initial,
                       athletic_website, facility, championships, recent_record
                FROM schools
                ORDER BY name ASC
            """

            let rows = try await connection.query(PostgresQuery(stringLiteral: query), logger: .init(label: "postgres"))

            var fetchedSchools: [School] = []
            for try await row in rows {
                let school = School(
                    id: row["id"].string ?? "",
                    name: row["name"].string ?? "",
                    location: row["location"].string ?? "",
                    division: row["division"].string ?? "",
                    conference: row["conference"].string ?? "",
                    ranking: row["ranking"].string,
                    initial: row["initial"].string ?? "",
                    athleticWebsite: row["athletic_website"].string,
                    facility: row["facility"].string,
                    championships: row["championships"].string,
                    recentRecord: row["recent_record"].string
                )
                fetchedSchools.append(school)
            }

            schools = fetchedSchools
            isLoading = false
        } catch {
            errorMessage = "Failed to fetch schools: \(error.localizedDescription)"
            isLoading = false
        }
    }

    // MARK: - Fetch Coaches
    func fetchCoaches() async {
        isLoading = true
        errorMessage = nil

        do {
            let connection = try await PostgresConnection.connect(
                configuration: try parseConnectionString(Config.neonDatabaseURL),
                id: 2,
                logger: .init(label: "postgres")
            )

            defer {
                Task {
                    try? await connection.close()
                }
            }

            let query = """
                SELECT c.id, c.name, c.title, c.school_id, s.name as school_name,
                       s.initial as school_initial, c.years_experience, c.email,
                       c.phone, c.specialization
                FROM coaches c
                JOIN schools s ON c.school_id = s.id
                ORDER BY c.name ASC
            """

            let rows = try await connection.query(PostgresQuery(stringLiteral: query), logger: .init(label: "postgres"))

            var fetchedCoaches: [Coach] = []
            for try await row in rows {
                let coach = Coach(
                    id: row["id"].string ?? "",
                    name: row["name"].string ?? "",
                    title: row["title"].string ?? "",
                    schoolId: row["school_id"].string ?? "",
                    schoolName: row["school_name"].string ?? "",
                    schoolInitial: row["school_initial"].string ?? "",
                    yearsExperience: row["years_experience"].string ?? "",
                    email: row["email"].string,
                    phone: row["phone"].string,
                    specialization: row["specialization"].string,
                    achievements: nil // Could be fetched from a separate table
                )
                fetchedCoaches.append(coach)
            }

            coaches = fetchedCoaches
            isLoading = false
        } catch {
            errorMessage = "Failed to fetch coaches: \(error.localizedDescription)"
            isLoading = false
        }
    }

    // MARK: - Search
    func searchSchools(query: String) -> [School] {
        if query.isEmpty {
            return schools
        }
        return schools.filter { school in
            school.name.localizedCaseInsensitiveContains(query) ||
            school.location.localizedCaseInsensitiveContains(query) ||
            school.conference.localizedCaseInsensitiveContains(query)
        }
    }

    func searchCoaches(query: String) -> [Coach] {
        if query.isEmpty {
            return coaches
        }
        return coaches.filter { coach in
            coach.name.localizedCaseInsensitiveContains(query) ||
            coach.schoolName.localizedCaseInsensitiveContains(query) ||
            coach.title.localizedCaseInsensitiveContains(query)
        }
    }

    // MARK: - Helper
    private func parseConnectionString(_ connectionString: String) throws -> PostgresConnection.Configuration {
        guard let url = URL(string: connectionString) else {
            throw NSError(domain: "Invalid connection string", code: -1)
        }

        let host = url.host ?? "localhost"
        let port = url.port ?? 5432
        let database = String(url.path.dropFirst()) // Remove leading "/"
        let username = url.user ?? ""
        let password = url.password ?? ""

        return PostgresConnection.Configuration(
            host: host,
            port: port,
            username: username,
            password: password,
            database: database,
            tls: .require(try .init(configuration: .clientDefault))
        )
    }
}

