import Foundation

@MainActor
class DatabaseService: ObservableObject {
    static let shared = DatabaseService()

    @Published var schools: [School] = []
    @Published var coaches: [Coach] = []
    @Published var isLoading = false
    @Published var errorMessage: String?

    private init() {}

    // MARK: - Fetch Schools
    func fetchSchools() async {
        isLoading = true
        errorMessage = nil

        do {
            let query = """
                SELECT id, name, location, division, conference, ranking, initial,
                       athletic_website, facility, championships, recent_record
                FROM schools
                ORDER BY name ASC
            """

            let result = try await executeQuery(query)

            var fetchedSchools: [School] = []
            if let rows = result["rows"] as? [[String: Any]] {
                for row in rows {
                    let school = School(
                        id: row["id"] as? String ?? "",
                        name: row["name"] as? String ?? "",
                        location: row["location"] as? String ?? "",
                        division: row["division"] as? String ?? "",
                        conference: row["conference"] as? String ?? "",
                        ranking: row["ranking"] as? String,
                        initial: row["initial"] as? String ?? "",
                        athleticWebsite: row["athletic_website"] as? String,
                        facility: row["facility"] as? String,
                        championships: row["championships"] as? String,
                        recentRecord: row["recent_record"] as? String
                    )
                    fetchedSchools.append(school)
                }
            }

            schools = fetchedSchools
            isLoading = false
        } catch {
            errorMessage = "Failed to fetch schools: \(error.localizedDescription)"
            isLoading = false
        }
    }

    // MARK: - Fetch Coaches
    func fetchCoaches() async {
        isLoading = true
        errorMessage = nil

        do {
            let query = """
                SELECT c.id, c.name, c.title, c.school_id, s.name as school_name,
                       s.initial as school_initial, c.years_experience, c.email,
                       c.phone, c.specialization
                FROM coaches c
                JOIN schools s ON c.school_id = s.id
                ORDER BY c.name ASC
            """

            let result = try await executeQuery(query)

            var fetchedCoaches: [Coach] = []
            if let rows = result["rows"] as? [[String: Any]] {
                for row in rows {
                    let coach = Coach(
                        id: row["id"] as? String ?? "",
                        name: row["name"] as? String ?? "",
                        title: row["title"] as? String ?? "",
                        schoolId: row["school_id"] as? String ?? "",
                        schoolName: row["school_name"] as? String ?? "",
                        schoolInitial: row["school_initial"] as? String ?? "",
                        yearsExperience: row["years_experience"] as? String ?? "",
                        email: row["email"] as? String,
                        phone: row["phone"] as? String,
                        specialization: row["specialization"] as? String,
                        achievements: nil
                    )
                    fetchedCoaches.append(coach)
                }
            }

            coaches = fetchedCoaches
            isLoading = false
        } catch {
            errorMessage = "Failed to fetch coaches: \(error.localizedDescription)"
            isLoading = false
        }
    }

    // MARK: - Execute Query via Neon SQL API
    private func executeQuery(_ query: String) async throws -> [String: Any] {
        // Parse connection string
        guard let url = URL(string: Config.neonDatabaseURL),
              let host = url.host,
              let database = url.path.split(separator: "/").last,
              let username = url.user,
              let password = url.password else {
            throw NSError(domain: "Invalid connection string", code: -1)
        }

        // Neon SQL over HTTP endpoint
        let apiURL = URL(string: "https://\(host)/sql")!

        var request = URLRequest(url: apiURL)
        request.httpMethod = "POST"
        request.addValue("application/json", forHTTPHeaderField: "Content-Type")

        // Basic auth
        let credentials = "\(username):\(password)".data(using: .utf8)!.base64EncodedString()
        request.addValue("Basic \(credentials)", forHTTPHeaderField: "Authorization")

        let body: [String: Any] = [
            "query": query,
            "params": []
        ]
        request.httpBody = try JSONSerialization.data(withJSONObject: body)

        let (data, response) = try await URLSession.shared.data(for: request)

        guard let httpResponse = response as? HTTPURLResponse,
              (200...299).contains(httpResponse.statusCode) else {
            throw NSError(domain: "HTTP Error", code: (response as? HTTPURLResponse)?.statusCode ?? -1)
        }

        guard let json = try JSONSerialization.jsonObject(with: data) as? [String: Any] else {
            throw NSError(domain: "Invalid JSON response", code: -1)
        }

        return json
    }

    // MARK: - Search
    func searchSchools(query: String) -> [School] {
        if query.isEmpty {
            return schools
        }
        return schools.filter { school in
            school.name.localizedCaseInsensitiveContains(query) ||
            school.location.localizedCaseInsensitiveContains(query) ||
            school.conference.localizedCaseInsensitiveContains(query)
        }
    }

    func searchCoaches(query: String) -> [Coach] {
        if query.isEmpty {
            return coaches
        }
        return coaches.filter { coach in
            coach.name.localizedCaseInsensitiveContains(query) ||
            coach.schoolName.localizedCaseInsensitiveContains(query) ||
            coach.title.localizedCaseInsensitiveContains(query)
        }
    }
}

