import { NextRequest, NextResponse } from 'next/server';
import { neon } from '@neondatabase/serverless';

export async function GET(request: NextRequest) {
  try {
    const searchParams = request.nextUrl.searchParams;
    const query = searchParams.get('q') || '';
    const type = searchParams.get('type') || 'all'; // 'schools', 'coaches', 'athletes', or 'all'

    const sql = neon(process.env.DATABASE_URL!);
    const results: any = {};

    // Search schools
    if (type === 'schools' || type === 'all') {
      const schools = await sql`
        SELECT id, name, location, division, conference, ranking, initial,
               athletic_website, facility, championships, recent_record
        FROM schools
        WHERE
          name ILIKE ${'%' + query + '%'} OR
          location ILIKE ${'%' + query + '%'} OR
          conference ILIKE ${'%' + query + '%'}
        ORDER BY name ASC
      `;
      results.schools = schools;
    }

    // Search coaches
    if (type === 'coaches' || type === 'all') {
      const coaches = await sql`
        SELECT c.id, c.name, c.title, c.school_id, s.name as school_name,
               s.initial as school_initial, c.years_experience, c.email,
               c.phone, c.specialization
        FROM coaches c
        JOIN schools s ON c.school_id = s.id
        WHERE
          c.name ILIKE ${'%' + query + '%'} OR
          c.title ILIKE ${'%' + query + '%'} OR
          s.name ILIKE ${'%' + query + '%'}
        ORDER BY c.name ASC
      `;
      results.coaches = coaches;
    }

    // Search athletes (placeholder for future implementation)
    if (type === 'athletes' || type === 'all') {
      // TODO: Add athletes table and search
      results.athletes = [];
    }

    return NextResponse.json(results);
  } catch (error) {
    console.error('Search error:', error);
    return NextResponse.json(
      { error: 'Search failed' },
      { status: 500 }
    );
  }
}

