```

### **4. Example Route Structure:**

```typescript
// apps/api/src/routes/v1/dbsearch.routes.ts
import { FastifyPluginAsync } from 'fastify';
import { searchController } from '../../controllers/search.controller';
import { SearchRequestSchema } from '@repo/types';

const dbsearchRoutes: FastifyPluginAsync = async (fastify) => {
  fastify.get('/', {
    schema: {
      querystring: SearchRequestSchema,
      response: {
        200: SearchResponseSchema
      }
    },
    handler: searchController.search
  });
};

export default dbsearchRoutes;
```

```typescript
// apps/api/src/controllers/search.controller.ts
import { searchService } from '../services/search.service';

export const searchController = {
  async search(request, reply) {
    const { type, query } = request.query;
    const results = await searchService.search(type, query);
    return reply.send(results);
  }
};
```

```typescript
