//
//  BrandButton.swift
//  Enhanced button component with icon, loading state, and haptic feedback
//
//  Usage:
//  BrandButton(title: "Sign In", icon: "arrow.right", style: .primary) {
//      // action
//  }
//

import SwiftUI

/// App-specific button wrapper with enhanced features
struct BrandButton: View {
    let title: String
    let icon: String?
    let style: ButtonStyle
    let size: ButtonSize
    let isLoading: Bool
    let action: () -> Void

    enum ButtonStyle {
        case primary
        case secondary
        case destructive
        case outline

        var dsIntent: DSButton.Intent {
            switch self {
            case .primary: return .primary
            case .secondary: return .secondary
            case .destructive: return .destructive
            case .outline: return .secondary
            }
        }
    }

    enum ButtonSize {
        case small
        case medium
        case large

        var dsSize: DSButton.Size {
            switch self {
            case .small: return .s
            case .medium: return .m
            case .large: return .l
            }
        }
    }

    @Environment(\.isEnabled) private var isEnabled
    @Environment(\.designTheme) private var theme

    init(
        title: String,
        icon: String? = nil,
        style: ButtonStyle = .primary,
        size: ButtonSize = .medium,
        isLoading: Bool = false,
        action: @escaping () -> Void
    ) {
        self.title = title
        self.icon = icon
        self.style = style
        self.size = size
        self.isLoading = isLoading
        self.action = action
    }

    var body: some View {
        if style == .outline {
            outlineButton
        } else {
            standardButton
        }
    }

    // MARK: - Standard Button (using DSButton)

    private var standardButton: some View {
        Button(action: {
            HapticManager.shared.medium()
            action()
        }) {
            HStack(spacing: .spacing3) {
                if isLoading {
                    ProgressView()
                        .tint(foregroundColor)
                } else {
                    if let icon = icon {
                        Image(systemName: icon)
                    }
                    Text(title)
                        .font(theme.font(.body))
                }
            }
            .frame(maxWidth: .infinity)
            .padding(.horizontal, horizontalPadding)
            .padding(.vertical, verticalPadding)
            .foregroundStyle(foregroundColor)
            .background(backgroundColor)
            .clipShape(RoundedRectangle(cornerRadius: .radiusLarge, style: .continuous))
            .shadow(color: shadowColor, radius: 8, y: 4)
        }
        .disabled(!isEnabled || isLoading)
        .opacity(isEnabled && !isLoading ? 1.0 : 0.5)
        .accessibilityLabel(title)
    }

    // MARK: - Outline Button

    private var outlineButton: some View {
        Button(action: {
            HapticManager.shared.medium()
            action()
        }) {
            HStack(spacing: .spacing3) {
                if isLoading {
                    ProgressView()
                        .tint(theme.color(.primary))
                } else {
                    if let icon = icon {
                        Image(systemName: icon)
                    }
                    Text(title)
                        .font(theme.font(.body))
                }
            }
            .frame(maxWidth: .infinity)
            .padding(.horizontal, horizontalPadding)
            .padding(.vertical, verticalPadding)
            .foregroundStyle(theme.color(.primary))
            .background(
                RoundedRectangle(cornerRadius: .radiusLarge, style: .continuous)
                    .stroke(theme.color(.primary), lineWidth: 2)
            )
        }
        .disabled(!isEnabled || isLoading)
        .opacity(isEnabled && !isLoading ? 1.0 : 0.5)
        .accessibilityLabel(title)
    }

    // MARK: - Helper Properties

    private var horizontalPadding: CGFloat {
        switch size {
        case .small: return .spacing3   // 12pt
        case .medium: return .spacing4  // 16pt
        case .large: return .spacing5   // 20pt
        }
    }

    private var verticalPadding: CGFloat {
        switch size {
        case .small: return .spacing2   // 8pt (closest to 10)
        case .medium: return .spacing4  // 16pt (closest to 14)
        case .large: return .spacing5   // 20pt (closest to 18)
        }
    }

    private var foregroundColor: Color {
        switch style {
        case .primary:
            return theme.color(.primaryforeground)
        case .secondary:
            return theme.color(.secondaryforeground)
        case .destructive:
            return theme.color(.destructiveforeground)
        case .outline:
            return theme.color(.primary)
        }
    }

    private var backgroundColor: Color {
        switch style {
        case .primary:
            return theme.color(.primary)
        case .secondary:
            return theme.color(.secondary)
        case .destructive:
            return theme.color(.destructive)
        case .outline:
            return Color.clear
        }
    }

    private var shadowColor: Color {
        switch style {
        case .primary:
            return theme.color(.ring).opacity(0.12)
        case .secondary:
            return Color.black.opacity(0.05)
        case .destructive:
            return theme.color(.destructive).opacity(0.12)
        case .outline:
            return Color.clear
        }
    }
}

// MARK: - Preview

#if DEBUG
struct BrandButton_Previews: PreviewProvider {
    static var previews: some View {
        Group {
            // Light mode
            VStack(spacing: .spacing4) {
                BrandButton(title: "Primary Button", style: .primary) {}
                BrandButton(title: "With Icon", icon: "arrow.right", style: .primary) {}
                BrandButton(title: "Secondary", style: .secondary) {}
                BrandButton(title: "Destructive", style: .destructive) {}
                BrandButton(title: "Outline", style: .outline) {}
                BrandButton(title: "Loading", style: .primary, isLoading: true) {}

                HStack(spacing: .spacing3) {
                    BrandButton(title: "Small", style: .primary, size: .small) {}
                    BrandButton(title: "Medium", style: .primary, size: .medium) {}
                    BrandButton(title: "Large", style: .primary, size: .large) {}
                }
            }
            .padding()
            .withDesignTheme()
            .background(DesignTheme.defaultLight.color(.background))
            .previewDisplayName("Light Mode")

            // Dark mode
            VStack(spacing: .spacing4) {
                BrandButton(title: "Primary Button", style: .primary) {}
                BrandButton(title: "With Icon", icon: "arrow.right", style: .primary) {}
                BrandButton(title: "Secondary", style: .secondary) {}
                BrandButton(title: "Destructive", style: .destructive) {}
                BrandButton(title: "Outline", style: .outline) {}
            }
            .padding()
            .withDesignTheme()
            .background(DesignTheme.defaultDark.color(.background))
            .preferredColorScheme(.dark)
            .previewDisplayName("Dark Mode")
        }
    }
}
#endif

//
//  DesignTokens+Extensions.swift
//  Extensions for DesignSystem providing spacing, radius, gradients, and environment access
//
//  Usage:
//  .padding(.spacing4)
//  .cornerRadius(.radiusLarge)
//  LinearGradient.athleteGradient
//  @Environment(\.designTheme) var theme
//

import SwiftUI

// MARK: - Spacing Extensions (4pt grid)
extension CGFloat {
    /// 4pt - Extra Small spacing
    static let spacing1: CGFloat = 4
    /// 8pt - Small spacing
    static let spacing2: CGFloat = 8
    /// 12pt - Medium spacing
    static let spacing3: CGFloat = 12
    /// 16pt - Large spacing (default)
    static let spacing4: CGFloat = 16
    /// 20pt - Extra Large spacing
    static let spacing5: CGFloat = 20
    /// 24pt - 2X Large spacing
    static let spacing6: CGFloat = 24
    /// 32pt - 3X Large spacing
    static let spacing8: CGFloat = 32
    /// 40pt - 4X Large spacing
    static let spacing10: CGFloat = 40
}

// MARK: - Corner Radius
extension CGFloat {
    /// 8pt - Small corner radius
    static let radiusSmall: CGFloat = 8
    /// 12pt - Medium corner radius
    static let radiusMedium: CGFloat = 12
    /// 16pt - Large corner radius (default)
    static let radiusLarge: CGFloat = 16
    /// 20pt - Extra Large corner radius
    static let radiusXLarge: CGFloat = 20
}

// MARK: - Shadow & Elevation
extension View {
    /// Applies a subtle shadow (elevation 1)
    /// Use for slightly raised elements like buttons or small cards
    func shadowSubtle() -> some View {
        self.shadow(color: Color.black.opacity(0.05), radius: 2, x: 0, y: 1)
    }

    /// Applies a medium shadow (elevation 2)
    /// Use for cards, modals, and raised content
    func shadowMedium() -> some View {
        self.shadow(color: Color.black.opacity(0.08), radius: 8, x: 0, y: 4)
    }

    /// Applies a large shadow (elevation 3)
    /// Use for floating action buttons, popovers, and prominent elements
    func shadowLarge() -> some View {
        self.shadow(color: Color.black.opacity(0.12), radius: 16, x: 0, y: 8)
    }

    /// Applies an extra large shadow (elevation 4)
    /// Use for modals, sheets, and elements that should appear above all content
    func shadowXLarge() -> some View {
        self.shadow(color: Color.black.opacity(0.16), radius: 24, x: 0, y: 12)
    }
}

// MARK: - Gradients for Sports App
extension LinearGradient {
    /// Athlete gradient (indigo/purple)
    static let athleteGradient = LinearGradient(
        colors: [Color(hex: "#6366F1"), Color(hex: "#818CF8")],
        startPoint: .topLeading,
        endPoint: .bottomTrailing
    )

    /// Coach gradient (red/pink)
    static let coachGradient = LinearGradient(
        colors: [Color(hex: "#EF4444"), Color(hex: "#F87171")],
        startPoint: .topLeading,
        endPoint: .bottomTrailing
    )

    /// Default brand gradient (uses athlete gradient)
    static let brandGradient = athleteGradient

    /// Feed background gradient (dark variant)
    static let feedBackground = LinearGradient(
        colors: [
            Color(hex: "#1E293B"),
            Color(hex: "#0F172A")
        ],
        startPoint: .top,
        endPoint: .bottom
    )
}

// MARK: - Environment Key for Theme
struct DesignThemeKey: EnvironmentKey {
    static let defaultValue: DesignTheme = .defaultLight
}

extension EnvironmentValues {
    var designTheme: DesignTheme {
        get { self[DesignThemeKey.self] }
        set { self[DesignThemeKey.self] = newValue }
    }
}

// MARK: - View Extension for Easy Theme Access
extension View {
    /// Injects the appropriate design theme based on current color scheme
    func withDesignTheme() -> some View {
        self.modifier(DesignThemeModifier())
    }
}

private struct DesignThemeModifier: ViewModifier {
    @Environment(\.colorScheme) private var colorScheme

    func body(content: Content) -> some View {
        content
            .environment(\.designTheme, colorScheme == .dark ? .defaultDark : .defaultLight)
    }
}

// MARK: - Semantic Font Extensions
extension Font {
    /// Section title font (title2 with semibold weight)
    static let sectionTitle: Font = .system(.title2, design: .default, weight: .semibold)

    /// Body text font (standard body text)
    static let bodyText: Font = .system(.body, design: .default)
}
