import { config } from "dotenv";
import { resolve } from "path";
import { drizzle } from "drizzle-orm/node-postgres";
import * as schema from "./schema/public";

config({ path: resolve(__dirname, "../.env") });

const dbOwner = drizzle({
  connection: {
    connectionString: process.env.DATABASE_OWNER_URL!,
    ssl: true,
  },
  schema,
});

// TODO: Will use Neon's authenticated role connection string in the future
// For now uses a standard connection string for authenticated app users
const dbUser = drizzle({
  connection: {
    connectionString: process.env.DATABASE_USER_URL!,
    ssl: true,
  },
  schema,
});

// TODO: Will be for AI agent roles in the future
// const dbAgent = drizzle({
//   connection: {
//     connectionString: process.env.DATABASE_AGENT_URL!,
//     ssl: true,
//   },
//   schema,
// });

export { dbOwner, dbUser };

export {
  streamingSessions,
  sessionParticipants,
  sessionAnalytics,
} from "./schema/public/streaming_sessions";
export { users } from "./schema/public/users";
export { roles } from "./schema/public/roles";
export { userDomainRoles, domainEnum } from "./schema/public/user_domain_roles";
export { athleteProfiles } from "./schema/public/athlete_profiles";
export { guardianProfiles } from "./schema/public/guardian_profiles";
export { fanProfiles } from "./schema/public/fan_profiles";
export { athleteGuardianRelationships } from "./schema/public/athlete_guardian_relationships";

import { pgTable, uuid, text, integer, boolean, timestamp, pgEnum } from "drizzle-orm/pg-core";
import { relations } from "drizzle-orm";
import { users } from "./users";

export const sessionStatusEnum = pgEnum("session_status", [
  "scheduled",
  "active",
  "ended",
  "cancelled",
]);

export const streamingSessions = pgTable("streaming_sessions", {
  id: uuid("id").primaryKey().defaultRandom(),
  slug: text("slug").notNull().unique(),
  roomName: text("room_name").notNull().unique(),
  title: text("title"),
  description: text("description"),
  streamerId: integer("streamer_id")
    .notNull()
    .references(() => users.id, { onDelete: "cascade" }),
  status: sessionStatusEnum("status").notNull().default("scheduled"),
  scheduledStart: timestamp("scheduled_start", { withTimezone: true }),
  startedAt: timestamp("started_at", { withTimezone: true }),
  endedAt: timestamp("ended_at", { withTimezone: true }),
  isPublic: boolean("is_public").notNull().default(true),
  maxParticipants: integer("max_participants").default(50),
  liveKitRoomSid: text("livekit_room_sid"),
  recordingEnabled: boolean("recording_enabled").notNull().default(false),
  createdAt: timestamp("created_at", { withTimezone: true, mode: "string" }).defaultNow().notNull(),
  updatedAt: timestamp("updated_at", { withTimezone: true, mode: "string" }).defaultNow().notNull(),
});

export const participantRoleEnum = pgEnum("participant_role", ["streamer", "viewer"]);

export const sessionParticipants = pgTable("session_participants", {
  id: uuid("id").primaryKey().defaultRandom(),
  sessionId: uuid("session_id")
    .notNull()
    .references(() => streamingSessions.id, { onDelete: "cascade" }),
  userId: integer("user_id").references(() => users.id, {
    onDelete: "cascade",
  }),
  participantIdentity: text("participant_identity").notNull(),
  participantName: text("participant_name").notNull(),
  role: participantRoleEnum("role").notNull(),
  joinedAt: timestamp("joined_at", { withTimezone: true }).defaultNow().notNull(),
  leftAt: timestamp("left_at", { withTimezone: true }),
  tokenIssuedAt: timestamp("token_issued_at", { withTimezone: true }),
  tokenExpiresAt: timestamp("token_expires_at", { withTimezone: true }),
});

export const streamingSessionsRelations = relations(streamingSessions, ({ one, many }) => ({
  streamer: one(users, {
    fields: [streamingSessions.streamerId],
    references: [users.id],
  }),
  participants: many(sessionParticipants),
  analytics: one(sessionAnalytics, {
    fields: [streamingSessions.id],
    references: [sessionAnalytics.sessionId],
  }),
}));

export const sessionParticipantsRelations = relations(sessionParticipants, ({ one }) => ({
  session: one(streamingSessions, {
    fields: [sessionParticipants.sessionId],
    references: [streamingSessions.id],
  }),
  user: one(users, {
    fields: [sessionParticipants.userId],
    references: [users.id],
  }),
}));

export const sessionAnalytics = pgTable("session_analytics", {
  sessionId: uuid("session_id").primaryKey().notNull().references(() => streamingSessions.id, { onDelete: "cascade" }),
  peakParticipants: integer("peak_participants").notNull().default(0),
  totalParticipants: integer("total_participants").notNull().default(0),
  uniqueViewers: integer("unique_viewers").notNull().default(0),
  avgViewerDurationSeconds: integer("avg_viewer_duration_seconds"),
  totalViewMinutes: integer("total_view_minutes").notNull().default(0),
  streamerDurationSeconds: integer("streamer_duration_seconds"),
  processedAt: timestamp("processed_at", { withTimezone: true }).defaultNow().notNull(),
  createdAt: timestamp("created_at", { withTimezone: true }).defaultNow().notNull(),
});

export const sessionAnalyticsRelations = relations(sessionAnalytics, ({ one }) => ({
  session: one(streamingSessions, {
    fields: [sessionAnalytics.sessionId],
    references: [streamingSessions.id],
  }),
}));

import { z } from "zod";

// =============================================================================
// COMMON PARAMETER SCHEMAS
// =============================================================================

export const SessionIdParamsSchema = z.object({
  sessionId: z.string({ message: "Session ID is required" }),
});

export const IdParamsSchema = z.object({
  id: z.string({ message: "ID is required" }),
});

export const SlugParamsSchema = z.object({
  slug: z.string({ message: "Slug is required" }),
});

export type SessionIdParams = z.infer<typeof SessionIdParamsSchema>;
export type IdParams = z.infer<typeof IdParamsSchema>;
export type SlugParams = z.infer<typeof SlugParamsSchema>;

export const CreateSessionRequestSchema = z.object({
  title: z
    .string({ message: "Session title is required" })
    .max(50, "Title must be 50 characters or less"),
  description: z.string().max(200, "Description must be 200 characters or less").optional(),
  roomName: z
    .string({ message: "Room name is required" })
    .max(50, "Room name must be 50 characters or less"),
});

export const CreateSessionResponseSchema = z.object({
  sessionId: z.string(),
  slug: z.string(),
  token: z.string(),
  roomName: z.string(),
  playbackUrl: z.string(),
  createdAt: z.iso.datetime(),
});

export type CreateSessionRequest = z.infer<typeof CreateSessionRequestSchema>;
export type CreateSessionResponse = z.infer<typeof CreateSessionResponseSchema>;

export const JoinSessionRequestSchema = z.object({
  participantName: z
    .string({ message: "Participant name is required" })
    .max(50, "Participant name must be 50 characters or less"),
  participantId: z.string({ message: "Participant ID is required" }),
});

export const JoinSessionResponseSchema = z.object({
  token: z.string(),
  participantId: z.string(),
  roomName: z.string(),
  sessionId: z.string(),
});

export type JoinSessionRequest = z.infer<typeof JoinSessionRequestSchema>;
export type JoinSessionResponse = z.infer<typeof JoinSessionResponseSchema>;

// =============================================================================
// LEAVE SESSION CONTRACTS
// =============================================================================

export const LeaveSessionRequestSchema = z.object({
  participantId: z.string({ message: "Participant ID is required" }),
});

export type LeaveSessionRequest = z.infer<typeof LeaveSessionRequestSchema>;

// =============================================================================
// SESSION VIEWER TOKEN CONTRACTS
// =============================================================================

export const SessionViewerTokenRequestSchema = z.object({
  viewerIdentity: z.string({ message: "Viewer identity is required" }),
  viewerName: z
    .string({ message: "Viewer name is required" })
    .max(50, "Viewer name must be 50 characters or less"),
});

export const SessionViewerTokenResponseSchema = z.object({
  token: z.string(),
});

export type SessionViewerTokenRequest = z.infer<typeof SessionViewerTokenRequestSchema>;
export type SessionViewerTokenResponse = z.infer<typeof SessionViewerTokenResponseSchema>;

// =============================================================================
// SESSION PARTICIPANTS CONTRACTS
// =============================================================================

export const SessionParticipantsResponseSchema = z.array(
  z.object({
    id: z.string(),
    sessionId: z.string(),
    userId: z.number().nullable(),
    participantIdentity: z.string(),
    participantName: z.string(),
    role: z.enum(["streamer", "viewer"]),
    joinedAt: z.iso.datetime(),
    leftAt: z.iso.datetime().nullable(),
    tokenIssuedAt: z.iso.datetime().nullable(),
    tokenExpiresAt: z.iso.datetime().nullable(),
    user: z
      .object({
        id: z.number(),
        firstName: z.string(),
        lastName: z.string(),
        userName: z.string(),
      })
      .nullable(),
  }),
);

export type SessionParticipantsResponse = z.infer<typeof SessionParticipantsResponseSchema>;

// =============================================================================
// SESSION STATUS CONTRACTS
// =============================================================================

export const SessionStatusResponseSchema = z.object({
  sessionId: z.string(),
  roomExists: z.boolean(),
  participantCount: z.number(),
  status: z.enum(["scheduled", "active", "ended", "cancelled"]),
  liveKitRoom: z
    .object({
      sid: z.string(),
      name: z.string(),
      numParticipants: z.number(),
      metadata: z.string(),
      creationTime: z.number(),
      activeRecording: z.boolean(),
    })
    .optional(),
  participants: z
    .array(
      z.object({
        identity: z.string(),
        name: z.string(),
        joinedAt: z.number(),
        state: z.number(),
        isPublisher: z.boolean(),
      }),
    )
    .optional(),
});

export type SessionStatusResponse = z.infer<typeof SessionStatusResponseSchema>;

// =============================================================================
// ACTIVE SESSIONS CONTRACTS
// =============================================================================

export const ActiveSessionsResponseSchema = z.array(
  z.object({
    sessionId: z.string(),
    slug: z.string(),
    title: z.string().nullable(),
    streamerId: z.number(),
    roomName: z.string(),
    status: z.enum(["scheduled", "active", "ended", "cancelled"]),
    startedAt: z.iso.datetime().nullable(),
    participantCount: z.number(),
    liveKitRoomExists: z.boolean(),
    liveKitRoomSid: z.string().optional(),
    // Streamer information
    streamerName: z.string(),
    streamerUserName: z.string(),
    streamerProfileUrl: z.string().nullable(),
  }),
);

export type ActiveSessionsResponse = z.infer<typeof ActiveSessionsResponseSchema>;

// =============================================================================
// SESSION DATA RESPONSE (for GET session)
// =============================================================================

export const SessionDataResponseSchema = z.object({
  id: z.string(),
  slug: z.string(),
  roomName: z.string(),
  title: z.string().nullable(),
  description: z.string().nullable(),
  streamerId: z.number(),
  status: z.enum(["scheduled", "active", "ended", "cancelled"]),
  scheduledStart: z.iso.datetime().nullable(),
  startedAt: z.iso.datetime().nullable(),
  endedAt: z.iso.datetime().nullable(),
  isPublic: z.boolean(),
  maxParticipants: z.number().nullable(),
  liveKitRoomSid: z.string().nullable(),
  recordingEnabled: z.boolean(),
  createdAt: z.iso.datetime(),
  updatedAt: z.iso.datetime(),
});

export const SessionWithStreamerResponseSchema = SessionDataResponseSchema.extend({
  streamer: z.object({
    firstName: z.string(),
    lastName: z.string(),
    userName: z.string(),
  }),
});

export type SessionDataResponse = z.infer<typeof SessionDataResponseSchema>;
export type SessionWithStreamerResponse = z.infer<typeof SessionWithStreamerResponseSchema>;

// =============================================================================
// VIEWER COUNTS CONTRACTS
// =============================================================================

export const ViewerCountsRequestSchema = z.object({
  sessionIds: z.array(z.string()).min(1, "At least one session ID is required"),
});

export const ViewerCountsResponseSchema = z.record(z.string(), z.number());

export type ViewerCountsRequest = z.infer<typeof ViewerCountsRequestSchema>;
export type ViewerCountsResponse = z.infer<typeof ViewerCountsResponseSchema>;

