import SwiftUI

struct ProfileView: View {
    @Environment(\.designTheme) private var theme
    @State private var currentUser = User.currentUser
    @State private var showingPrivacySettings = false

    var body: some View {
        NavigationStack {
            GeometryReader { geo in
                ZStack {
                    // Background photo - constrained to screen bounds
                    Image(currentUser.profileImageName)
                        .resizable()
                        .aspectRatio(contentMode: .fill)
                        .frame(width: geo.size.width, height: geo.size.height)
                        .clipped()
                        .accessibilityHidden(true)

                    // Gradient overlay
                    LinearGradient(
                        gradient: Gradient(colors: [
                            Color.clear,
                            theme.color(.foreground).opacity(0.65)
                        ]),
                        startPoint: UnitPoint(x: 0.5, y: 0.55),
                        endPoint: .bottom
                    )

                    // Content - Vertical icon navigation with liquid glass container
                    VStack {
                        Spacer()

                        // Vertical navigation toolbar with liquid glass appearance
                        VStack(spacing: .spacing2) {
                            NavigationLink(destination: AboutView()) {
                                Image(systemName: "person.fill")
                                    .font(.system(size: 20))
                                    .foregroundStyle(.white)
                                    .frame(width: 44, height: 44)
                            }
                            .accessibilityLabel("About")

                            NavigationLink(destination: AthleticsView()) {
                                Image(systemName: "figure.run")
                                    .font(.system(size: 20))
                                    .foregroundStyle(.white)
                                    .frame(width: 44, height: 44)
