import SwiftUI

struct PublicProfileView: View {
    let user: User
    @Environment(\.designTheme) private var theme
    @State private var showingGoLive = false

    // Computed properties for abbreviated sport and position
    private var abbreviatedSport: String {
        switch user.sport.lowercased() {
        case "volleyball": return "VB"
        case "basketball": return "BB"
        case "football": return "FB"
        case "soccer": return "SOC"
        case "baseball": return "BSB"
        case "softball": return "SB"
        case "track & field", "track and field": return "T&F"
        case "cross country": return "XC"
        case "swimming": return "SWIM"
        case "tennis": return "TEN"
        case "golf": return "GOLF"
        case "lacrosse": return "LAX"
        case "hockey": return "HKY"
        case "wrestling": return "WR"
        default: return user.sport
        }
    }

    private var abbreviatedPosition: String {
        switch user.position.lowercased() {
        // Volleyball
        case "middle blocker": return "MB"
        case "outside hitter": return "OH"
        case "opposite hitter": return "OPP"
        case "setter": return "S"
        case "libero": return "L"
        case "defensive specialist": return "DS"
        // Basketball
        case "point guard": return "PG"
        case "shooting guard": return "SG"
        case "small forward": return "SF"
        case "power forward": return "PF"
        case "center": return "C"
        // Football
        case "quarterback": return "QB"
        case "running back": return "RB"
        case "wide receiver": return "WR"
        case "tight end": return "TE"
        case "offensive line": return "OL"
        case "defensive line": return "DL"
        case "linebacker": return "LB"
        case "cornerback": return "CB"
        case "safety": return "S"
        default: return user.position
        }
    }

    var body: some View {
        NavigationView {
            GeometryReader { geo in
                ZStack {
                    // Background photo
                    Image(user.profileImageName)
                        .resizable()
                        .aspectRatio(contentMode: .fill)
                        .frame(width: geo.size.width, height: geo.size.height)
                        .clipped()
                        .accessibilityHidden(true)

                    // Gradient
                    LinearGradient(
                        gradient: Gradient(colors: [
                            Color.clear,
                            Color.black.opacity(0.65)
                        ]),
                        startPoint: UnitPoint(x: 0.5, y: 0.55),
                        endPoint: .bottom
                    )

                    // Content
                    ScrollView {
                        VStack(spacing: 0) {
                            Spacer()
                                .frame(height: geo.size.height * 0.55)

                            // All content with consistent background
                            VStack(spacing: 0) {
                                // Profile Info Card
                                VStack(spacing: .spacing1) {
                                    Text("\(user.fullName) - \(String(user.classYear.suffix(2)))")
                                        .font(theme.font(.title2))
                                        .fontWeight(.bold)
                                        .foregroundStyle(.white)

                                    Text("\(abbreviatedSport) ‚Ä¢ \(abbreviatedPosition)")
                                        .font(theme.font(.subheadline))
                                        .foregroundStyle(.white)
                                }
                                .frame(maxWidth: .infinity)
                                .photoOverlayCard()
                                .padding(.horizontal, .spacing4)
                                .padding(.top, .spacing4)

                                // Action Buttons
                                HStack(spacing: .spacing3) {
                                    Button(action: {}) {
                                        HStack {
                                            Image(systemName: "person.badge.plus")
                                            Text("Follow")
                                        }
                                        .frame(maxWidth: .infinity)
                                        .padding(.vertical, .spacing3)
                                        .background(theme.color(.primary))
                                        .foregroundStyle(theme.color(.primaryforeground))
                                        .clipShape(Capsule())
                                    }

                                    Button(action: {}) {
                                        HStack {
                                            Image(systemName: "message")
                                            Text("Message")
                                        }
                                        .frame(maxWidth: .infinity)
                                        .padding(.vertical, .spacing3)
                                        .background(theme.color(.muted))
                                        .foregroundStyle(theme.color(.foreground))
                                        .clipShape(Capsule())
                                    }
                                }
                                .padding(.horizontal, .spacing4)
                                .padding(.top, .spacing4)

                                // Menu Items
                                VStack(spacing: .spacing3) {
                                    MenuItem(icon: "person.fill", title: "About", iconColor: theme.color(.primary), destination: PublicAboutView(user: user))
                                    MenuItem(icon: "video.fill", title: "Film", iconColor: theme.color(.destructive), destination: Text("Film View - Coming Soon"))
                                    MenuItem(icon: "pencil", title: "Posts", iconColor: theme.color(.success), destination: Text("Posts View - Coming Soon"))
                                    MenuItem(icon: "chart.bar.fill", title: "Athletics", iconColor: theme.color(.warning), destination: Text("Athletics View - Coming Soon"))
                                    MenuItem(icon: "graduate", title: "Academics", iconColor: theme.color(.accent), destination: Text("Academics View - Coming Soon"))
                                }
                                .padding(.horizontal, .spacing4)
                                .padding(.top, .spacing4)
                                .padding(.bottom, 100)
                            }
                        }
                    }
                    .scrollContentBackground(.hidden)
                }
                .ignoresSafeArea()
            }
            .navigationBarTitleDisplayMode(.inline)
            .toolbar {
                ToolbarItem(placement: .navigationBarTrailing) {
                    Button(action: {}) {
                        Image(systemName: "ellipsis")
                            .foregroundStyle(.white)
                    }
                }
            }
        }
        .withDesignTheme()
    }
}

