    // Helper function to calculate image brightness
    private func calculateImageBrightness(imageName: String) -> Double {
        guard let uiImage = UIImage(named: imageName) else { return 0.5 }
        guard let cgImage = uiImage.cgImage else { return 0.5 }

        let width = cgImage.width
        let height = cgImage.height
        let bytesPerPixel = 4
        let bytesPerRow = bytesPerPixel * width
        let bitsPerComponent = 8

        var pixelData = [UInt8](repeating: 0, count: width * height * bytesPerPixel)

        guard let context = CGContext(
            data: &pixelData,
            width: width,
            height: height,
            bitsPerComponent: bitsPerComponent,
            bytesPerRow: bytesPerRow,
            space: CGColorSpaceCreateDeviceRGB(),
            bitmapInfo: CGImageAlphaInfo.premultipliedLast.rawValue
        ) else { return 0.5 }

        context.draw(cgImage, in: CGRect(x: 0, y: 0, width: width, height: height))

        // Sample the bottom portion of the image where the card will be
        let sampleHeight = height / 4
        let startY = height - sampleHeight

        var totalBrightness: Double = 0
        var pixelCount = 0

        for y in startY..<height {
            for x in 0..<width {
                let offset = (y * width + x) * bytesPerPixel
                let r = Double(pixelData[offset])
                let g = Double(pixelData[offset + 1])
                let b = Double(pixelData[offset + 2])

                // Calculate perceived brightness using standard formula
                let brightness = (0.299 * r + 0.587 * g + 0.114 * b) / 255.0
                totalBrightness += brightness
                pixelCount += 1
            }
        }

        return pixelCount > 0 ? totalBrightness / Double(pixelCount) : 0.5
    }
}

