                            .accessibilityLabel("About")

                            NavigationLink(destination: Text("Athletics View - Coming Soon")) {
                                Image(systemName: "figure.run")
                                    .font(.system(size: 20))
                                    .foregroundStyle(contentColor)
                                    .frame(width: 44, height: 44)
                            }
                            .accessibilityLabel("Athletics")

                            NavigationLink(destination: Text("Academics View - Coming Soon")) {
                                Image(systemName: "book.fill")
                                    .font(.system(size: 20))
                                    .foregroundStyle(contentColor)
                                    .frame(width: 44, height: 44)
