//
//  MessagesView.swift
//  RecruitAI
//
//  Created by Corey Young on 10/2/25.
//

import SwiftUI

struct MessagesView: View {
    @Environment(\.designTheme) private var theme
    @State private var selectedTab: MessageTab = .direct
    @State private var showCompose = false

    enum MessageTab: String, CaseIterable {
        case direct = "Direct"
        case emails = "Emails"
    }

    var body: some View {
        NavigationView {
            VStack(spacing: 0) {
                // Tab Selector
                HStack(spacing: 0) {
                    ForEach(MessageTab.allCases, id: \.self) { tab in
                        Button(action: {
                            selectedTab = tab
                        }) {
                            VStack(spacing: .spacing2) {
                                Text(tab.rawValue)
                                    .font(.subheadline)
                                    .fontWeight(.semibold)
                                    .foregroundStyle(selectedTab == tab ? theme.color(.foreground) : theme.color(.mutedforeground))

                                Rectangle()
                                    .fill(selectedTab == tab ? theme.color(.primary) : Color.clear)
                                    .frame(height: 2)
                            }
                            .frame(maxWidth: .infinity)
                        }
                    }
                }
                .padding(.horizontal)
                .padding(.top, .spacing2)

                // Content based on selected tab
                if selectedTab == .direct {
                    DirectMessagesView()
                } else {
                    EmailsView()
                }
            }
            .withDesignTheme()
            .brandBackground(.feed)
            .navigationTitle("Messages")
            .navigationBarTitleDisplayMode(.inline)
            .toolbar {
                ToolbarItem(placement: .navigationBarTrailing) {
                    Button(action: { showCompose = true }) {
                        Image(systemName: "square.and.pencil")
                            .foregroundStyle(theme.color(.foreground))
                    }
                }
            }
            .sheet(isPresented: $showCompose) {
                ComposeMessageView(messageType: selectedTab)
            }
        }
    }
}

// Direct Messages View (iMessage style)
struct DirectMessagesView: View {
    var body: some View {
        ScrollView {
            VStack(spacing: 0) {
                MessageRow(
                    name: "Coach Martinez",
                    avatar: "person.circle.fill",
                    lastMessage: "Great job at practice today! Keep it up.",
                    timestamp: "2h ago",
                    unread: true
                )

                MessageRow(
                    name: "Sarah Johnson",
                    avatar: "person.circle.fill",
                    lastMessage: "Did you see the game schedule?",
                    timestamp: "5h ago",
                    unread: false
                )

                MessageRow(
                    name: "Team Group",
                    avatar: "person.3.fill",
                    lastMessage: "Emma: See you all tomorrow!",
                    timestamp: "1d ago",
                    unread: false
                )

                MessageRow(
                    name: "Emma Davis",
                    avatar: "person.circle.fill",
                    lastMessage: "Thanks for the help with that drill!",
                    timestamp: "2d ago",
                    unread: false
                )
            }
            .padding(.top, 12)
        }
    }
}

// Message Row Component (iMessage style)
struct MessageRow: View {
    @Environment(\.designTheme) private var theme
    let name: String
    let avatar: String
    let lastMessage: String
    let timestamp: String
    let unread: Bool

    var body: some View {
        NavigationLink(destination: ChatView(name: name)) {
            HStack(spacing: .spacing3) {
                // Avatar
                Image(systemName: avatar)
                    .resizable()
                    .frame(width: 50, height: 50)
                    .foregroundStyle(theme.color(.mutedforeground))

                VStack(alignment: .leading, spacing: .spacing1) {
                    HStack {
                        Text(name)
                            .font(.headline)
                            .foregroundStyle(theme.color(.foreground))
                            .fontWeight(unread ? .bold : .semibold)

                        Spacer()

                        Text(timestamp)
                            .font(.caption)
                            .foregroundStyle(theme.color(.mutedforeground))
                    }

                    HStack {
                        Text(lastMessage)
                            .font(.subheadline)
                            .foregroundStyle(unread ? theme.color(.foreground) : theme.color(.mutedforeground))
                            .fontWeight(unread ? .semibold : .regular)
                            .lineLimit(2)

                        Spacer()

                        if unread {
                            Circle()
                                .fill(theme.color(.primary))
                                .frame(width: 10, height: 10)
                        }
                    }
                }
            }
            .glassCard(cornerRadius: .radiusSmall, padding: .spacing3)
        }
        .buttonStyle(PlainButtonStyle())
    }
}

// Emails View
struct EmailsView: View {
    var body: some View {
        ScrollView {
            VStack(spacing: 0) {
                EmailRow(
                    sender: "University of Texas",
                    subject: "Recruiting Opportunity",
                    preview: "We're interested in learning more about your volleyball career...",
                    timestamp: "1h ago",
                    unread: true
                )

                EmailRow(
                    sender: "Coach Johnson",
                    subject: "Camp Invitation",
                    preview: "You're invited to our summer volleyball camp...",
                    timestamp: "3h ago",
                    unread: true
                )

                EmailRow(
                    sender: "NCAA Clearinghouse",
                    subject: "Registration Confirmation",
                    preview: "Your registration has been confirmed...",
                    timestamp: "1d ago",
                    unread: false
                )

                EmailRow(
                    sender: "Stanford Athletics",
                    subject: "Showcase Event",
                    preview: "Join us for our annual volleyball showcase...",
                    timestamp: "2d ago",
                    unread: false
                )
            }
            .padding(.top, 12)
        }
    }
}

// Email Row Component
struct EmailRow: View {
    @Environment(\.designTheme) private var theme
    let sender: String
    let subject: String
    let preview: String
    let timestamp: String
    let unread: Bool

    var body: some View {
        NavigationLink(destination: EmailDetailView(sender: sender, subject: subject)) {
            HStack(alignment: .top, spacing: .spacing3) {
                // Icon
                Image(systemName: "envelope.fill")
                    .foregroundStyle(unread ? theme.color(.primary) : theme.color(.mutedforeground))
                    .frame(width: 50, height: 50)

                VStack(alignment: .leading, spacing: .spacing1_5) {
                    HStack {
                        Text(sender)
                            .font(.headline)
                            .foregroundStyle(theme.color(.foreground))
                            .fontWeight(unread ? .bold : .semibold)

                        Spacer()

                        Text(timestamp)
                            .font(.caption)
                            .foregroundStyle(theme.color(.mutedforeground))
                    }

                    Text(subject)
                        .font(.subheadline)
                        .foregroundStyle(unread ? theme.color(.foreground) : theme.color(.mutedforeground))
                        .fontWeight(unread ? .semibold : .medium)
                        .lineLimit(1)

                    Text(preview)
                        .font(.caption)
                        .foregroundStyle(theme.color(.mutedforeground))
                        .lineLimit(2)
                }

                if unread {
                    Circle()
                        .fill(theme.color(.primary))
                        .frame(width: 10, height: 10)
                }
            }
            .glassCard(cornerRadius: .radiusSmall, padding: .spacing3)
        }
        .buttonStyle(PlainButtonStyle())
    }
}

// Chat View (iMessage style)
struct ChatView: View {
    @Environment(\.designTheme) private var theme
    let name: String
    @State private var messageText = ""
    @Environment(\.dismiss) var dismiss

    var body: some View {
        VStack(spacing: 0) {
            ScrollView {
                VStack(spacing: .spacing4) {
                    // Sample messages
                    ChatBubble(text: "Hey! How's training going?", isSent: false)
                    ChatBubble(text: "Going great! Just finished practice.", isSent: true)
                    ChatBubble(text: "That's awesome! Keep it up ğŸ’ª", isSent: false)
                }
                .padding()
            }

            // Message input
            HStack(spacing: .spacing3) {
                TextField("Message", text: $messageText)
                    .glassCard(cornerRadius: .radiusLarge, padding: .spacing2_5)
                    .foregroundStyle(theme.color(.foreground))

                Button(action: {}) {
                    Image(systemName: "arrow.up.circle.fill")
                        .font(.system(size: 32))
                        .foregroundStyle(theme.color(.primary))
                }
            }
            .padding()
        }
        .withDesignTheme()
        .brandBackground(.feed)
        .navigationTitle(name)
        .navigationBarTitleDisplayMode(.inline)
    }
}

// Chat Bubble Component
struct ChatBubble: View {
    let text: String
    let isSent: Bool

    var body: some View {
        HStack {
            if isSent { Spacer() }

            Text(text)
                .padding(12)
                .background(
                    RoundedRectangle(cornerRadius: 18)
                        .fill(isSent ? Color.blue : Color.white.opacity(0.2))
                )
                .foregroundColor(.white)

            if !isSent { Spacer() }
        }
    }
}

// Email Detail View
struct EmailDetailView: View {
    let sender: String
    let subject: String
    @Environment(\.dismiss) var dismiss

    var body: some View {
        ZStack {
            LinearGradient(
                gradient: Gradient(colors: [
                    Color(red: 0.1, green: 0.1, blue: 0.2),
                    Color(red: 0.05, green: 0.05, blue: 0.15)
                ]),
                startPoint: .topLeading,
                endPoint: .bottomTrailing
            )
            .ignoresSafeArea()

            ScrollView {
                VStack(alignment: .leading, spacing: 20) {
                    // Email header
                    VStack(alignment: .leading, spacing: 12) {
                        Text(subject)
                            .font(.title2)
                            .fontWeight(.bold)
                            .foregroundColor(.white)

                        HStack {
                            Image(systemName: "person.circle.fill")
                                .foregroundColor(.gray)
                            Text(sender)
                                .font(.subheadline)
                                .foregroundColor(.gray)
                        }
                    }
                    .padding()
                    .background(
                        RoundedRectangle(cornerRadius: 16)
                            .fill(Color.black.opacity(0.4))
                            .overlay(
                                RoundedRectangle(cornerRadius: 16)
                                    .stroke(Color.white.opacity(0.3), lineWidth: 1)
                            )
                    )

                    // Email body
                    Text("Email content would appear here. This is a placeholder for the full email message content.")
                        .font(.body)
                        .foregroundColor(.white)
                        .padding()
                        .background(
                            RoundedRectangle(cornerRadius: 16)
                                .fill(Color.black.opacity(0.4))
                                .overlay(
                                    RoundedRectangle(cornerRadius: 16)
                                        .stroke(Color.white.opacity(0.3), lineWidth: 1)
                                )
                        )
                }
                .padding()
            }
        }
        .navigationBarTitleDisplayMode(.inline)
        .toolbar {
            ToolbarItem(placement: .navigationBarTrailing) {
                Button(action: {}) {
                    Image(systemName: "arrowshape.turn.up.left.fill")
                        .foregroundColor(.blue)
                }
            }
        }
    }
}

// Compose Message View
struct ComposeMessageView: View {
    let messageType: MessagesView.MessageTab
    @Environment(\.dismiss) var dismiss
    @State private var recipient = ""
    @State private var subject = ""
    @State private var messageContent = ""

    var body: some View {
        NavigationView {
            ZStack {
                LinearGradient(
                    gradient: Gradient(colors: [
                        Color(red: 0.1, green: 0.1, blue: 0.2),
                        Color(red: 0.05, green: 0.05, blue: 0.15)
                    ]),
                    startPoint: .topLeading,
                    endPoint: .bottomTrailing
                )
                .ignoresSafeArea()

                VStack(spacing: 16) {
                    // Recipient
                    VStack(alignment: .leading, spacing: 8) {
                        Text("To")
                            .font(.caption)
                            .foregroundColor(.gray)

                        TextField("Recipient", text: $recipient)
                            .padding()
                            .background(
                                RoundedRectangle(cornerRadius: 12)
                                    .fill(Color.black.opacity(0.4))
                                    .overlay(
                                        RoundedRectangle(cornerRadius: 12)
                                            .stroke(Color.white.opacity(0.3), lineWidth: 1)
                                    )
                            )
                            .foregroundColor(.white)
                    }

                    // Subject (only for emails)
                    if messageType == .emails {
                        VStack(alignment: .leading, spacing: 8) {
                            Text("Subject")
                                .font(.caption)
                                .foregroundColor(.gray)

                            TextField("Subject", text: $subject)
                                .padding()
                                .background(
                                    RoundedRectangle(cornerRadius: 12)
                                        .fill(Color.black.opacity(0.4))
                                        .overlay(
                                            RoundedRectangle(cornerRadius: 12)
                                                .stroke(Color.white.opacity(0.3), lineWidth: 1)
                                        )
                                )
                                .foregroundColor(.white)
                        }
                    }

                    // Message
                    VStack(alignment: .leading, spacing: 8) {
                        Text("Message")
                            .font(.caption)
                            .foregroundColor(.gray)

                        TextEditor(text: $messageContent)
                            .frame(height: 200)
                            .scrollContentBackground(.hidden)
                            .padding()
                            .background(
                                RoundedRectangle(cornerRadius: 12)
                                    .fill(Color.black.opacity(0.4))
                                    .overlay(
                                        RoundedRectangle(cornerRadius: 12)
                                            .stroke(Color.white.opacity(0.3), lineWidth: 1)
                                    )
                            )
                            .foregroundColor(.white)
                    }

                    Spacer()
                }
                .padding()
            }
            .navigationTitle(messageType == .direct ? "New Message" : "New Email")
            .navigationBarTitleDisplayMode(.inline)
            .toolbar {
                ToolbarItem(placement: .navigationBarLeading) {
                    Button("Cancel") {
                        dismiss()
                    }
                    .foregroundColor(.white)
                }
                ToolbarItem(placement: .navigationBarTrailing) {
                    Button("Send") {
                        // Send logic here
                        dismiss()
                    }
                    .foregroundColor(.blue)
                    .fontWeight(.semibold)
                }
            }
        }
    }
}

