// Video Card Component
struct VideoCard: View {
    @Environment(\.designTheme) private var theme
    let title: String
    let duration: String
    let uploadTime: String
    let thumbnailIcon: String
    var thumbnailImage: String?
    var videoURL: URL?

    var body: some View {
        NavigationLink(destination: VideoPlayerView(videoTitle: title, videoURL: videoURL)) {
            HStack(spacing: .spacing4) {
                // Thumbnail
                ZStack {
                    if let thumbnailImage = thumbnailImage {
                        Image(thumbnailImage)
                            .resizable()
                            .aspectRatio(contentMode: .fill)
                            .frame(width: 120, height: 90)
                            .clipShape(RoundedRectangle(cornerRadius: .radiusMedium))
                    } else {
                        RoundedRectangle(cornerRadius: .radiusMedium)
                            .fill(Color.gray.opacity(0.3))
                            .frame(width: 120, height: 90)

                        Image(systemName: thumbnailIcon)
                            .font(.system(size: 32))
                            .foregroundColor(.white.opacity(0.7))
                    }

                    // Play button overlay
                    Image(systemName: "play.circle.fill")
                        .font(.system(size: 40))
                        .foregroundColor(.white)
                        .shadow(radius: 4)
                }

                // Video Info
                VStack(alignment: .leading, spacing: .spacing2) {
                    Text(title)
                        .font(.headline)
                        .foregroundStyle(theme.color(.foreground))
                        .lineLimit(2)

                    HStack(spacing: .spacing1) {
                        Text(duration)
                            .font(.subheadline)
                            .foregroundStyle(theme.color(.mutedforeground))

                        Text("â€¢")
                            .font(.subheadline)
                            .foregroundStyle(theme.color(.mutedforeground))

                        Text(uploadTime)
                            .font(.subheadline)
                            .foregroundStyle(theme.color(.mutedforeground))
                    }
                }

                Spacer()
            }
            .glassCard(cornerRadius: .radiusLarge, padding: .spacing4)
        }
        .buttonStyle(PlainButtonStyle())
    }
}

// Live Stream Card Component
struct LiveStreamCard: View {
