                    .font(.headline)
                    .foregroundStyle(theme.color(.foreground))

                HStack(spacing: 4) {
                    Text(authorHandle)
                        .font(.caption)
                        .foregroundStyle(theme.color(.mutedforeground))
                    Text("â€¢")
                        .font(.caption)
                        .foregroundStyle(theme.color(.mutedforeground))
                    Text(timeAgo)
                        .font(.caption)
                        .foregroundStyle(theme.color(.mutedforeground))
                }
