//
//  SignInView.swift
//  AthleadAI
//
//  Sign-in view using Clerk's built-in AuthView
//

import SwiftUI
import Clerk

struct SignInView: View {
    @Environment(\.designTheme) private var theme
    @Environment(\.clerk) private var clerk
    @State private var authIsPresented = false
    @State private var userTypeSelectionIsPresented = false
    @State private var isLoading = false
    @State private var errorMessage: String?

    var body: some View {
        NavigationStack {
            ScrollView {
                VStack(spacing: 32) {
                    Spacer()
                        .frame(height: 60)

                    // App branding
                    VStack(spacing: 16) {
                        Image(systemName: "sportscourt.fill")
                            .font(.system(size: 80))
                            .foregroundStyle(theme.color(.primary))
                            .shadow(color: theme.color(.primary).opacity(0.3), radius: 10, x: 0, y: 5)
                            .accessibilityLabel("AthleadAI logo")
                            .accessibilityHidden(true)

                        Text("AthleadAI")
                            .font(.largeTitle)
                            .fontWeight(.bold)
                            .fontDesign(.rounded)
                            .dynamicTypeSize(...DynamicTypeSize.xxxLarge)

                        Text("Connect with coaches and showcase your talent")
                            .font(.body)
                            .foregroundStyle(theme.color(.mutedForeground))
                            .multilineTextAlignment(.center)
                            .padding(.horizontal, 40)
                    }

                    // Error message
                    if let errorMessage = errorMessage {
                        HStack {
                            Image(systemName: "exclamationmark.triangle.fill")
                            Text(errorMessage)
                                .font(.footnote)
                        }
                        .foregroundStyle(theme.color(.destructive))
                        .padding()
                        .background(theme.color(.destructive).opacity(0.1))
                        .cornerRadius(.radiusMedium)
                        .padding(.horizontal)
                        .transition(.scale.combined(with: .opacity))
                    }

                    // Sign in section
                    VStack(spacing: 20) {
                        if clerk.user == nil {
                            // Sign in button
                            BrandButton(
                                title: "Sign In",
                                icon: "person.circle.fill",
                                style: .primary,
                                isLoading: isLoading
                            ) {
                                authIsPresented = true
                                errorMessage = nil
                            }
                            .disabled(isLoading)
                            .padding(.horizontal, 32)

                            // Sign up button
                            BrandButton(
                                title: "Create Account",
                                icon: "person.badge.plus",
                                style: .secondary
                            ) {
                                userTypeSelectionIsPresented = true
                                errorMessage = nil
                            }
                            .padding(.horizontal, 32)

                            // Additional info
                            VStack(spacing: 12) {
                                HStack(spacing: 16) {
                                    FeatureBadge(icon: "shield.checkered", text: "Secure")
                                    FeatureBadge(icon: "lock.fill", text: "Private")
                                    FeatureBadge(icon: "checkmark.seal.fill", text: "Verified")
                                }

                                Text("Sign in securely with Clerk authentication")
                                    .font(.caption)
                                    .foregroundStyle(theme.color(.mutedForeground))
                                    .multilineTextAlignment(.center)
                            }
                            .padding(.top, 8)
                        } else {
                            // User is signed in
                            VStack(spacing: 16) {
                                Image(systemName: "checkmark.circle.fill")
                                    .font(.system(size: 60))
                                    .foregroundStyle(.green)

                                Text("Welcome, \(clerk.user?.firstName ?? "User")!")
                                    .font(.title2)
                                    .fontWeight(.semibold)

                                Text("You're all set!")
                                    .font(.subheadline)
                                    .foregroundStyle(theme.color(.mutedForeground))

                                UserButton()
                                    .padding(.top, 8)
                            }
                            .padding()
                        }
                    }

                    Spacer()
                }
                .padding(.vertical)
            }
            .withDesignTheme()
            .brandBackground(.adaptive)
            .sheet(isPresented: $authIsPresented) {
                AuthView()
                    .onDisappear {
                        // Handle auth completion
                        if clerk.user != nil {
                            isLoading = false
                        } else {
                            // User dismissed without signing in
                            errorMessage = nil
                        }
                    }
            }
            .sheet(isPresented: $userTypeSelectionIsPresented) {
                NavigationStack {
                    UserTypeSelectionView()
                }
            }
        }
    }
}

