//
//  AthleteProfileSetupView.swift
//  AthleadAI
//
//  Athlete profile setup screen - collects basic info during onboarding
//

import SwiftUI
import Clerk

struct AthleteProfileSetupView: View {
    @Environment(\.designTheme) private var theme
    @Environment(\.clerk) private var clerk
    @Environment(\.dismiss) private var dismiss

    // Auth method determines which fields to show
    let authMethod: SignUpAuthView.AuthMethod

    // Form fields
    @State private var firstName: String = ""
    @State private var lastName: String = ""
    @State private var email: String = ""
    @State private var password: String = ""
    @State private var confirmPassword: String = ""
    @State private var primarySport: String = "Volleyball"
    @State private var graduationYear: String = "2025"

    // UI state
    @State private var isLoading = false
    @State private var errorMessage: String?
    @State private var showPassword = false
    @State private var showConfirmPassword = false
    @State private var navigateToPositionSelection = false
    @State private var isAuthenticating = false

    // Determine if user signed up with email or OAuth based on auth method
    private var usedEmailSignUp: Bool {
        return authMethod == .email
    }

    private let sports = [
        "Volleyball", "Basketball", "Football", "Soccer", "Baseball",
        "Softball", "Track & Field", "Swimming", "Tennis", "Golf",
        "Lacrosse", "Wrestling", "Cross Country", "Gymnastics", "Hockey"
    ]

    private let graduationYears = Array(2025...2032).map { String($0) }

    var body: some View {
        ScrollView {
            VStack(spacing: .spacing6) {
                // Header
                VStack(spacing: .spacing3) {
                    Image(systemName: "target")
                        .font(.system(size: 60))
                        .foregroundStyle(theme.color(.primary))

                    Text("ATHLEAD")
                        .font(.system(size: 32, weight: .bold))
                        .foregroundStyle(theme.color(.primary))

                    Text("Athlete Sign Up")
                        .font(.title3)
                        .fontWeight(.semibold)
                        .foregroundStyle(theme.color(.foreground))
                }
                .padding(.top, 40)
                .padding(.bottom, 8)

                    // Form
                    VStack(spacing: 16) {
                        // First Name
                        FormField(
                            label: "First Name",
                            icon: "person",
                            placeholder: "First Name",
                            text: $firstName
                        )

                        // Last Name
                        FormField(
                            label: "Last Name",
                            icon: "person",
                            placeholder: "Last Name",
                            text: $lastName
                        )

                        // Email (only shown for email sign-up)
                        if usedEmailSignUp {
                            FormField(
                                label: "Email",
                                icon: "envelope",
                                placeholder: "Email",
                                text: $email,
                                keyboardType: .emailAddress,
                                autocapitalization: .never
                            )

                            // Password
                            VStack(alignment: .leading, spacing: 8) {
                                Text("Password")
                                    .font(.subheadline)
                                    .fontWeight(.medium)
                                    .foregroundStyle(theme.color(.foreground))

                                HStack {
                                    Image(systemName: "lock")
                                        .foregroundStyle(theme.color(.mutedForeground))
                                        .frame(width: 20)

                                    if showPassword {
                                        TextField("Password", text: $password)
                                            .autocapitalization(.none)
                                    } else {
                                        SecureField("Password", text: $password)
                                            .autocapitalization(.none)
                                    }

                                    Button {
                                        showPassword.toggle()
                                    } label: {
                                        Image(systemName: showPassword ? "eye.slash" : "eye")
                                            .foregroundStyle(theme.color(.mutedForeground))
                                    }

                                    if !password.isEmpty {
                                        Button {
                                            password = ""
                                        } label: {
                                            Image(systemName: "xmark.circle.fill")
                                                .foregroundStyle(theme.color(.mutedForeground))
                                        }
                                    }
                                }
                                .glassCard(cornerRadius: .radiusMedium, padding: .spacing4)
                            }

                            // Re-enter Password
                            VStack(alignment: .leading, spacing: 8) {
                                Text("Confirm Password")
                                    .font(.subheadline)
                                    .fontWeight(.medium)
                                    .foregroundStyle(theme.color(.foreground))

                                HStack {
                                    Image(systemName: "lock")
                                        .foregroundStyle(theme.color(.mutedForeground))
                                        .frame(width: 20)

                                    if showConfirmPassword {
                                        TextField("Re-enter Password", text: $confirmPassword)
                                            .autocapitalization(.none)
                                    } else {
                                        SecureField("Re-enter Password", text: $confirmPassword)
                                            .autocapitalization(.none)
                                    }

                                    Button {
                                        showConfirmPassword.toggle()
                                    } label: {
                                        Image(systemName: showConfirmPassword ? "eye.slash" : "eye")
                                            .foregroundStyle(theme.color(.mutedForeground))
                                    }

                                    if !confirmPassword.isEmpty {
                                        Button {
                                            confirmPassword = ""
                                        } label: {
                                            Image(systemName: "xmark.circle.fill")
                                                .foregroundStyle(theme.color(.mutedForeground))
                                        }
                                    }
                                }
                                .glassCard(cornerRadius: .radiusMedium, padding: .spacing4)
                            }
                        }

                        // Primary Sport Dropdown
                        VStack(alignment: .leading, spacing: 8) {
                            Text("Primary Sport")
                                .font(.subheadline)
                                .fontWeight(.medium)
                                .foregroundStyle(theme.color(.foreground))

                            Menu {
                                ForEach(sports, id: \.self) { sport in
                                    Button(sport) {
                                        primarySport = sport
                                    }
                                }
                            } label: {
                                HStack {
                                    Text(primarySport)
                                        .foregroundStyle(theme.color(.foreground))
                                    Spacer()
                                    Image(systemName: "chevron.down")
                                        .foregroundStyle(theme.color(.mutedForeground))
                                        .font(.caption)
                                }
                                .glassCard(cornerRadius: .radiusMedium, padding: .spacing4)
                            }
                        }

                        // Graduation Year Dropdown
                        VStack(alignment: .leading, spacing: 8) {
                            Text("Graduation Year")
                                .font(.subheadline)
                                .fontWeight(.medium)
                                .foregroundStyle(theme.color(.foreground))

                            Menu {
                                ForEach(graduationYears, id: \.self) { year in
                                    Button(year) {
                                        graduationYear = year
                                    }
                                }
                            } label: {
                                HStack {
                                    Text(graduationYear)
                                        .foregroundStyle(theme.color(.foreground))
                                    Spacer()
                                    Image(systemName: "chevron.down")
                                        .foregroundStyle(theme.color(.mutedForeground))
                                        .font(.caption)
                                }
                                .glassCard(cornerRadius: .radiusMedium, padding: .spacing4)
                            }
                        }
                    }
                    .padding(.horizontal, 24)

                // Error message
                if let errorMessage = errorMessage {
                    HStack {
                        Image(systemName: "exclamationmark.triangle.fill")
                        Text(errorMessage)
                            .font(.footnote)
                    }
                    .foregroundStyle(theme.color(.destructive))
                    .glassCard(cornerRadius: .radiusMedium, padding: .spacing4)
                    .padding(.horizontal, 24)
                    .transition(.scale.combined(with: .opacity))
                }

                // Submit Button
                BrandButton(
                    title: "Submit",
                    icon: nil,
                    style: .primary,
                    size: .medium,
                    isLoading: isLoading
                ) {
                    submitForm()
                }
                .disabled(!isFormValid || isLoading)
                .padding(.horizontal, 24)
                .padding(.top, 8)

                Spacer()
            }
            .padding(.bottom, 40)
        }
        .withDesignTheme()
        .brandBackground(.auth)
        .navigationDestination(isPresented: $navigateToPositionSelection) {
            AthletePositionView(userName: firstName, sport: primarySport)
        }
        .onAppear {
            // For OAuth users, trigger authentication immediately
            if authMethod != .email && !isAuthenticating {
                isAuthenticating = true
                authenticateWithOAuth()
            }

            // Pre-populate fields from Clerk user if available
            if let user = clerk.user {
                if firstName.isEmpty, let clerkFirstName = user.firstName {
                    firstName = clerkFirstName
                }
                if lastName.isEmpty, let clerkLastName = user.lastName {
                    lastName = clerkLastName
                }
                if email.isEmpty, let primaryEmail = user.emailAddresses.first?.emailAddress {
                    email = primaryEmail
                }
            }
        }
    }

    private func authenticateWithOAuth() {
        // TODO: Implement OAuth authentication with Clerk
        // For now, just show a message that OAuth is not yet configured
        errorMessage = "OAuth authentication not yet configured. Please use email sign-up."
        isAuthenticating = false
    }

    private var isFormValid: Bool {
        // Basic validation
        let basicFieldsValid = !firstName.isEmpty && !lastName.isEmpty

        if usedEmailSignUp {
            // Email sign-up: validate email and password
            let emailValid = !email.isEmpty && email.contains("@")
            let passwordValid = !password.isEmpty && password.count >= 8
            let passwordsMatch = password == confirmPassword

            return basicFieldsValid && emailValid && passwordValid && passwordsMatch
        } else {
            // OAuth sign-up: only basic fields needed
            return basicFieldsValid
        }
    }

    private func submitForm() {
        errorMessage = nil
        isLoading = true

        // Validate password match for email sign-up
        if usedEmailSignUp && password != confirmPassword {
            errorMessage = "Passwords do not match"
            isLoading = false
            return
        }

        Task {
            do {
                if usedEmailSignUp {
                    // Create Clerk account programmatically (no UI)
                    try await createClerkAccount()
                } else {
                    // For OAuth, authentication should have already happened
                    // Just verify user is authenticated
                    guard clerk.user != nil else {
                        await MainActor.run {
                            errorMessage = "Authentication required"
                            isLoading = false
                        }
                        return
                    }
                }

                // TODO: Save sport and graduation year to backend

                await MainActor.run {
                    isLoading = false
                    navigateToPositionSelection = true
                }
            } catch {
                await MainActor.run {
                    errorMessage = error.localizedDescription
                    isLoading = false
                }
            }
        }
    }

    private func createClerkAccount() async throws {
        // Use Clerk API to create account programmatically
        // This is a placeholder - the actual Clerk iOS SDK might require different approach
        // For now, we'll skip authentication and just proceed
        // In production, you'd integrate with your backend API that handles Clerk
        try await Task.sleep(nanoseconds: 500_000_000) // 0.5 second delay to simulate API call

        // TODO: Implement actual Clerk API integration
        // This might involve calling your backend API which then creates the Clerk user
        // Example:
        // let response = await APIClient.shared.createUser(
        //     email: email,
        //     password: password,
        //     firstName: firstName,
        //     lastName: lastName
        // )
    }
}

// MARK: - Form Field Component
struct FormField: View {
    @Environment(\.designTheme) private var theme
    let label: String
    let icon: String
    let placeholder: String
    @Binding var text: String
    var keyboardType: UIKeyboardType = .default
    var autocapitalization: TextInputAutocapitalization = .words

    var body: some View {
        VStack(alignment: .leading, spacing: 8) {
            Text(label)
                .font(.subheadline)
                .fontWeight(.medium)
                .foregroundStyle(theme.color(.foreground))

            HStack {
                Image(systemName: icon)
                    .foregroundStyle(theme.color(.mutedForeground))
                    .frame(width: 20)

                TextField(placeholder, text: $text)
                    .keyboardType(keyboardType)
                    .textInputAutocapitalization(autocapitalization)

                if !text.isEmpty {
                    Button {
                        text = ""
                    } label: {
                        Image(systemName: "xmark.circle.fill")
                            .foregroundStyle(theme.color(.mutedForeground))
                    }
                }
            }
            .glassCard(cornerRadius: .radiusMedium, padding: .spacing4)
        }
    }
}

