import Fastify from "fastify";
import { protectedRoutes, publicRoutes } from "./routes/index";
import {
  createFastifyLogger,
  postHogPlugin,
  setupErrorHandler,
  securityPlugin,
} from "@athlead/api-utils";

const requiredEnvVars = [
  "DATABASE_OWNER_URL",
  "DATABASE_USER_URL",
  "LIVEKIT_API_KEY",
  "LIVEKIT_API_SECRET",
  "LIVEKIT_URL",
  "POSTHOG_API_KEY",
  "POSTHOG_HOST",
  "POSTHOG_ENABLED",
] as const;

function validateEnvironment() {
  let hasErrors = false;

  for (const envVar of requiredEnvVars) {
    if (!process.env[envVar]) {
      console.error(`${envVar} missing in livestream-api`);
      hasErrors = true;
    }
  }

  if (hasErrors) {
    process.exit(1);
  }
}

export const buildServer = async () => {
  validateEnvironment();

  const app = Fastify({
    logger: createFastifyLogger(),
  });

  setupErrorHandler(app);

  // Register security plugin (helmet + cors)
  await app.register(securityPlugin, {
    corsOrigins: process.env.ALLOWED_ORIGINS?.split(",").map(origin => origin.trim()) || [],
    development: process.env.NODE_ENV !== "production",
  });

  await app.register(postHogPlugin, {
    apiKey: process.env.POSTHOG_API_KEY,
    host: process.env.POSTHOG_HOST,
    enabled: process.env.POSTHOG_ENABLED === "true",
  });

  // Configure content type parser for LiveKit webhooks
  // LiveKit sends webhooks with application/webhook+json content type
  // We need to preserve the raw body string for signature validation
  app.addContentTypeParser('application/webhook+json', { parseAs: 'string' }, (req, body, done) => {
    done(null, body);
  });

  await app.register(publicRoutes);

  await app.register(protectedRoutes);

  return app;
};

