import { FastifyInstance } from "fastify";
import { clerkPlugin } from "@clerk/fastify";
import sessionRoutes from "./session";
import webhookRoutes from "./webhook";
import { healthRoutes as healthRoutesHandler } from "./health";

export async function protectedRoutes(fastify: FastifyInstance) {
  await fastify.register(clerkPlugin);

  await fastify.register(sessionRoutes, { prefix: "/api/livestream" });
}

//TODO: Move healthcheck to protected after GH integration
export async function publicRoutes(fastify: FastifyInstance) {
  await fastify.register(healthRoutesHandler);
  await fastify.register(webhookRoutes, { prefix: "/api/livestream" });
}

