import Foundation

class LivestreamService {
    static let shared = LivestreamService()

    private init() {}

    // MARK: - Request/Response Models

    struct CreateSessionRequest: Encodable {
        let roomName: String
        let metadata: SessionMetadata?

        struct SessionMetadata: Encodable {
            let title: String?
            let description: String?
            let isPublic: Bool
        }
    }

    struct SessionResponse: Decodable {
        let roomName: String
        let token: String
        let url: String?
        let expiresAt: String?
    }

    struct SessionListResponse: Decodable {
        let sessions: [LiveSession]

        struct LiveSession: Decodable {
            let id: String
            let roomName: String
            let createdAt: String
            let participantCount: Int
        }
    }

    // MARK: - API Methods

    /// Create a new livestream session
    func createSession(roomName: String, title: String? = nil, isPublic: Bool = true) async throws -> SessionResponse {
        let metadata = CreateSessionRequest.SessionMetadata(
            title: title,
            description: nil,
            isPublic: isPublic
        )

        let request = CreateSessionRequest(
            roomName: roomName,
            metadata: metadata
        )

        let response: SessionResponse = try await APIClient.shared.request(
            endpoint: .livestream("/api/livestream/session"),
            method: "POST",
            body: request
        )

        return response
    }

    /// Get list of active sessions
    func getActiveSessions() async throws -> SessionListResponse {
        let response: SessionListResponse = try await APIClient.shared.request(
            endpoint: .livestream("/api/livestream/sessions"),
            method: "GET"
        )

        return response
    }

    /// End a livestream session
    func endSession(roomName: String) async throws {
        struct EndSessionRequest: Encodable {
            let roomName: String
        }

        let _: EmptyResponse = try await APIClient.shared.request(
            endpoint: .livestream("/api/livestream/session/end"),
            method: "POST",
            body: EndSessionRequest(roomName: roomName)
        )
    }
}

// Empty response for endpoints that don't return data
struct EmptyResponse: Decodable {}

// MARK: - Usage Example

