import { FastifyInstance } from "fastify";
import {
  CreateSessionRequestSchema,
  CreateSessionResponseSchema,
  JoinSessionRequestSchema,
  JoinSessionResponseSchema,
  SessionViewerTokenRequestSchema,
  SessionViewerTokenResponseSchema,
  LeaveSessionRequestSchema,
  SessionDataResponseSchema,
  SessionStatusResponseSchema,
  SessionParticipantsResponseSchema,
  ActiveSessionsResponseSchema,
  SessionIdParamsSchema,
  IdParamsSchema,
  SlugParamsSchema,
  ViewerCountsRequestSchema,
  ViewerCountsResponseSchema,
} from "@athlead/shared-api/contracts/livestream";
import {
  generateSessionViewerToken,
  createSession,
  joinSession,
  leaveSession,
  startSession,
  endSession,
  getSession,
  getSessionBySlug,
  getSessionParticipants,
  getSessionRoomStatus,
  listActiveSessions,
  getViewerCounts,
} from "@athlead/livestream-service";
import { sendSuccess, protectedHandler, publicHandler } from "@athlead/api-utils";

export default async function sessionRoutes(fastify: FastifyInstance) {
  // =============================================================================
  // SESSION CREATION
  // =============================================================================

  fastify.post(
    "/sessions",
    protectedHandler(
      {
        body: CreateSessionRequestSchema,
        response: CreateSessionResponseSchema,
      },
      async ({ clerk, body }, reply) => {
        const sessionResponse = await createSession({
          authId: clerk.authId,
          title: body.title,
          description: body.description,
          roomName: body.roomName,
        });

        return sendSuccess(reply, sessionResponse, 201);
      },
    ),
  );

  // =============================================================================
  // SESSION MANAGEMENT
  // =============================================================================

  fastify.get(
    "/sessions/:id",
    publicHandler(
      {
        params: IdParamsSchema,
        response: SessionDataResponseSchema,
      },
      async ({ params }, reply) => {
        const session = await getSession({ sessionId: params.id });
        // Transform Date objects to strings for API contract compatibility
        const response = {
          ...session,
          scheduledStart: session.scheduledStart?.toISOString() || null,
          startedAt: session.startedAt?.toISOString() || null,
          endedAt: session.endedAt?.toISOString() || null,
        };
        return sendSuccess(reply, response);
      },
    ),
  );

  fastify.get(
    "/sessions/by-slug/:slug",
    publicHandler(
      {
        params: SlugParamsSchema,
        response: SessionDataResponseSchema,
      },
      async ({ params }, reply) => {
        const session = await getSessionBySlug({ slug: params.slug });
        // Transform Date objects to strings for API contract compatibility
        const response = {
          ...session,
          scheduledStart: session.scheduledStart?.toISOString() || null,
          startedAt: session.startedAt?.toISOString() || null,
          endedAt: session.endedAt?.toISOString() || null,
        };
        return sendSuccess(reply, response);
      },
    ),
  );

  fastify.post(
    "/sessions/:sessionId/join",
    protectedHandler(
      {
        body: JoinSessionRequestSchema,
        params: SessionIdParamsSchema,
        response: JoinSessionResponseSchema,
      },
      async ({ clerk, body, params }, reply) => {
        const joinResponse = await joinSession({
          sessionId: params.sessionId,
          authId: clerk.authId,
          participantName: body.participantName,
          participantId: body.participantId,
        });

        return sendSuccess(reply, joinResponse);
      },
    ),
  );

  // =============================================================================
  // STREAMER SESSION MANAGEMENT
  // =============================================================================

  fastify.put(
    "/sessions/:sessionId/start",
    protectedHandler(
      {
        params: SessionIdParamsSchema,
        response: SessionDataResponseSchema,
      },
      async ({ clerk, params }, reply) => {
        const session = await startSession({
          sessionId: params.sessionId,
          authId: clerk.authId,
        });
        // Transform Date objects to strings for API contract compatibility
        const response = {
          ...session,
          scheduledStart: session.scheduledStart?.toISOString() || null,
          startedAt: session.startedAt?.toISOString() || null,
          endedAt: session.endedAt?.toISOString() || null,
        };
        return sendSuccess(reply, response);
      },
    ),
  );

  fastify.put(
    "/sessions/:sessionId/end",
    protectedHandler(
      {
        params: SessionIdParamsSchema,
        response: SessionDataResponseSchema,
      },
      async ({ clerk, params }, reply) => {
        const session = await endSession({
          sessionId: params.sessionId,
          authId: clerk.authId,
        });
        // Transform Date objects to strings for API contract compatibility
        const response = {
          ...session,
          scheduledStart: session.scheduledStart?.toISOString() || null,
          startedAt: session.startedAt?.toISOString() || null,
          endedAt: session.endedAt?.toISOString() || null,
        };
        return sendSuccess(reply, response);
      },
    ),
  );

  // =============================================================================
  // ADVANCED TOKEN GENERATION
  // =============================================================================

  fastify.post(
    "/sessions/:sessionId/viewer-token",
    protectedHandler(
      {
        body: SessionViewerTokenRequestSchema,
        params: SessionIdParamsSchema,
        response: SessionViewerTokenResponseSchema,
      },
      async ({ clerk, params, body }, reply) => {
        const tokenResponse = await generateSessionViewerToken({
          sessionId: params.sessionId,
          viewerIdentity: `user_${clerk.authId}`,
          viewerName: body.viewerName || body.viewerIdentity, // Fallback if name not provided
        });

        return sendSuccess(reply, tokenResponse);
      },
    ),
  );

  // =============================================================================
  // PARTICIPANT MANAGEMENT
  // =============================================================================

  fastify.post(
    "/sessions/:sessionId/leave",
    protectedHandler(
      {
        body: LeaveSessionRequestSchema,
        params: SessionIdParamsSchema,
      },
      async ({ body }, reply) => {
        await leaveSession({
          participantId: body.participantId,
        });

        return sendSuccess(reply, { message: "Successfully left session" });
      },
    ),
  );

  // =============================================================================
  // MONITORING & ANALYTICS
  // =============================================================================

  fastify.get(
    "/sessions/:sessionId/participants",
    publicHandler(
      {
        params: SessionIdParamsSchema,
        response: SessionParticipantsResponseSchema,
      },
      async ({ params }, reply) => {
        const participants = await getSessionParticipants({
          sessionId: params.sessionId,
        });
        return sendSuccess(reply, participants);
      },
    ),
  );

  fastify.get(
    "/sessions/:sessionId/status",
    publicHandler(
      {
        params: SessionIdParamsSchema,
        response: SessionStatusResponseSchema,
      },
      async ({ params }, reply) => {
        const status = await getSessionRoomStatus({
          sessionId: params.sessionId,
        });
        return sendSuccess(reply, status);
      },
    ),
  );

  fastify.get(
    "/sessions/active",
    protectedHandler(
      {
        response: ActiveSessionsResponseSchema,
      },
      async (_, reply) => {
        const activeSessions = await listActiveSessions();
        return sendSuccess(reply, activeSessions);
      },
    ),
  );

  // Batch viewer counts endpoint for client polling
  fastify.post(
    "/sessions/viewer-counts",
    protectedHandler(
      {
        body: ViewerCountsRequestSchema,
        response: ViewerCountsResponseSchema,
      },
      async ({ body }, reply) => {
        const viewerCounts = await getViewerCounts({
          sessionIds: body.sessionIds,
        });
        return sendSuccess(reply, viewerCounts);
      },
    ),
  );
}

