class ClerkService {
    var isSignedIn = false
    var user: Clerk.User?
    var isLoading = false
    var error: String?

    static let shared = ClerkService()

    private init() {
        setupClerk()
    }

    private func setupClerk() {
        // Initialize Clerk with publishable key
        Task {
            do {
                try await Clerk.shared.configure(publishableKey: Config.clerkPublishableKey)
                updateAuthState()
            } catch {
                self.error = "Failed to configure Clerk: \(error.localizedDescription)"
            }
        }
    }

    private func updateAuthState() {
        isSignedIn = Clerk.shared.session != nil
        user = Clerk.shared.user
    }

    /// Get the current session token for authenticated API calls
    func getSessionToken() async throws -> String? {
        guard let session = Clerk.shared.session else {
            throw ClerkError.notAuthenticated
        }

        do {
            let token = try await session.getToken()
            return token.jwt
        } catch {
            throw ClerkError.tokenFetchFailed
        }
    }

    /// Sign out the current user
    func signOut() async throws {
        try await Clerk.shared.signOut()
        updateAuthState()
    }
}

