import SwiftUI
import Clerk

struct SignInView: View {
    @Environment(ClerkService.self) private var clerkService
    @State private var showError = false

    var body: some View {
        VStack(spacing: 20) {
            // App branding
            VStack {
                Image(systemName: "sportscourt.fill")
                    .font(.system(size: 60))
                    .foregroundColor(.blue)

                Text("AthleadAI")
                    .font(.largeTitle)
                    .fontWeight(.bold)

                Text("Connect with coaches and showcase your talent")
                    .font(.subheadline)
                    .foregroundColor(.secondary)
                    .multilineTextAlignment(.center)
            }
            .padding(.bottom, 40)

            // Clerk's built-in sign-in component
            // This handles all the UI for email, social auth, etc.
            ClerkSignInView()
                .frame(maxWidth: .infinity)
                .frame(height: 400)

            if let error = clerkService.error {
                Text(error)
                    .font(.caption)
                    .foregroundColor(.red)
                    .padding()
            }

            Spacer()
        }
        .padding()
    }
}

// Wrapper for Clerk's sign-in component
struct ClerkSignInView: UIViewControllerRepresentable {
    func makeUIViewController(context: Context) -> some UIViewController {
        // Clerk provides pre-built UI components
        // This is a placeholder - actual implementation depends on Clerk iOS SDK
        let viewController = UIViewController()
        viewController.view.backgroundColor = .systemBackground

        // Add sign-in UI here using Clerk's components
        // Example: Clerk.shared.presentSignIn(on: viewController)

        return viewController
    }

    func updateUIViewController(_ uiViewController: UIViewControllerType, context: Context) {
        // Update if needed
    }
}

