//
//  ClerkService.swift
//  AthleadAI
//
//  Authentication service for Clerk integration
//

import Foundation
import Clerk

@MainActor
@Observable
class ClerkService {
    var isSignedIn = false
    var userId: String?
    var userEmail: String?
    var isLoading = false
    var error: String?

    static let shared = ClerkService()

    private init() {
        setupClerk()
    }

    private func setupClerk() {
        // Initialize Clerk with publishable key
        Task {
            do {
                try await Clerk.shared.configure(publishableKey: Config.clerkPublishableKey)
                updateAuthState()
            } catch {
                self.error = "Failed to configure Clerk: \(error.localizedDescription)"
            }
        }
    }

    private func updateAuthState() {
        isSignedIn = Clerk.shared.session != nil
        // Note: User info would be accessed differently in Clerk iOS SDK
        // For now, we'll get it from the session when needed
        userId = Clerk.shared.session?.user.id
        userEmail = Clerk.shared.session?.user.primaryEmailAddress?.emailAddress
    }

    /// Get the current session token for authenticated API calls
    func getSessionToken() async throws -> String? {
        guard let session = Clerk.shared.session else {
            throw ClerkError.notAuthenticated
        }

        guard let token = try await session.getToken() else {
            throw ClerkError.tokenFetchFailed
        }

        return token.jwt
    }

    /// Sign out the current user
    func signOut() async throws {
        try await Clerk.shared.signOut()
        updateAuthState()
    }
}

enum ClerkError: LocalizedError {
    case notAuthenticated
    case tokenFetchFailed

    var errorDescription: String? {
        switch self {
        case .notAuthenticated:
            return "User is not authenticated"
        case .tokenFetchFailed:
            return "Failed to fetch session token"
        }
    }
}

