//
//  APIClient.swift
//  AthleadAI
//
//  API client with Clerk authentication
//

import Foundation

class APIClient {
    static let shared = APIClient()

    private let livestreamBaseURL: URL
    private let usersBaseURL: URL

    private init() {
        guard let livestreamURL = URL(string: Config.livestreamAPIURL),
              let usersURL = URL(string: Config.usersAPIURL) else {
            fatalError("Invalid API URLs in configuration")
        }
        self.livestreamBaseURL = livestreamURL
        self.usersBaseURL = usersURL
    }

    enum APIEndpoint {
        case livestream(String)
        case users(String)

        var baseURL: URL {
            switch self {
            case .livestream:
                return APIClient.shared.livestreamBaseURL
            case .users:
                return APIClient.shared.usersBaseURL
            }
        }

        var path: String {
            switch self {
            case .livestream(let path), .users(let path):
                return path
            }
        }
    }

    /// Make an authenticated API request
    func request<T: Decodable>(
        endpoint: APIEndpoint,
        method: String = "GET",
        body: Encodable? = nil
    ) async throws -> T {
        // Get authentication token from Clerk
        guard let token = try await ClerkService.shared.getSessionToken() else {
            throw APIError.authenticationRequired
        }

        // Build URL
        let url = endpoint.baseURL.appendingPathComponent(endpoint.path)

        // Create request
        var request = URLRequest(url: url)
        request.httpMethod = method
        request.setValue("Bearer \(token)", forHTTPHeaderField: "Authorization")
        request.setValue("application/json", forHTTPHeaderField: "Content-Type")

        // Add body if present
        if let body = body {
            request.httpBody = try JSONEncoder().encode(body)
        }

        // Make request
        let (data, response) = try await URLSession.shared.data(for: request)

        // Check response
        guard let httpResponse = response as? HTTPURLResponse else {
            throw APIError.invalidResponse
        }

        guard (200...299).contains(httpResponse.statusCode) else {
            throw APIError.httpError(statusCode: httpResponse.statusCode)
        }

        // Decode response
        do {
            let decoder = JSONDecoder()
