//
//  CollegeCoachTabView.swift
//  AthleadAI
//
//  Tab navigation for College Coach user type
//

import SwiftUI

// MARK: - College Coach Tab Enum
enum CollegeCoachTab {
    case profile
    case feed
    case ai
    case messages
    case search
}

// MARK: - College Coach Tab View
/// College coaches have the same tab structure as athletes
/// This provides them with access to profile, feed, AI recruiting assistant, messages, and athlete search
struct CollegeCoachTabView: View {
    @State private var selectedTab: CollegeCoachTab = .feed

    var body: some View {
        TabView(selection: $selectedTab) {
            // Profile - Reuse existing ProfileView
            ProfileView()
                .tabItem {
                    Label("Profile", systemImage: "person.circle.fill")
                }
                .tag(CollegeCoachTab.profile)

            // Feed - Reuse existing FeedView
            FeedView()
                .tabItem {
                    Label("Feed", systemImage: "house.fill")
                }
                .tag(CollegeCoachTab.feed)

            // AI - Reuse existing AIView (AI recruiting assistant)
            AIView()
                .tabItem {
                    Label("AI", systemImage: "sparkles")
                }
                .tag(CollegeCoachTab.ai)

            // Messages - Reuse existing MessagesView
            MessagesView()
                .tabItem {
                    Label("Messages", systemImage: "message.fill")
                }
                .tag(CollegeCoachTab.messages)

            // Search - Reuse existing SearchView (search for athletes)
            SearchView()
                .tabItem {
                    Label("Search", systemImage: "magnifyingglass")
                }
                .tag(CollegeCoachTab.search)
        }
    }
}

