// MARK: - Recruit Filter Enum
enum RecruitFilter {
    case watchlist
    case gradYear2025
    case gradYear2026
    case gradYear2027
    case positionOutside
    case positionSetter
    case locationTexas
}

// MARK: - Filter Circle Component
struct FilterCircle: View {
    let name: String
    let icon: String
    var iconColor: Color = .blue
    let isSelected: Bool
    let action: () -> Void

    var body: some View {
        Button(action: action) {
            VStack(spacing: 6) {
                // Circle with icon
                ZStack {
                    // Outer ring (gradient border)
                    Circle()
                        .strokeBorder(
                            LinearGradient(
                                colors: isSelected
                                    ? [iconColor, iconColor.opacity(0.6)]
                                    : [Color.white.opacity(0.3), Color.white.opacity(0.3)],
                                startPoint: .topLeading,
                                endPoint: .bottomTrailing
                            ),
                            lineWidth: 3
                        )
                        .frame(width: 70, height: 70)

                    // Inner circle with icon
                    Circle()
                        .fill(
                            LinearGradient(
                                colors: isSelected
