//
//  CollegeCoachSearchView.swift
//  AthleadAI
//
//  Search view for college coaches - focused on athlete recruiting search
//

import SwiftUI

struct CollegeCoachSearchView: View {
    @State private var searchText = ""
    @State private var selectedPosition: String? = nil
    @State private var selectedGradYear: String? = nil
    @State private var selectedLocation: String? = nil
    @State private var showFilters = false

    let positions = ["Outside Hitter", "Setter", "Middle Blocker", "Libero", "Opposite", "Defensive Specialist"]
    let gradYears = ["2025", "2026", "2027", "2028"]
    let locations = ["Texas", "California", "Florida", "Illinois", "New York", "All States"]

    var body: some View {
        NavigationView {
            ZStack {
                // Background gradient
                LinearGradient(
                    gradient: Gradient(colors: [
                        Color(red: 0.1, green: 0.1, blue: 0.2),
                        Color(red: 0.05, green: 0.05, blue: 0.15)
                    ]),
                    startPoint: .topLeading,
                    endPoint: .bottomTrailing
                )
                .ignoresSafeArea()

                VStack(spacing: 0) {
                    // Search Bar
                    HStack {
                        Image(systemName: "magnifyingglass")
                            .foregroundColor(.gray)

                        TextField("Search athletes...", text: $searchText)
                            .foregroundColor(.white)

                        if !searchText.isEmpty {
                            Button(action: { searchText = "" }) {
                                Image(systemName: "xmark.circle.fill")
                                    .foregroundColor(.gray)
                            }
                        }
                    }
                    .padding()
                    .background(
                        RoundedRectangle(cornerRadius: 12)
                            .fill(Color.black.opacity(0.4))
                            .overlay(
                                RoundedRectangle(cornerRadius: 12)
                                    .stroke(Color.white.opacity(0.3), lineWidth: 1)
                            )
                    )
                    .padding(.horizontal)
                    .padding(.vertical, 12)

                    // Filter Chips
                    ScrollView(.horizontal, showsIndicators: false) {
                        HStack(spacing: 12) {
                            // Filters Button
                            Button(action: { showFilters.toggle() }) {
                                HStack(spacing: 6) {
                                    Image(systemName: "line.3.horizontal.decrease.circle")
                                    Text("Filters")
                                }
                                .padding(.horizontal, 16)
                                .padding(.vertical, 8)
                                .background(
                                    Capsule()
                                        .fill(showFilters ? Color.blue : Color.black.opacity(0.4))
                                )
                                .foregroundColor(.white)
                            }

                            // Position Filter
                            if let position = selectedPosition {
                                FilterChip(title: position, icon: "figure.volleyball") {
                                    selectedPosition = nil
                                }
                            }

                            // Grad Year Filter
                            if let year = selectedGradYear {
                                FilterChip(title: "Class of \(year)", icon: "graduationcap.fill") {
                                    selectedGradYear = nil
                                }
                            }

                            // Location Filter
                            if let location = selectedLocation {
                                FilterChip(title: location, icon: "map.fill") {
                                    selectedLocation = nil
                                }
                            }
                        }
                        .padding(.horizontal)
                    }
                    .padding(.bottom, 12)

                    // Filter Options (expandable)
                    if showFilters {
                        VStack(alignment: .leading, spacing: 16) {
                            // Position Filter
                            VStack(alignment: .leading, spacing: 8) {
                                Text("Position")
                                    .font(.subheadline)
                                    .fontWeight(.semibold)
                                    .foregroundColor(.white.opacity(0.7))

                                ScrollView(.horizontal, showsIndicators: false) {
                                    HStack(spacing: 8) {
                                        ForEach(positions, id: \.self) { position in
                                            Button(action: {
                                                selectedPosition = position
                                            }) {
                                                Text(position)
                                                    .font(.caption)
                                                    .padding(.horizontal, 12)
                                                    .padding(.vertical, 6)
                                                    .background(
                                                        Capsule()
                                                            .fill(selectedPosition == position ? Color.blue : Color.white.opacity(0.2))
                                                    )
                                                    .foregroundColor(.white)
                                            }
                                        }
                                    }
                                }
                            }

                            // Grad Year Filter
                            VStack(alignment: .leading, spacing: 8) {
                                Text("Graduation Year")
                                    .font(.subheadline)
                                    .fontWeight(.semibold)
                                    .foregroundColor(.white.opacity(0.7))

                                HStack(spacing: 8) {
                                    ForEach(gradYears, id: \.self) { year in
                                        Button(action: {
                                            selectedGradYear = year
                                        }) {
                                            Text(year)
                                                .font(.caption)
                                                .padding(.horizontal, 12)
                                                .padding(.vertical, 6)
                                                .background(
                                                    Capsule()
                                                        .fill(selectedGradYear == year ? Color.blue : Color.white.opacity(0.2))
                                                )
                                                .foregroundColor(.white)
                                        }
                                    }
                                }
                            }

                            // Location Filter
                            VStack(alignment: .leading, spacing: 8) {
                                Text("Location")
                                    .font(.subheadline)
                                    .fontWeight(.semibold)
                                    .foregroundColor(.white.opacity(0.7))

                                ScrollView(.horizontal, showsIndicators: false) {
                                    HStack(spacing: 8) {
                                        ForEach(locations, id: \.self) { location in
                                            Button(action: {
                                                selectedLocation = location
                                            }) {
                                                Text(location)
                                                    .font(.caption)
                                                    .padding(.horizontal, 12)
                                                    .padding(.vertical, 6)
                                                    .background(
                                                        Capsule()
                                                            .fill(selectedLocation == location ? Color.blue : Color.white.opacity(0.2))
                                                    )
                                                    .foregroundColor(.white)
                                            }
                                        }
                                    }
                                }
                            }
                        }
                        .padding()
                        .background(
                            RoundedRectangle(cornerRadius: 16)
                                .fill(Color.black.opacity(0.4))
                                .overlay(
                                    RoundedRectangle(cornerRadius: 16)
                                        .stroke(Color.white.opacity(0.3), lineWidth: 1)
                                )
                        )
                        .padding(.horizontal)
                        .padding(.bottom, 12)
                    }

                    // Results List
                    ScrollView {
                        VStack(spacing: 12) {
                            // Sample athlete results
                            AthleteResultCard(
                                name: "Kinsley Young",
                                position: "Outside Hitter",
                                gradYear: "2026",
                                location: "Southlake, TX",
                                height: "5'11\"",
                                gpa: "3.8",
                                isWatchlisted: true
                            )

                            AthleteResultCard(
                                name: "Sarah Johnson",
                                position: "Setter",
                                gradYear: "2026",
                                location: "Austin, TX",
                                height: "5'9\"",
                                gpa: "3.9",
                                isWatchlisted: false
                            )

                            AthleteResultCard(
                                name: "Emma Davis",
                                position: "Middle Blocker",
                                gradYear: "2027",
                                location: "Dallas, TX",
                                height: "6'2\"",
                                gpa: "3.7",
                                isWatchlisted: false
                            )

                            AthleteResultCard(
                                name: "Olivia Martinez",
                                position: "Libero",
                                gradYear: "2025",
                                location: "Houston, TX",
                                height: "5'6\"",
                                gpa: "4.0",
                                isWatchlisted: true
                            )
                        }
                        .padding(.horizontal)
                        .padding(.bottom, 20)
                    }
                }
            }
            .navigationTitle("Athlete Search")
            .navigationBarTitleDisplayMode(.inline)
        }
    }
}

// MARK: - Filter Chip Component
struct FilterChip: View {
    let title: String
    let icon: String
    let onRemove: () -> Void

    var body: some View {
        HStack(spacing: 6) {
            Image(systemName: icon)
                .font(.caption)
