}

// MARK: - Athlete Result Card Component
struct AthleteResultCard: View {
    let name: String
    let position: String
    let gradYear: String
    let location: String
    let height: String
    let gpa: String
    let isWatchlisted: Bool

    @State private var watchlisted: Bool

    init(name: String, position: String, gradYear: String, location: String, height: String, gpa: String, isWatchlisted: Bool) {
        self.name = name
        self.position = position
        self.gradYear = gradYear
        self.location = location
        self.height = height
        self.gpa = gpa
        self.isWatchlisted = isWatchlisted
        self._watchlisted = State(initialValue: isWatchlisted)
    }

    var body: some View {
        Button(action: {
            // Navigate to athlete profile
        }) {
            VStack(spacing: 0) {
                HStack(spacing: 12) {
                    // Profile Image
                    if name == "Kinsley Young" {
                        Image("B302C166-4D7D-4BAD-BBBA-39D27A56987D")
                            .resizable()
                            .scaledToFill()
                            .frame(width: 60, height: 60)
                            .clipShape(Circle())
                    } else {
                        Circle()
                            .fill(Color.gray.opacity(0.3))
                            .frame(width: 60, height: 60)
                            .overlay(
                                Image(systemName: "person.fill")
                                    .foregroundColor(.gray)
                            )
                    }

                    // Info
                    VStack(alignment: .leading, spacing: 6) {
                        HStack {
                            Text(name)
                                .font(.headline)
                                .foregroundColor(.white)

                            Spacer()

                            // Watchlist Button
                            Button(action: {
                                watchlisted.toggle()
                            }) {
                                Image(systemName: watchlisted ? "star.fill" : "star")
                                    .foregroundColor(watchlisted ? .yellow : .gray)
                                    .font(.title3)
                            }
                            .buttonStyle(PlainButtonStyle())
                        }

                        HStack(spacing: 12) {
                            Label(position, systemImage: "figure.volleyball")
                                .font(.caption)
                                .foregroundColor(.blue)

                            Label("'\(gradYear.suffix(2))", systemImage: "graduationcap.fill")
                                .font(.caption)
                                .foregroundColor(.green)
                        }

                        HStack(spacing: 12) {
                            Text(location)
                                .font(.caption)
                                .foregroundColor(.gray)

                            Text("â€¢")
                                .foregroundColor(.gray)

                            Text(height)
                                .font(.caption)
                                .foregroundColor(.gray)

                            Text("â€¢")
                                .foregroundColor(.gray)

                            Text("GPA: \(gpa)")
                                .font(.caption)
                                .foregroundColor(.gray)
                        }
                    }

                    Image(systemName: "chevron.right")
                        .foregroundColor(.gray)
                        .font(.caption)
                }
                .padding(16)
            }
            .background(
                RoundedRectangle(cornerRadius: 16)
                    .fill(Color.black.opacity(0.4))
                    .overlay(
                        RoundedRectangle(cornerRadius: 16)
                            .stroke(Color.white.opacity(0.3), lineWidth: 1)
                    )
            )
        }
        .buttonStyle(PlainButtonStyle())
    }
}

