                        .font(.subheadline)
                        .foregroundColor(.secondary)

                    Button {
                        dismiss()
                    } label: {
                        Text("Sign In")
                            .font(.subheadline)
                            .fontWeight(.semibold)
                            .foregroundColor(.blue)
                    }
                }
                .padding(.bottom, 40)
            }
        }
        .navigationDestination(isPresented: $navigateToAuth) {
            if let userType = selectedUserType {
                SignUpAuthView(userType: userType)
            }
        }
    }
}

