//  Athlete profile setup screen - collects basic info during onboarding
//

import SwiftUI
import Clerk

struct AthleteProfileSetupView: View {
    @Environment(\.clerk) private var clerk
    @Environment(\.dismiss) private var dismiss

    // Auth method determines which fields to show
    let authMethod: SignUpAuthView.AuthMethod

    // Form fields
    @State private var firstName: String = ""
    @State private var lastName: String = ""
    @State private var email: String = ""
    @State private var password: String = ""
    @State private var confirmPassword: String = ""
    @State private var primarySport: String = "Volleyball"
    @State private var graduationYear: String = "2025"

    // UI state
    @State private var isLoading = false
    @State private var errorMessage: String?
    @State private var showPassword = false
    @State private var showConfirmPassword = false
    @State private var navigateToPositionSelection = false
    @State private var isAuthenticating = false

    // Determine if user signed up with email or OAuth based on auth method
    private var usedEmailSignUp: Bool {
