//
//  CollegeCoachProfileSetupView.swift
//  AthleadAI
//
//  College Coach verification and profile setup - verifies .edu email for coaching credentials
//

import SwiftUI
import Clerk

struct CollegeCoachProfileSetupView: View {
    @Environment(\.clerk) private var clerk
    @Environment(\.dismiss) private var dismiss

    // Auth method determines verification flow
    let authMethod: SignUpAuthView.AuthMethod

    // Form fields
    @State private var eduEmail: String = ""
    @State private var firstName: String = ""
    @State private var lastName: String = ""
    @State private var password: String = ""
    @State private var confirmPassword: String = ""
    @State private var phoneNumber: String = ""

    // UI state
    @State private var isLoading = false
    @State private var errorMessage: String?
    @State private var showPassword = false
    @State private var showConfirmPassword = false
    @State private var navigateToNextStep = false
    @State private var isEmailValid = true

    // Verification flow state
    @State private var coachProfile: CoachProfile?
    @State private var navigateToSuccess = false
    @State private var navigateToConnection = false

    // Determine if user signed up with email or OAuth
    private var usedEmailSignUp: Bool {
        return authMethod == .email
    }

    // Check if email is a valid .edu address
    private var isEduEmail: Bool {
        eduEmail.lowercased().hasSuffix(".edu")
    }

    var body: some View {
        ZStack {
            // Background
            Color(.systemBackground)
                .ignoresSafeArea()

            ScrollView {
                VStack(spacing: 24) {
                    // Header
                    VStack(spacing: 12) {
                        // Shield icon matching web design
                        ZStack {
                            Circle()
                                .fill(Color.blue.opacity(0.1))
                                .frame(width: 80, height: 80)

                            Image(systemName: "shield.fill")
                                .font(.system(size: 40))
                                .foregroundColor(.blue)
                        }

                        Text("Coach Verification")
                            .font(.title2)
                            .fontWeight(.bold)

                        Text("Enter your .edu email to verify your coaching credentials")
                            .font(.subheadline)
                            .foregroundColor(.secondary)
                            .multilineTextAlignment(.center)
                            .padding(.horizontal, 32)
                    }
                    .padding(.top, 60)
                    .padding(.bottom, 20)

                    // Form
                    VStack(spacing: 16) {
                        // Educational Email Address (only for email sign-up)
                        if usedEmailSignUp {
                            VStack(alignment: .leading, spacing: 8) {
                                Text("Educational Email Address")
                                    .font(.subheadline)
                                    .fontWeight(.medium)
                                    .foregroundColor(.primary)

                                HStack {
                                    Image(systemName: "envelope")
                                        .foregroundColor(.gray)
                                        .frame(width: 20)

                                    TextField("coach@university.edu", text: $eduEmail)
                                        .keyboardType(.emailAddress)
                                        .autocapitalization(.none)
                                        .onChange(of: eduEmail) { _, newValue in
                                            validateEmail(newValue)
                                        }

                                    if !eduEmail.isEmpty {
                                        if isEduEmail {
                                            Image(systemName: "checkmark.circle.fill")
                                                .foregroundColor(.green)
                                        } else {
                                            Button {
                                                eduEmail = ""
                                            } label: {
                                                Image(systemName: "xmark.circle.fill")
                                                    .foregroundColor(.red)
                                            }
                                        }
                                    }
                                }
                                .padding()
                                .background(Color(.systemGray6))
                                .cornerRadius(8)
                                .overlay(
                                    RoundedRectangle(cornerRadius: 8)
                                        .stroke(isEmailValid ? Color.clear : Color.red, lineWidth: 1)
                                )

                                Text("Must be a valid .edu email address")
                                    .font(.caption)
                                    .foregroundColor(isEmailValid ? .secondary : .red)
                            }

                            // First Name
                            FormField(
                                label: "First Name",
                                icon: "person",
                                placeholder: "First Name",
                                text: $firstName
                            )

                            // Last Name
                            FormField(
                                label: "Last Name",
                                icon: "person",
                                placeholder: "Last Name",
                                text: $lastName
                            )

                            // Password
                            VStack(alignment: .leading, spacing: 8) {
                                Text("Password")
                                    .font(.subheadline)
                                    .fontWeight(.medium)
                                    .foregroundColor(.primary)

                                HStack {
                                    Image(systemName: "lock")
                                        .foregroundColor(.gray)
                                        .frame(width: 20)

                                    if showPassword {
                                        TextField("Password", text: $password)
                                            .autocapitalization(.none)
                                    } else {
                                        SecureField("Password", text: $password)
                                            .autocapitalization(.none)
                                    }

                                    Button {
                                        showPassword.toggle()
                                    } label: {
                                        Image(systemName: showPassword ? "eye.slash" : "eye")
                                            .foregroundColor(.gray)
                                    }

                                    if !password.isEmpty {
                                        Button {
                                            password = ""
                                        } label: {
                                            Image(systemName: "xmark.circle.fill")
                                                .foregroundColor(.gray)
                                        }
                                    }
                                }
                                .padding()
                                .background(Color(.systemGray6))
                                .cornerRadius(8)
                            }

                            // Re-enter Password
                            VStack(alignment: .leading, spacing: 8) {
                                Text("Re-enter Password")
                                    .font(.subheadline)
                                    .fontWeight(.medium)
                                    .foregroundColor(.primary)

                                HStack {
                                    Image(systemName: "lock")
                                        .foregroundColor(.gray)
                                        .frame(width: 20)

                                    if showConfirmPassword {
                                        TextField("Re-enter Password", text: $confirmPassword)
                                            .autocapitalization(.none)
                                    } else {
                                        SecureField("Re-enter Password", text: $confirmPassword)
                                            .autocapitalization(.none)
                                    }

                                    Button {
                                        showConfirmPassword.toggle()
                                    } label: {
                                        Image(systemName: showConfirmPassword ? "eye.slash" : "eye")
                                            .foregroundColor(.gray)
                                    }

                                    if !confirmPassword.isEmpty {
                                        Button {
                                            confirmPassword = ""
                                        } label: {
                                            Image(systemName: "xmark.circle.fill")
                                                .foregroundColor(.gray)
                                        }
                                    }
                                }
                                .padding()
                                .background(Color(.systemGray6))
                                .cornerRadius(8)
                            }
                        } else {
                            // For OAuth users, show email as read-only verified field
                            VStack(alignment: .leading, spacing: 8) {
                                Text("Educational Email Address")
                                    .font(.subheadline)
                                    .fontWeight(.medium)
                                    .foregroundColor(.primary)

                                HStack {
                                    Image(systemName: "envelope")
                                        .foregroundColor(.gray)
                                        .frame(width: 20)

                                    Text(eduEmail.isEmpty ? clerk.user?.emailAddresses.first?.emailAddress ?? "" : eduEmail)
                                        .foregroundColor(.primary)

                                    Spacer()

                                    if isEduEmail {
                                        HStack(spacing: 4) {
                                            Image(systemName: "checkmark.circle.fill")
                                                .foregroundColor(.green)
                                            Text("Verified")
                                                .font(.caption)
                                                .foregroundColor(.green)
                                        }
                                    }
                                }
                                .padding()
                                .background(Color(.systemGray6))
                                .cornerRadius(8)

                                Text("Verified through your authentication provider")
                                    .font(.caption)
                                    .foregroundColor(.secondary)
                            }

                            // First Name
                            FormField(
                                label: "First Name",
                                icon: "person",
                                placeholder: "First Name",
                                text: $firstName
                            )

                            // Last Name
                            FormField(
                                label: "Last Name",
                                icon: "person",
                                placeholder: "Last Name",
                                text: $lastName
                            )
                        }

                        // Phone Number (optional)
                        FormField(
                            label: "Phone Number (Optional)",
                            icon: "phone",
                            placeholder: "(555) 123-4567",
                            text: $phoneNumber,
                            keyboardType: .phonePad,
                            autocapitalization: .never
                        )
                    }
                    .padding(.horizontal, 24)

                    // Error message
                    if let errorMessage = errorMessage {
                        HStack {
                            Image(systemName: "exclamationmark.triangle.fill")
                            Text(errorMessage)
                                .font(.footnote)
                        }
                        .foregroundColor(.red)
                        .padding()
                        .background(Color.red.opacity(0.1))
                        .cornerRadius(10)
                        .padding(.horizontal, 24)
                        .transition(.scale.combined(with: .opacity))
                    }

                    // Submit Button
                    Button {
                        submitForm()
                    } label: {
                        HStack {
                            if isLoading {
                                ProgressView()
                                    .progressViewStyle(CircularProgressViewStyle(tint: .white))
                                    .scaleEffect(0.9)
                            } else {
                                Text("Verify Coach Account")
                                    .font(.headline)
                                    .fontWeight(.semibold)
                            }
                        }
                        .frame(maxWidth: .infinity)
                        .padding()
                        .background(
                            isFormValid ? Color.blue : Color.gray.opacity(0.5)
                        )
                        .foregroundStyle(.white)
                        .cornerRadius(8)
                    }
                    .disabled(!isFormValid || isLoading)
                    .padding(.horizontal, 24)
                    .padding(.top, 8)

                    Spacer()
                }
                .padding(.bottom, 40)
            }
        }
        .navigationDestination(isPresented: $navigateToSuccess) {
            if let profile = coachProfile {
                CollegeCoachVerificationSuccessView(coachProfile: profile)
            }
        }
        .navigationDestination(isPresented: $navigateToConnection) {
            CollegeCoachProfileConnectionView(
                eduEmail: eduEmail,
                firstName: firstName,
                lastName: lastName
            )
        }
        .onAppear {
            // Pre-populate email from OAuth provider if available
            if authMethod != .email {
                if let primaryEmail = clerk.user?.emailAddresses.first?.emailAddress {
                    eduEmail = primaryEmail
                    validateEmail(primaryEmail)
                }

                // Pre-populate name fields
                if firstName.isEmpty, let clerkFirstName = clerk.user?.firstName {
                    firstName = clerkFirstName
                }
                if lastName.isEmpty, let clerkLastName = clerk.user?.lastName {
                    lastName = clerkLastName
                }
            }
        }
    }

    private func validateEmail(_ email: String) {
        if email.isEmpty {
            isEmailValid = true
            return
        }

        isEmailValid = email.lowercased().hasSuffix(".edu") && email.contains("@")
    }

    private var isFormValid: Bool {
        // Basic validation
        let basicFieldsValid = !firstName.isEmpty && !lastName.isEmpty

        // Email validation - must be .edu
        let emailValid = !eduEmail.isEmpty && isEduEmail

        if usedEmailSignUp {
            // Email sign-up: validate email and password
            let passwordValid = !password.isEmpty && password.count >= 8
            let passwordsMatch = password == confirmPassword

            return basicFieldsValid && emailValid && passwordValid && passwordsMatch
        } else {
            // OAuth sign-up: only basic fields needed (email already verified)
            return basicFieldsValid && emailValid
        }
    }

    private func submitForm() {
        errorMessage = nil

        // Validate .edu email
        if !isEduEmail {
            errorMessage = "Please enter a valid .edu email address"
            return
        }

        // Validate password match for email sign-up
        if usedEmailSignUp && password != confirmPassword {
            errorMessage = "Passwords do not match"
            return
        }

        isLoading = true

        Task {
            do {
                if usedEmailSignUp {
                    // Create Clerk account with .edu email
                    try await createCollegeCoachAccount()
                } else {
                    // For OAuth, verify the .edu email is associated with account
                    guard clerk.user != nil else {
                        await MainActor.run {
                            errorMessage = "Authentication required"
                            isLoading = false
                        }
                        return
                    }
                }

                // Check if coach exists in database
                let foundProfile = try await checkCoachInDatabase()

                await MainActor.run {
                    isLoading = false

                    if let profile = foundProfile {
                        // Coach found - show success screen
                        coachProfile = profile
                        navigateToSuccess = true
                    } else {
                        // Coach not found - show profile connection screen
                        navigateToConnection = true
                    }
                }
            } catch {
                await MainActor.run {
                    errorMessage = error.localizedDescription
                    isLoading = false
                }
            }
        }
    }

