import SwiftUI

struct AIView: View {
    @State private var viewModel = AIViewModel()
    @FocusState private var isInputFocused: Bool

    var body: some View {
        NavigationView {
            ZStack {
                backgroundGradient

                VStack(spacing: 0) {
                    chatMessagesView
                    inputBar
                }
            }
            .navigationTitle("Alex")
            .navigationBarTitleDisplayMode(.inline)
        }
    }

    private var backgroundGradient: some View {
        LinearGradient(
            gradient: Gradient(colors: [
                Color(red: 0.25, green: 0.35, blue: 0.55),
                Color(red: 0.15, green: 0.25, blue: 0.45)
            ]),
            startPoint: .topLeading,
            endPoint: .bottomTrailing
        )
        .ignoresSafeArea()
    }

    private var chatMessagesView: some View {
        ScrollViewReader { proxy in
            ScrollView {
                LazyVStack(alignment: .leading, spacing: 12) {
                    ForEach(viewModel.messages) { message in
                        MessageBubble(message: message)
                            .id(message.id)
                    }

                    if viewModel.isProcessing {
                        HStack {
                            ProgressView()
                                .tint(.white)
                                .padding(.trailing, 8)
                            Text("Alex is thinking...")
                                .foregroundStyle(.white.opacity(0.7))
                                .font(.subheadline)
                        }
                        .padding(.leading)
                    }
                }
                .padding()
                .padding(.bottom, 80)
            }
            .onTapGesture {
                isInputFocused = false
            }
            .onChange(of: viewModel.messages.count) { _, _ in
                if let lastMessage = viewModel.messages.last {
                    withAnimation {
                        proxy.scrollTo(lastMessage.id, anchor: .bottom)
                    }
                }
            }
        }
    }

    private var inputBar: some View {
        HStack(alignment: .bottom, spacing: 12) {
            inputTextField
            sendButton
        }
        .padding(.horizontal, 16)
        .padding(.vertical, 12)
        .background(inputBarBackground)
    }

    private var inputTextField: some View {
        TextField("", text: $viewModel.userInput, prompt: Text("What can Alex help with?").foregroundColor(.white.opacity(0.5)), axis: .vertical)
            .foregroundColor(.white)
            .tint(.white)
            .padding(14)
            .background(textFieldBackground)
            .lineLimit(1...5)
            .disabled(viewModel.isProcessing)
            .focused($isInputFocused)
    }

    private var textFieldBackground: some View {
        RoundedRectangle(cornerRadius: 22)
            .fill(Color.black.opacity(0.4))
            .overlay(
                RoundedRectangle(cornerRadius: 22)
                    .stroke(Color.white.opacity(0.3), lineWidth: 1)
            )
    }

    private var sendButton: some View {
        Button {
            isInputFocused = false
            Task {
                await viewModel.sendMessage()
            }
        } label: {
            ZStack {
                Circle()
                    .fill(sendButtonGradient)
                    .frame(width: 44, height: 44)

                Image(systemName: "arrow.up")
                    .font(.system(size: 20, weight: .semibold))
                    .foregroundColor(.white)
            }
        }
        .disabled(viewModel.userInput.isEmpty || viewModel.isProcessing)
    }

    private var sendButtonGradient: LinearGradient {
        LinearGradient(
            colors: viewModel.userInput.isEmpty
                ? [.gray.opacity(0.5), .gray.opacity(0.3)]
                : [Color(red: 0.35, green: 0.8, blue: 0.95), Color(red: 0.2, green: 0.6, blue: 0.9)],
            startPoint: .topLeading,
            endPoint: .bottomTrailing
        )
    }

    private var inputBarBackground: some View {
        Rectangle()
            .fill(.ultraThinMaterial)
            .overlay(
                Rectangle()
                    .fill(
                        LinearGradient(
                            colors: [
                                Color.black.opacity(0.3),
                                Color.black.opacity(0.1)
                            ],
                            startPoint: .top,
                            endPoint: .bottom
                        )
                    )
            )
    }
}

struct MessageBubble: View {
    let message: ChatMessage

    var body: some View {
        HStack(alignment: .bottom, spacing: 8) {
            if message.role == .user {
                Spacer(minLength: 60)
            }

            // Alex avatar for assistant messages
            if message.role == .assistant {
                Circle()
                    .fill(
                        LinearGradient(
                            colors: [.blue, .purple],
                            startPoint: .topLeading,
                            endPoint: .bottomTrailing
                        )
                    )
                    .frame(width: 32, height: 32)
                    .overlay(
                        Image(systemName: "sparkles")
                            .font(.system(size: 14))
                            .foregroundColor(.white)
                    )
            }

            VStack(alignment: message.role == .user ? .trailing : .leading, spacing: 4) {
                Text(message.content)
                    .font(.body)
                    .padding(14)
                    .background(
                        RoundedRectangle(cornerRadius: 18)
                            .fill(
                                message.role == .user
                                    ? LinearGradient(
                                        colors: [Color(red: 0.35, green: 0.8, blue: 0.95), Color(red: 0.2, green: 0.6, blue: 0.9)],
                                        startPoint: .topLeading,
                                        endPoint: .bottomTrailing
                                    )
                                    : LinearGradient(
                                        colors: [Color.black.opacity(0.5), Color.black.opacity(0.4)],
                                        startPoint: .topLeading,
                                        endPoint: .bottomTrailing
                                    )
                            )
                            .overlay(
                                RoundedRectangle(cornerRadius: 18)
                                    .stroke(
                                        message.role == .user
                                            ? Color.white.opacity(0.5)
                                            : Color.white.opacity(0.3),
                                        lineWidth: 1
                                    )
                            )
                    )
                    .foregroundStyle(.white)
            }
            .frame(maxWidth: .infinity * 0.75, alignment: message.role == .user ? .trailing : .leading)

            if message.role == .assistant {
                Spacer(minLength: 60)
            }
        }
    }
}

