//
//  CollegeCoachProgramProfileView.swift
//  AthleadAI
//
//  Sports program profile view - showcases team, facilities, and recruiting info
//

import SwiftUI

enum ProgramTab: String, CaseIterable {
    case overview = "Overview"
    case roster = "Roster"
    case facilities = "Facilities"
}

struct CollegeCoachProgramProfileView: View {
    var viewModel: ProgramProfileViewModel
    @State private var selectedTab: ProgramTab = .overview
    @State private var showingEditView = false

    var body: some View {
        ScrollView {
            VStack(spacing: 0) {
                // Hero Banner
                ProgramHeroSection(
                    heroImageURL: viewModel.heroImageURL,
                    logoURL: viewModel.logoURL,
                    programName: viewModel.programName,
                    conference: viewModel.conference,
                    division: viewModel.division,
                    gradientColors: viewModel.gradientColors
                )

                // Quick Stats
                QuickStatsView(
                    founded: viewModel.founded,
                    conferenceTitles: viewModel.conferenceChampionships,
                    nationalRanking: viewModel.currentRanking
                )
                .padding(.horizontal, 20)
                .padding(.top, -30)

                // Tab Navigation
                ProgramTabBarView(selectedTab: $selectedTab)
                    .padding(.top, 20)

                // Tab Content
                VStack(spacing: 16) {
                    switch selectedTab {
                    case .overview:
                        overviewContent
                    case .roster:
                        rosterContent
                    case .facilities:
                        facilitiesContent
                    }
                }
                .padding(.horizontal, 20)
                .padding(.top, 20)
                .padding(.bottom, 40)
            }
        }
        .background(Color(.systemBackground))
        .navigationTitle("Program Profile")
        .navigationBarTitleDisplayMode(.inline)
        .toolbar {
            ToolbarItem(placement: .navigationBarTrailing) {
                if viewModel.canEdit {
                    Button("Edit") {
                        showingEditView = true
                    }
                }
            }
        }
        .sheet(isPresented: $showingEditView) {
            // TODO: Implement EditProgramProfileView
            Text("Edit Program Profile")
                .font(.title2)
                .padding()
        }
    }

    private var overviewContent: some View {
        VStack(spacing: 16) {
            // Program Overview
            ContentSection(title: "Program Overview") {
                Text(viewModel.overview)
                    .font(.body)
                    .foregroundColor(.primary)
            }

            // Recent Achievements
            ContentSection(title: "Recent Achievements") {
                VStack(spacing: 12) {
                    ForEach(viewModel.recentAchievements) { achievement in
                        AchievementBadge(
                            icon: achievement.icon,
                            title: achievement.title,
                            subtitle: String(achievement.year)
                        )
                    }
                }
            }

            // Culture & Values
            ContentSection(title: "Program Culture & Values") {
                VStack(spacing: 12) {
                    ScrollView(.horizontal, showsIndicators: false) {
                        HStack(spacing: 12) {
                            ForEach(viewModel.cultureValues, id: \.self) { value in
                                Text(value)
                                    .font(.caption)
                                    .fontWeight(.semibold)
                                    .foregroundColor(.blue)
                                    .padding(.horizontal, 12)
                                    .padding(.vertical, 6)
                                    .background(Color.blue.opacity(0.1))
                                    .cornerRadius(16)
                            }
                        }
                    }

                    Text(viewModel.cultureDescription)
                        .font(.body)
                        .foregroundColor(.secondary)
                }
            }

            // Recruiting Priorities
            ContentSection(title: "Recruiting Priorities") {
                VStack(alignment: .leading, spacing: 16) {
                    ForEach(viewModel.recruitingPriorities) { priority in
                        VStack(alignment: .leading, spacing: 8) {
                            Text(priority.graduatingClass)
                                .font(.subheadline)
                                .fontWeight(.semibold)
                                .foregroundColor(.primary)

                            VStack(spacing: 8) {
                                ForEach(priority.positions) { position in
                                    RecruitingPriorityCard(
                                        position: position.position,
                                        isPriority: position.isPriority
                                    )
                                }
                            }
                        }
                    }
                }
            }

            // Academic Support
            ContentSection(title: "Academic Support") {
                VStack(alignment: .leading, spacing: 12) {
                    Text(viewModel.academicDescription)
                        .font(.body)
                        .foregroundColor(.primary)

                    HStack(spacing: 20) {
                        StatsPill(
                            value: String(format: "%.1f", viewModel.teamGPA),
                            label: "Team GPA"
                        )
                        StatsPill(
                            value: "\(Int(viewModel.graduationRate))%",
                            label: "Grad Rate"
                        )
                    }
                    .frame(maxWidth: .infinity)
                }
            }

            // Coaching Staff
            ContentSection(title: "Coaching Staff") {
                VStack(spacing: 12) {
                    ForEach(viewModel.coachingStaff) { coach in
                        CoachStaffRow(coach: coach)
                    }
                }
            }
        }
    }

    private var rosterContent: some View {
        VStack(spacing: 16) {
            // Filter Options
            ScrollView(.horizontal, showsIndicators: false) {
                HStack(spacing: 12) {
                    ForEach(["All", "Seniors", "Juniors", "Sophomores", "Freshmen"], id: \.self) { filter in
                        FilterChip(title: filter, isSelected: viewModel.selectedFilter == filter) {
                            viewModel.selectedFilter = filter
                        }
                    }
                }
            }

            // Roster by Class
            ForEach(viewModel.rosterByClass, id: \.classYear) { section in
                VStack(alignment: .leading, spacing: 12) {
                    Text("\(section.classYear) (\(section.players.count))")
                        .font(.headline)
                        .foregroundColor(.primary)

                    VStack(spacing: 12) {
                        ForEach(section.players) { player in
                            PlayerCardView(player: player)
                        }
                    }
                }
            }

            // Contact CTA
            VStack(spacing: 12) {
                Text("Interested in joining?")
                    .font(.headline)
                    .foregroundColor(.primary)

                Button {
                    // TODO: Implement contact action
                } label: {
                    Text("Contact Coaching Staff")
                        .font(.headline)
                        .fontWeight(.semibold)
                        .frame(maxWidth: .infinity)
                        .padding()
                        .background(Color.blue)
                        .foregroundStyle(.white)
                        .cornerRadius(8)
                }
            }
            .padding(.top, 8)
        }
    }

    private var facilitiesContent: some View {
        VStack(spacing: 16) {
            ForEach(viewModel.facilities) { facility in
                FacilityCardView(facility: facility)
            }
        }
    }
}

// MARK: - Program Hero Section
struct ProgramHeroSection: View {
    let heroImageURL: String
    let logoURL: String
    let programName: String
    let conference: String
    let division: String
    let gradientColors: [Color]

    var body: some View {
        ZStack(alignment: .bottom) {
            // Background image or gradient
            if !heroImageURL.isEmpty {
                AsyncImage(url: URL(string: heroImageURL)) { image in
                    image
                        .resizable()
                        .aspectRatio(contentMode: .fill)
                } placeholder: {
                    LinearGradient(
                        gradient: Gradient(colors: gradientColors),
                        startPoint: .topLeading,
                        endPoint: .bottomTrailing
                    )
                }
                .frame(height: 300)
                .clipped()
            } else {
                LinearGradient(
                    gradient: Gradient(colors: gradientColors),
                    startPoint: .topLeading,
                    endPoint: .bottomTrailing
                )
                .frame(height: 300)
            }

            // Overlay gradient for better text readability
            LinearGradient(
                gradient: Gradient(colors: [.clear, .black.opacity(0.6)]),
                startPoint: .top,
                endPoint: .bottom
            )
            .frame(height: 300)

            VStack(spacing: 12) {
                // Program Logo
                AsyncImage(url: URL(string: logoURL)) { image in
                    image
                        .resizable()
                        .aspectRatio(contentMode: .fit)
                } placeholder: {
                    Image(systemName: "sportscourt.fill")
                        .resizable()
                        .foregroundColor(.white.opacity(0.5))
                }
                .frame(width: 80, height: 80)
                .background(Color.white.opacity(0.2))
                .clipShape(RoundedRectangle(cornerRadius: 12))
                .overlay(
                    RoundedRectangle(cornerRadius: 12)
                        .stroke(Color.white, lineWidth: 2)
                )

                // Program Name
                Text(programName)
                    .font(.system(size: 24, weight: .bold))
                    .foregroundColor(.white)

                // Conference & Division
                HStack(spacing: 16) {
                    Label(conference, systemImage: "trophy.fill")
                    Label(division, systemImage: "flag.fill")
                }
                .font(.subheadline)
                .foregroundColor(.white.opacity(0.9))
            }
            .padding(.bottom, 50)
        }
    }
}

// MARK: - Quick Stats View
struct QuickStatsView: View {
    let founded: Int
    let conferenceTitles: Int
    let nationalRanking: Int

    var body: some View {
        HStack(spacing: 0) {
            StatsPill(value: String(founded), label: "Founded")
            Divider().frame(height: 40)
            StatsPill(value: String(conferenceTitles), label: "Conf. Titles")
            Divider().frame(height: 40)
            StatsPill(value: "#\(nationalRanking)", label: "Nat. Rank")
        }
        .padding(.vertical, 16)
        .background(Color(.systemBackground))
        .cornerRadius(12)
        .shadow(color: Color.black.opacity(0.1), radius: 10, x: 0, y: 5)
    }
}

// MARK: - Tab Bar View
struct ProgramTabBarView: View {
    @Binding var selectedTab: ProgramTab

    var body: some View {
        HStack(spacing: 0) {
            ForEach(ProgramTab.allCases, id: \.self) { tab in
                Button {
                    withAnimation(.spring()) {
                        selectedTab = tab
                    }
                } label: {
                    Text(tab.rawValue)
                        .font(.subheadline)
                        .fontWeight(selectedTab == tab ? .semibold : .regular)
                        .foregroundColor(selectedTab == tab ? .blue : .secondary)
                        .frame(maxWidth: .infinity)
                        .padding(.vertical, 12)
                        .background(
                            selectedTab == tab ?
                                Color.blue.opacity(0.1) : Color.clear
                        )
                }
            }
        }
        .background(Color(.systemGray6))
        .cornerRadius(8)
        .padding(.horizontal, 20)
    }
}

// MARK: - Content Section
struct ContentSection<Content: View>: View {
    let title: String
    @ViewBuilder let content: () -> Content

    var body: some View {
        VStack(alignment: .leading, spacing: 12) {
            Text(title)
                .font(.headline)
                .foregroundColor(.primary)

            VStack(spacing: 0) {
                content()
            }
            .padding(16)
            .frame(maxWidth: .infinity, alignment: .leading)
            .background(Color(.systemBackground))
            .cornerRadius(12)
            .overlay(
                RoundedRectangle(cornerRadius: 12)
                    .stroke(Color(.separator), lineWidth: 1)
            )
        }
    }
}

// MARK: - Achievement Badge
struct AchievementBadge: View {
    let icon: String
    let title: String
    let subtitle: String

    var body: some View {
        HStack(spacing: 12) {
            Text(icon)
                .font(.system(size: 32))

            VStack(alignment: .leading, spacing: 2) {
                Text(title)
                    .font(.body)
                    .fontWeight(.semibold)
                    .foregroundColor(.primary)

                Text(subtitle)
                    .font(.caption)
                    .foregroundColor(.secondary)
            }

            Spacer()
        }
        .padding(12)
        .background(Color(.systemGray6))
        .cornerRadius(8)
    }
}

// MARK: - Recruiting Priority Card
struct RecruitingPriorityCard: View {
    let position: String
    let isPriority: Bool

    var body: some View {
        HStack {
            Image(systemName: "figure.volleyball")
                .foregroundColor(.blue)

            Text(position)
                .font(.body)
                .foregroundColor(.primary)

            Spacer()

            if isPriority {
                Text("PRIORITY")
                    .font(.caption2)
                    .fontWeight(.bold)
                    .foregroundColor(.white)
                    .padding(.horizontal, 8)
                    .padding(.vertical, 4)
                    .background(Color.orange)
                    .cornerRadius(4)
            }
        }
        .padding(12)
        .background(Color(.systemGray6))
        .cornerRadius(8)
    }
}

// MARK: - Stats Pill
struct StatsPill: View {
    let value: String
    let label: String

    var body: some View {
        VStack(spacing: 4) {
            Text(value)
                .font(.system(size: 24, weight: .bold))
                .foregroundColor(.primary)

            Text(label)
                .font(.caption)
                .foregroundColor(.secondary)
        }
        .frame(maxWidth: .infinity)
    }
}

// MARK: - Filter Chip
struct FilterChip: View {
    let title: String
    let isSelected: Bool
    let action: () -> Void

    var body: some View {
        Button(action: action) {
            Text(title)
                .font(.subheadline)
                .fontWeight(isSelected ? .semibold : .regular)
                .foregroundColor(isSelected ? .white : .primary)
                .padding(.horizontal, 16)
                .padding(.vertical, 8)
                .background(isSelected ? Color.blue : Color(.systemGray6))
                .cornerRadius(20)
        }
    }
}

// MARK: - Player Card View
struct PlayerCardView: View {
    let player: PlayerItem

    var body: some View {
        HStack(spacing: 12) {
            // Player Photo
            AsyncImage(url: URL(string: player.photoURL)) { image in
                image
                    .resizable()
                    .aspectRatio(contentMode: .fill)
            } placeholder: {
                Image(systemName: "person.fill")
                    .resizable()
                    .foregroundColor(.gray)
            }
            .frame(width: 60, height: 60)
            .clipShape(RoundedRectangle(cornerRadius: 8))

            VStack(alignment: .leading, spacing: 4) {
                HStack {
                    Text("#\(player.jerseyNumber)")
                        .font(.caption)
                        .fontWeight(.bold)
                        .foregroundColor(.blue)
                    Text(player.name)
                        .font(.body)
                        .fontWeight(.semibold)
                        .foregroundColor(.primary)
                }

                Text(player.position)
                    .font(.caption)
                    .foregroundColor(.secondary)

                HStack(spacing: 4) {
                    Text(player.height)
                    Text("â€¢")
                    Text(player.hometown)
                }
                .font(.caption)
                .foregroundColor(.secondary)

                // Star Rating
                HStack(spacing: 2) {
                    ForEach(0..<player.starRating, id: \.self) { _ in
                        Image(systemName: "star.fill")
                            .font(.system(size: 10))
                            .foregroundColor(.orange)
                    }
                }
            }

            Spacer()
        }
        .padding(12)
        .background(Color(.systemGray6))
        .cornerRadius(12)
    }
}

// MARK: - Coach Staff Row
struct CoachStaffRow: View {
    let coach: CoachStaffItem

    var body: some View {
        HStack(spacing: 12) {
            // Coach Photo
            AsyncImage(url: URL(string: coach.photoURL)) { image in
