//
//  GlassCard.swift
//  A glass-morphism card component with blur effect and border
//
//  Usage:
//  GlassCard {
//      Text("Hello")
//  }
//
//  Or as modifier:
//  VStack { Text("Hello") }
//      .glassCard()
//
//  When to use:
//  - Use .glassCard() for cards on solid/gradient backgrounds
//  - Use .photoOverlayCard() for cards over photo/image backgrounds
//    (PhotoOverlayCard uses Material effects for better contrast on any image)
//

import SwiftUI

/// A glass-morphism card with blur effect and border
/// Uses DesignTheme colors for consistent styling
struct GlassCard<Content: View>: View {
    let content: Content
    var cornerRadius: CGFloat = .radiusLarge
    var padding: CGFloat = .spacing4

    @Environment(\.designTheme) private var theme
    @Environment(\.colorScheme) private var colorScheme

    init(
        cornerRadius: CGFloat = .radiusLarge,
        padding: CGFloat = .spacing4,
        @ViewBuilder content: () -> Content
    ) {
        self.cornerRadius = cornerRadius
        self.padding = padding
        self.content = content()
    }

    var body: some View {
        content
            .padding(padding)
            .background(
                RoundedRectangle(cornerRadius: cornerRadius, style: .continuous)
                    .fill(theme.color(.card))
                    .overlay(
                        RoundedRectangle(cornerRadius: cornerRadius, style: .continuous)
                            .stroke(theme.color(.border).opacity(0.3), lineWidth: 1)
                    )
            )
    }
}

// MARK: - ViewModifier Version

struct GlassCardModifier: ViewModifier {
    var cornerRadius: CGFloat = .radiusLarge
    var padding: CGFloat = .spacing4

    @Environment(\.designTheme) private var theme

    func body(content: Content) -> some View {
        content
            .padding(padding)
            .background(
                RoundedRectangle(cornerRadius: cornerRadius, style: .continuous)
                    .fill(theme.color(.card))
                    .overlay(
                        RoundedRectangle(cornerRadius: cornerRadius, style: .continuous)
                            .stroke(theme.color(.border).opacity(0.3), lineWidth: 1)
                    )
            )
    }
}

// MARK: - Automatic Upgrade & Deprecation

extension View {
    /// Applies glass card styling to the view
    ///
    /// **DEPRECATED:** For iOS 26+, use `.liquidGlassCard()` instead for modern Liquid Glass effects.
    /// This legacy implementation uses solid fills and will be removed in a future release.
    ///
    /// On iOS 26+, this method automatically upgrades to Liquid Glass when feature flag is enabled.
    /// On iOS 25 and below, falls back to legacy solid fill implementation.
    ///
    /// - Parameters:
    ///   - cornerRadius: Corner radius of the card (default: .radiusLarge)
    ///   - padding: Internal padding (default: .spacing4)
    /// - Returns: View with glass card styling applied
    @available(*, deprecated, message: "Use liquidGlassCard() for iOS 26+ with Liquid Glass effects")
    func glassCard(cornerRadius: CGFloat = .radiusLarge, padding: CGFloat = .spacing4) -> some View {
        if #available(iOS 26.0, *), FeatureFlags.useLiquidGlass {
            // Automatically upgrade to Liquid Glass on iOS 26+
            return AnyView(self.liquidGlassCard(cornerRadius: cornerRadius, padding: padding))
        } else {
            // Fallback to legacy implementation
            return AnyView(self.modifier(GlassCardModifier(cornerRadius: cornerRadius, padding: padding)))
        }
    }
}

