//
//  BrandButton.swift
//  Enhanced button component with icon, loading state, and haptic feedback
//
//  Usage:
//  BrandButton(title: "Sign In", icon: "arrow.right", style: .primary) {
//      // action
//  }
//

import SwiftUI

/// App-specific button wrapper with enhanced features
struct BrandButton: View {
    let title: String
    let icon: String?
    let style: ButtonStyle
    let size: ButtonSize
    let isLoading: Bool
    let action: () -> Void

    enum ButtonStyle {
        case primary
        case secondary
        case destructive
        case outline
        case glass              // iOS 26+ Liquid Glass button
        case glassProminent     // iOS 26+ Liquid Glass prominent button

        var dsIntent: DSButton.Intent {
            switch self {
            case .primary: return .primary
            case .secondary: return .secondary
            case .destructive: return .destructive
            case .outline: return .secondary
            case .glass: return .secondary
            case .glassProminent: return .primary
            }
        }
    }

    enum ButtonSize {
        case small
        case medium
        case large

        var dsSize: DSButton.Size {
            switch self {
            case .small: return .s
            case .medium: return .m
            case .large: return .l
            }
        }
    }

    @Environment(\.isEnabled) private var isEnabled
    @Environment(\.designTheme) private var theme

    /// Determines if iOS 18+ system glass button styles should be used
    private var useSystemGlassStyle: Bool {
        if #available(iOS 18.0, *) {
            return FeatureFlags.useLiquidGlass && (style == .glass || style == .glassProminent)
        }
        return false
    }

    init(
        title: String,
        icon: String? = nil,
        style: ButtonStyle = .primary,
        size: ButtonSize = .medium,
        isLoading: Bool = false,
        action: @escaping () -> Void
    ) {
        self.title = title
        self.icon = icon
        self.style = style
        self.size = size
        self.isLoading = isLoading
        self.action = action
    }

    var body: some View {
        if useSystemGlassStyle {
            glassButton
        } else if style == .outline {
            outlineButton
        } else {
            standardButton
        }
    }

    // MARK: - Glass Button (iOS 18+)

    @available(iOS 18.0, *)
    private var glassButton: some View {
        Button(action: {
            HapticManager.shared.medium()
            action()
        }) {
            HStack(spacing: .spacing3) {
                if isLoading {
                    ProgressView()
                } else {
                    if let icon = icon {
                        Image(systemName: icon)
                    }
                    Text(title)
                        .font(theme.font(.body))
                }
            }
            .frame(maxWidth: .infinity)
            .padding(.horizontal, horizontalPadding)
            .padding(.vertical, verticalPadding)
            .foregroundStyle(foregroundColor)
        }
        .buttonStyle(.plain)
        .liquidGlassCard(
            glassVariant: style == .glassProminent ? "regular" : "clear",
            cornerRadius: .radiusLarge,
            padding: 0, // No extra padding, we already have it in the content
            isInteractive: true,
            tintColor: style == .glassProminent ? theme.color(.primary) : nil
        )
        .disabled(!isEnabled || isLoading)
        .opacity(isEnabled && !isLoading ? 1.0 : 0.5)
        .accessibilityLabel(title)
    }

    // MARK: - Standard Button (using DSButton)

    private var standardButton: some View {
        Button(action: {
            HapticManager.shared.medium()
            action()
        }) {
            HStack(spacing: .spacing3) {
                if isLoading {
                    ProgressView()
                        .tint(foregroundColor)
                } else {
                    if let icon = icon {
                        Image(systemName: icon)
                    }
                    Text(title)
                        .font(theme.font(.body))
                }
            }
            .frame(maxWidth: .infinity)
            .padding(.horizontal, horizontalPadding)
            .padding(.vertical, verticalPadding)
            .foregroundStyle(foregroundColor)
            .background(backgroundColor)
            .clipShape(RoundedRectangle(cornerRadius: .radiusLarge, style: .continuous))
            .shadow(color: shadowColor, radius: 8, y: 4)
        }
        .disabled(!isEnabled || isLoading)
        .opacity(isEnabled && !isLoading ? 1.0 : 0.5)
        .accessibilityLabel(title)
    }

    // MARK: - Outline Button

    private var outlineButton: some View {
        Button(action: {
            HapticManager.shared.medium()
            action()
        }) {
            HStack(spacing: .spacing3) {
                if isLoading {
                    ProgressView()
                        .tint(theme.color(.primary))
                } else {
                    if let icon = icon {
                        Image(systemName: icon)
                    }
                    Text(title)
                        .font(theme.font(.body))
                }
            }
            .frame(maxWidth: .infinity)
            .padding(.horizontal, horizontalPadding)
            .padding(.vertical, verticalPadding)
            .foregroundStyle(theme.color(.primary))
            .background(
                RoundedRectangle(cornerRadius: .radiusLarge, style: .continuous)
                    .stroke(theme.color(.primary), lineWidth: 2)
            )
        }
        .disabled(!isEnabled || isLoading)
        .opacity(isEnabled && !isLoading ? 1.0 : 0.5)
        .accessibilityLabel(title)
    }

    // MARK: - Helper Properties

    private var horizontalPadding: CGFloat {
        switch size {
        case .small: return .spacing3   // 12pt
        case .medium: return .spacing4  // 16pt
        case .large: return .spacing5   // 20pt
        }
    }

    private var verticalPadding: CGFloat {
        switch size {
        case .small: return .spacing2   // 8pt (closest to 10)
        case .medium: return .spacing4  // 16pt (closest to 14)
        case .large: return .spacing5   // 20pt (closest to 18)
        }
    }

    private var foregroundColor: Color {
        switch style {
        case .primary:
            return theme.color(.primaryforeground)
        case .secondary:
            return theme.color(.secondaryforeground)
        case .destructive:
            return theme.color(.destructiveforeground)
        case .outline:
            return theme.color(.primary)
        case .glass:
            // Standard glass button uses system defaults
            return theme.color(.foreground)
        case .glassProminent:
            // Prominent glass button uses primary foreground
            return theme.color(.primaryforeground)
        }
    }

    private var backgroundColor: Color {
        switch style {
        case .primary:
            return theme.color(.primary)
        case .secondary:
            return theme.color(.secondary)
        case .destructive:
            return theme.color(.destructive)
        case .outline:
            return Color.clear
        case .glass, .glassProminent:
            // Not used for glass buttons (system styles handle this)
            return Color.clear
        }
    }

    private var shadowColor: Color {
        switch style {
        case .primary:
            return theme.color(.ring).opacity(0.12)
        case .secondary:
            return Color.black.opacity(0.05)
        case .destructive:
            return theme.color(.destructive).opacity(0.12)
        case .outline:
            return Color.clear
        case .glass, .glassProminent:
            // Not used for glass buttons (system styles handle this)
            return Color.clear
        }
    }
}

// MARK: - Preview

#if DEBUG
struct BrandButton_Previews: PreviewProvider {
    static var previews: some View {
        Group {
            // Light mode
            VStack(spacing: .spacing4) {
                BrandButton(title: "Primary Button", style: .primary) {}
                BrandButton(title: "With Icon", icon: "arrow.right", style: .primary) {}
                BrandButton(title: "Secondary", style: .secondary) {}
                BrandButton(title: "Destructive", style: .destructive) {}
                BrandButton(title: "Outline", style: .outline) {}
                BrandButton(title: "Loading", style: .primary, isLoading: true) {}

                HStack(spacing: .spacing3) {
                    BrandButton(title: "Small", style: .primary, size: .small) {}
                    BrandButton(title: "Medium", style: .primary, size: .medium) {}
                    BrandButton(title: "Large", style: .primary, size: .large) {}
                }
            }
            .padding()
            .withDesignTheme()
            .background(DesignTheme.defaultLight.color(.background))
            .previewDisplayName("Light Mode")

            // Dark mode
            VStack(spacing: .spacing4) {
                BrandButton(title: "Primary Button", style: .primary) {}
                BrandButton(title: "With Icon", icon: "arrow.right", style: .primary) {}
                BrandButton(title: "Secondary", style: .secondary) {}
                BrandButton(title: "Destructive", style: .destructive) {}
                BrandButton(title: "Outline", style: .outline) {}
            }
            .padding()
            .withDesignTheme()
            .background(DesignTheme.defaultDark.color(.background))
            .preferredColorScheme(.dark)
            .previewDisplayName("Dark Mode")
        }
    }
}
#endif

//
//  LiquidGlassCard.swift
//  Modern Liquid Glass card component for iOS 26+
//
//  Usage:
//  LiquidGlassCard {
//      Text("Hello")
//  }
//
//  Or as modifier:
//  VStack { Text("Hello") }
//      .liquidGlassCard()
//
//  Advanced usage:
//  .liquidGlassCard(
//      glass: .clear,
//      isInteractive: true,
//      tintColor: .blue
//  )
//
//  Performance Notes:
//  - Limit to 3-5 concurrent glass effects per screen
//  - Use AnimatedGlassContainer to group multiple glass cards
//  - Debug builds monitor active glass effect count
//

import SwiftUI

// MARK: - Liquid Glass Card Container

/// Modern Liquid Glass card component for iOS 26+
///
/// Uses Apple's Liquid Glass effect for depth, vibrancy, and fluid interactions.
/// Liquid Glass provides automatic refraction, reflection, and lensing effects
/// that adapt to content, lighting, and user interaction.
///
/// ## Glass Variants
/// - `.regular` - Most versatile, adaptive variant for general use
/// - `.clear` - Transparent variant, shows content below
///
/// ## Performance Considerations
/// Liquid Glass is GPU-intensive. Limit to 3-5 concurrent effects per screen.
/// Use `AnimatedGlassContainer` to group multiple glass effects for better performance.
///
/// ## Example
/// ```swift
/// // Basic glass card
/// LiquidGlassCard {
///     Text("Hello World")
/// }
///
/// // Interactive card with tint
/// LiquidGlassCard(glassVariant: "regular", isInteractive: true, tintColor: .blue) {
///     Button("Tap Me") { print("Tapped!") }
/// }
/// ```
@available(iOS 18.0, *)
struct LiquidGlassCard<Content: View>: View {
    let content: Content
    var glassVariant: String // "regular" or "clear"
    var cornerRadius: CGFloat
    var padding: CGFloat
    var isInteractive: Bool
    var tintColor: Color?

    /// Creates a Liquid Glass card
    ///
    /// - Parameters:
    ///   - glass: Glass variant (.regular or .clear)
    ///   - cornerRadius: Corner radius of the card (default: .radiusLarge)
    ///   - padding: Internal padding (default: .spacing4)
    ///   - isInteractive: Enable touch feedback with scaling/bouncing (default: false)
    ///   - tintColor: Optional tint color for glass effect (default: nil)
    ///   - content: The content to display inside the card
    init(
        glassVariant: String = "regular",
        cornerRadius: CGFloat = .radiusLarge,
        padding: CGFloat = .spacing4,
        isInteractive: Bool = false,
        tintColor: Color? = nil,
        @ViewBuilder content: () -> Content
    ) {
        self.glassVariant = glassVariant
        self.cornerRadius = cornerRadius
        self.padding = padding
        self.isInteractive = isInteractive
        self.tintColor = tintColor
        self.content = content()
    }

    var body: some View {
        content
            .padding(padding)
            // Note: .glassEffect() is an iOS 26 API that doesn't exist yet
            // This placeholder uses existing Material effects as a fallback
            .background(
                RoundedRectangle(cornerRadius: cornerRadius, style: .continuous)
                    .fill(glassVariant == "clear" ? .thinMaterial : .ultraThinMaterial)
            )
            .onAppear {
                #if DEBUG
                if FeatureFlags.glassEffectMonitoring {
                    GlassEffectCounter.increment()
                }
                #endif
            }
            .onDisappear {
                #if DEBUG
                if FeatureFlags.glassEffectMonitoring {
                    GlassEffectCounter.decrement()
                }
                #endif
            }
    }
}

// MARK: - View Modifier Version

/// View modifier that applies Liquid Glass card styling
@available(iOS 18.0, *)
struct LiquidGlassCardModifier: ViewModifier {
    var glassVariant: String
    var cornerRadius: CGFloat
    var padding: CGFloat
    var isInteractive: Bool
    var tintColor: Color?

    func body(content: Content) -> some View {
        content
            .padding(padding)
            // Note: .glassEffect() is an iOS 26 API that doesn't exist yet
            // This placeholder uses existing Material effects as a fallback
            .background(
                RoundedRectangle(cornerRadius: cornerRadius, style: .continuous)
                    .fill(glassVariant == "clear" ? .thinMaterial : .ultraThinMaterial)
            )
            .onAppear {
                #if DEBUG
                if FeatureFlags.glassEffectMonitoring {
                    GlassEffectCounter.increment()
                }
                #endif
            }
            .onDisappear {
                #if DEBUG
                if FeatureFlags.glassEffectMonitoring {
                    GlassEffectCounter.decrement()
                }
                #endif
            }
    }
}

// MARK: - View Extension

extension View {
    /// Applies Liquid Glass card styling (iOS 26+)
    ///
    /// Creates a modern glass card with refraction, reflection, and lensing effects.
    /// Supports interactive touch feedback and custom tinting.
    ///
    /// ## Glass Variants
    /// - `.regular` - Most versatile, adaptive variant for general use (default)
    /// - `.clear` - Transparent variant that shows content below
    ///
    /// ## Performance Notes
    /// - Limit to 3-5 concurrent glass effects per screen
    /// - Use `AnimatedGlassContainer` to group multiple glass cards
    /// - Debug builds monitor active glass effect count and log warnings
    ///
    /// ## Interactive Behavior
    /// When `isInteractive` is true, the glass effect responds to touch with:
    /// - Subtle scaling animation
    /// - Bouncing feedback
    /// - Enhanced shimmer on press
    ///
    /// ## Parameters
    /// - Parameters:
    ///   - glass: Glass variant (.regular for most use cases, .clear for transparency)
    ///   - cornerRadius: Corner radius of the card (default: .radiusLarge)
    ///   - padding: Internal padding (default: .spacing4)
    ///   - isInteractive: Enable touch feedback with scaling/bouncing (default: false)
    ///   - tintColor: Optional tint color for glass effect (default: nil)
    /// - Returns: View with Liquid Glass card styling
    ///
    /// ## Examples
    /// ```swift
    /// // Basic card
    /// VStack {
    ///     Text("Hello")
    /// }
    /// .liquidGlassCard()
    ///
    /// // Interactive card with blue tint
    /// VStack {
    ///     Text("Tap me")
    /// }
    /// .liquidGlassCard(isInteractive: true, tintColor: .blue)
    /// .onTapGesture {
    ///     print("Tapped!")
    /// }
    ///
    /// // Clear glass variant
    /// VStack {
    ///     Text("Transparent")
    /// }
    /// .liquidGlassCard(glass: .clear)
    ///
    /// // Custom styling
    /// VStack {
    ///     Text("Custom")
    /// }
    /// .liquidGlassCard(
    ///     glass: .regular,
    ///     cornerRadius: .radiusSmall,
    ///     padding: .spacing3,
    ///     isInteractive: true,
    ///     tintColor: .purple
    /// )
    /// ```
    @available(iOS 18.0, *)
    func liquidGlassCard(
        glassVariant: String = "regular",
        cornerRadius: CGFloat = .radiusLarge,
        padding: CGFloat = .spacing4,
        isInteractive: Bool = false,
        tintColor: Color? = nil
    ) -> some View {
        modifier(LiquidGlassCardModifier(
            glassVariant: glassVariant,
            cornerRadius: cornerRadius,
            padding: padding,
            isInteractive: isInteractive,
            tintColor: tintColor
        ))
    }
}

// MARK: - SwiftUI Previews

