//  Modern Liquid Glass card component for iOS 26+
//
//  Usage:
//  LiquidGlassCard {
//      Text("Hello")
//  }
//
//  Or as modifier:
//  VStack { Text("Hello") }
//      .liquidGlassCard()
//
//  Advanced usage:
//  .liquidGlassCard(
//      glass: .clear,
//      isInteractive: true,
//      tintColor: .blue
//  )
//
//  Performance Notes:
//  - Limit to 3-5 concurrent glass effects per screen
//  - Use AnimatedGlassContainer to group multiple glass cards
//  - Debug builds monitor active glass effect count
//

import SwiftUI

// MARK: - Liquid Glass Card Container

/// Modern Liquid Glass card component for iOS 26+
///
/// Uses Apple's Liquid Glass effect for depth, vibrancy, and fluid interactions.
/// Liquid Glass provides automatic refraction, reflection, and lensing effects
/// that adapt to content, lighting, and user interaction.
///
/// ## Glass Variants
/// - `.regular` - Most versatile, adaptive variant for general use
/// - `.clear` - Transparent variant, shows content below
///
/// ## Performance Considerations
/// Liquid Glass is GPU-intensive. Limit to 3-5 concurrent effects per screen.
/// Use `AnimatedGlassContainer` to group multiple glass effects for better performance.
///
/// ## Example
/// ```swift
/// // Basic glass card
/// LiquidGlassCard {
///     Text("Hello World")
/// }
///
/// // Interactive card with tint
/// LiquidGlassCard(glassVariant: "regular", isInteractive: true, tintColor: .blue) {
///     Button("Tap Me") { print("Tapped!") }
/// }
/// ```
@available(iOS 18.0, *)
struct LiquidGlassCard<Content: View>: View {
    let content: Content
    var glassVariant: String // "regular" or "clear"
    var cornerRadius: CGFloat
    var padding: CGFloat
    var isInteractive: Bool
    var tintColor: Color?

    /// Creates a Liquid Glass card
    ///
    /// - Parameters:
    ///   - glass: Glass variant (.regular or .clear)
    ///   - cornerRadius: Corner radius of the card (default: .radiusLarge)
    ///   - padding: Internal padding (default: .spacing4)
    ///   - isInteractive: Enable touch feedback with scaling/bouncing (default: false)
    ///   - tintColor: Optional tint color for glass effect (default: nil)
    ///   - content: The content to display inside the card
    init(
        glassVariant: String = "regular",
        cornerRadius: CGFloat = .radiusLarge,
        padding: CGFloat = .spacing4,
        isInteractive: Bool = false,
        tintColor: Color? = nil,
        @ViewBuilder content: () -> Content
    ) {
        self.glassVariant = glassVariant
        self.cornerRadius = cornerRadius
        self.padding = padding
        self.isInteractive = isInteractive
        self.tintColor = tintColor
        self.content = content()
    }

    var body: some View {
        // Build Glass configuration with type-safe APIs
        let glass: Glass = {
            var g: Glass = glassVariant == "clear" ? .clear : .regular

            if isInteractive {
                g = g.interactive()
            }

            if let tint = tintColor {
                g = g.tint(tint)
            }

            return g
        }()

        content
            .padding(padding)
            .glassEffect(glass, in: .rect(cornerRadius: cornerRadius))
            .onAppear {
                #if DEBUG
                if FeatureFlags.glassEffectMonitoring {
                    GlassEffectCounter.increment()
                }
                #endif
            }
            .onDisappear {
                #if DEBUG
                if FeatureFlags.glassEffectMonitoring {
                    GlassEffectCounter.decrement()
                }
                #endif
            }
    }
}

