//
//  GlassEffectHelpers.swift
//  Helper utilities for Liquid Glass effects and animations
//
//  Usage:
//  @Namespace private var namespace
//
//  GlassEffectContainer(spacing: 40.0) {
//      VStack {
//          Card1().liquidGlassCard().glassID("card1", in: namespace)
//          Card2().liquidGlassCard().glassID("card2", in: namespace)
//      }
//  }
//
//  Performance Benefits:
//  - Reduces GPU overhead by batching glass rendering
//  - Enables glass elements to reflect/interact with each other
//  - Required for morphing animations with .glassID()
//
//  Note: Use the system-provided GlassEffectContainer directly.
//  The helper extensions below simplify common glass effect operations.
//

import SwiftUI

// MARK: - Glass Effect Helper Extensions

/// Helper extensions for iOS 26 Liquid Glass APIs
///
/// These extensions provide simplified syntax for working with the system's
/// Liquid Glass effects, including identity tracking, transitions, and shape unions.
///
/// ## Available Helpers
/// - `glassID(_:in:)` - Associates an identity for morphing animations
/// - `glassTransition(_:)` - Sets the transition type for glass animations
/// - `glassUnion(id:namespace:)` - Unifies multiple glass effects into one shape
///
/// ## Usage with GlassEffectContainer
/// Use the system's `GlassEffectContainer` to group multiple glass effects:
/// ```swift
/// @Namespace private var namespace
///
/// GlassEffectContainer(spacing: 40.0) {
///     VStack {
///         HeaderCard()
///             .liquidGlassCard()
///             .glassID("header", in: namespace)
///
///         DetailsCard()
///             .liquidGlassCard()
///             .glassID("details", in: namespace)
///             .glassTransition(.smooth)
///     }
/// }
/// ```
@available(iOS 18.0, *)
extension View {
    /// Associates an identity value to Liquid Glass effects for morphing animations
    ///
    /// This helper wraps the system's `.glassEffectID(_:in:)` API with a simplified
    /// syntax. Use this with `GlassEffectContainer` to enable smooth morphing
    /// transitions between glass shapes when views appear, disappear, or change state.
    ///
    /// ## How It Works
    /// Glass effect IDs create a visual relationship between glass elements.
    /// When a view with a glass ID appears or disappears, the glass effect
    /// morphs smoothly rather than fading in/out abruptly.
    ///
    /// ## Requirements
    /// - Must be used inside `GlassEffectContainer`
    /// - Requires a `@Namespace` property wrapper
    /// - IDs should be unique within the namespace
    ///
    /// ## Parameters
    /// - Parameters:
    ///   - id: Unique identifier for this glass element
    ///   - namespace: Namespace ID from `@Namespace` property wrapper
    /// - Returns: View with glass effect ID assigned
    ///
    /// ## Example: Basic Usage
    /// ```swift
    /// @Namespace private var namespace
    ///
    /// VStack {
    ///     Text("Hello")
    /// }
    /// .liquidGlassCard()
    /// .glassID("greeting", in: namespace)
    /// ```
    ///
    /// ## Example: Morphing Between States
    /// ```swift
    /// @Namespace private var namespace
    /// @State private var isExpanded = false
    ///
    /// GlassEffectContainer {
    ///     if isExpanded {
    ///         ExpandedCard()
    ///             .liquidGlassCard()
    ///             .glassID("card", in: namespace)
    ///     } else {
    ///         CollapsedCard()
    ///             .liquidGlassCard()
    ///             .glassID("card", in: namespace)
    ///     }
    /// }
    /// .onTapGesture {
    ///     withAnimation {
    ///         isExpanded.toggle()
    ///     }
    /// }
    /// ```
    ///
    /// ## Example: List with Morphing
    /// ```swift
    /// @Namespace private var namespace
    ///
    /// GlassEffectContainer {
    ///     LazyVStack {
    ///         ForEach(items) { item in
    ///             ItemRow(item: item)
    ///                 .liquidGlassCard()
    ///                 .glassID(item.id, in: namespace)
    ///         }
    ///     }
    /// }
    /// ```
    func glassID(_ id: String, in namespace: Namespace.ID) -> some View {
        self.glassEffectID(id, in: namespace)
    }

    /// Sets the transition type for glass effect animations
    ///
    /// This helper wraps the system's `.glassEffectTransition(_:)` API.
    /// Use this to control how glass effects animate when appearing or disappearing.
    ///
    /// ## Available Transitions
    /// - `.smooth` - Gentle, fluid morphing (default)
    /// - `.spring` - Bouncy, energetic transitions
    /// - `.linear` - Constant-speed transitions
    /// - `.easeInOut` - Accelerate and decelerate
    ///
    /// ## Parameters
    /// - Parameter transition: The transition type to apply
    /// - Returns: View with glass transition configured
    ///
    /// ## Example
    /// ```swift
    /// VStack {
    ///     Text("Hello")
    /// }
    /// .liquidGlassCard()
    /// .glassTransition(.spring)
    /// ```
    func glassTransition(_ transition: GlassEffectTransition) -> some View {
        self.glassEffectTransition(transition)
    }

    /// Unifies multiple glass effects into a single shape
    ///
    /// This helper wraps the system's `.glassEffectUnion(id:namespace:)` API.
    /// Use this to merge multiple glass effects into one cohesive visual element,
    /// which can improve performance and create unique visual effects.
    ///
    /// ## When to Use
    /// - Merging multiple small glass elements into one larger effect
    /// - Creating complex glass shapes from simpler components
    /// - Optimizing performance by reducing the number of separate glass layers
    ///
    /// ## Parameters
    /// - Parameters:
    ///   - id: Unique identifier for the unified glass effect
    ///   - namespace: Namespace ID from `@Namespace` property wrapper
    /// - Returns: View with glass effects unified
    ///
    /// ## Example
    /// ```swift
    /// @Namespace private var namespace
    ///
    /// HStack {
    ///     Circle()
    ///         .liquidGlassCard(variant: .clear)
    ///         .frame(width: 60, height: 60)
    ///
    ///     Circle()
    ///         .liquidGlassCard(variant: .clear)
    ///         .frame(width: 60, height: 60)
    /// }
    /// .glassUnion(id: "merged-circles", namespace: namespace)
    /// ```
    func glassUnion(id: String, namespace: Namespace.ID) -> some View {
        self.glassEffectUnion(id: id, namespace: namespace)
    }
}

// MARK: - Performance Monitoring (Debug Only)

#if DEBUG
/// Performance counter for active glass effects
///
/// Tracks the number of active glass effects on screen and logs warnings
/// when exceeding recommended limits. This helps identify performance issues
/// during development.
///
/// ## Recommended Limits
/// - **3-5 effects:** Optimal performance on all devices
/// - **6-8 effects:** Acceptable on newer devices (iPhone 14+)
/// - **9+ effects:** May cause dropped frames and performance issues
///
/// ## How It Works
/// - Automatically increments when a LiquidGlassCard appears
/// - Automatically decrements when a LiquidGlassCard disappears
/// - Logs warnings to console when exceeding 5 concurrent effects
///
/// ## Warning Example
/// ```
/// ⚠️ GLASS EFFECT WARNING: 7 glass effects active (max recommended: 5)
///    Consider grouping effects in AnimatedGlassContainer or reducing effect count.
/// ```
///
/// ## Performance Tips
/// If you see warnings:
/// 1. Group related effects in `GlassEffectContainer`
/// 2. Use `.clear` glass variant for lighter rendering
/// 3. Reduce the number of glass effects on screen
/// 4. Consider using glass effects only for key UI elements
/// 5. Profile with Instruments to identify bottlenecks
///
/// ## Manual Usage
/// While automatic tracking happens via LiquidGlassCard, you can manually
/// track custom glass implementations:
/// ```swift
/// .onAppear { GlassEffectCounter.increment() }
/// .onDisappear { GlassEffectCounter.decrement() }
/// ```
struct GlassEffectCounter {
    /// Current number of active glass effects
    static var activeCount: Int = 0

    /// Maximum recommended number of concurrent glass effects
    static let maxRecommended: Int = 5

    /// Increments the counter and logs warning if exceeding recommended limit
    static func increment() {
        activeCount += 1
        if activeCount > maxRecommended {
            print("⚠️ GLASS EFFECT WARNING: \(activeCount) glass effects active (max recommended: \(maxRecommended))")
            print("   Consider grouping effects in GlassEffectContainer or reducing effect count.")
            print("   Performance may be impacted on older devices.")

            // Additional context for common scenarios
            if activeCount > 8 {
                print("   CRITICAL: \(activeCount) effects may cause significant frame drops!")
            }
        }
    }

    /// Decrements the counter
    static func decrement() {
        activeCount = max(0, activeCount - 1)
    }

    /// Resets the counter to zero
    ///
    /// Useful for testing or when transitioning between major app sections
    static func reset() {
        activeCount = 0
    }

    /// Returns the current count (for debugging/testing)
    static func currentCount() -> Int {
        return activeCount
    }
}
#endif

// MARK: - Performance Testing Helpers (Debug Only)

#if DEBUG
@available(iOS 18.0, *)
extension View {
    /// Logs performance metrics when the view appears
    ///
    /// Use this during development to monitor performance impact
    /// of grouped glass effects within GlassEffectContainer.
    ///
    /// ## Example
    /// ```swift
    /// GlassEffectContainer {
    ///     LazyVStack {
    ///         ForEach(items) { item in
    ///             ItemCard(item: item)
    ///                 .liquidGlassCard()
    ///         }
    ///     }
    /// }
    /// .monitorPerformance(label: "Feed View")
    /// ```
    func monitorPerformance(label: String = "Glass Container") -> some View {
        self.onAppear {
            print("📊 \(label): Container appeared")
            print("   Active glass effects: \(GlassEffectCounter.currentCount())")
        }
        .onDisappear {
            print("📊 \(label): Container disappeared")
            print("   Active glass effects: \(GlassEffectCounter.currentCount())")
        }
    }
}
#endif

