//
//  LiquidGlassPlayground.swift
//  Interactive playground for testing and exploring iOS 26 Liquid Glass effects
//
//  This playground provides comprehensive examples of all Liquid Glass features:
//  - Basic glass cards (.regular and .clear variants)
//  - Interactive glass with touch feedback
//  - Morphing animations with @Namespace
//  - Glass button styles
//  - Performance monitoring
//  - Multiple glass effects grouped in containers
//
//  Usage:
//  Use Xcode's Canvas Preview or run in simulator to explore interactively.
//

import SwiftUI

struct LiquidGlassPlayground: View {
    @State private var showDetails = false
    @State private var selectedTab = 0
    @Namespace private var namespace

    var body: some View {
        if #available(iOS 18.0, *), FeatureFlags.useLiquidGlass {
            content
        } else {
            unavailableView
        }
    }

    @available(iOS 18.0, *)
    private var content: some View {
        NavigationStack {
            TabView(selection: $selectedTab) {
                basicExamplesTab
                    .tabItem {
                        Label("Basic", systemImage: "square.on.square")
                    }
                    .tag(0)

                interactiveExamplesTab
                    .tabItem {
                        Label("Interactive", systemImage: "hand.tap")
                    }
                    .tag(1)

                morphingExamplesTab
                    .tabItem {
                        Label("Morphing", systemImage: "wand.and.stars")
                    }
                    .tag(2)

                buttonExamplesTab
                    .tabItem {
                        Label("Buttons", systemImage: "button.horizontal")
                    }
                    .tag(3)

                performanceTab
                    .tabItem {
                        Label("Performance", systemImage: "gauge")
                    }
                    .tag(4)
            }
            .navigationTitle("Liquid Glass Playground")
            .navigationBarTitleDisplayMode(.inline)
        }
    }

    private var unavailableView: some View {
        VStack(spacing: 16) {
            Image(systemName: "exclamationmark.triangle")
                .font(.system(size: 60))
                .foregroundColor(.orange)

            Text("iOS 26+ Required")
                .font(.title)
                .fontWeight(.bold)

            Text("Liquid Glass Playground requires iOS 18.0 or later and feature flag enabled")
                .font(.body)
                .foregroundColor(.secondary)
                .multilineTextAlignment(.center)

            if #available(iOS 18.0, *) {
                Text("Enable feature flag: FeatureFlags.useLiquidGlass")
                    .font(.caption)
                    .foregroundColor(.secondary)
                    .padding(.top, 8)
            }
        }
        .padding()
    }

    // MARK: - Basic Examples Tab

    @available(iOS 18.0, *)
    private var basicExamplesTab: some View {
        ScrollView {
            VStack(spacing: 24) {
                sectionHeader("Basic Glass Cards", subtitle: "Explore .regular and .clear variants")

                // Regular variant
                VStack(alignment: .leading, spacing: 8) {
                    Text("Regular Glass")
                        .font(.headline)
                    Text("Most versatile variant - adapts to background")
                        .font(.caption)
                        .foregroundColor(.secondary)
                    Text("Use for cards on solid or gradient backgrounds")
                        .font(.caption2)
                        .foregroundColor(.secondary)
                }
                .liquidGlassCard(glassVariant: "regular")

                // Clear variant
                VStack(alignment: .leading, spacing: 8) {
                    Text("Clear Glass")
                        .font(.headline)
                    Text("Transparent variant - shows content below")
                        .font(.caption)
                        .foregroundColor(.secondary)
                    Text("Use for overlays on photos or rich content")
                        .font(.caption2)
                        .foregroundColor(.secondary)
                }
                .liquidGlassCard(glassVariant: "clear")

                // Different corner radii
                sectionHeader("Corner Radius Variations", subtitle: "Customize the shape")

                HStack(spacing: 12) {
                    VStack {
                        Text("Small")
                            .font(.caption)
                    }
                    .frame(maxWidth: .infinity)
                    .liquidGlassCard(cornerRadius: .radiusSmall)

                    VStack {
                        Text("Medium")
                            .font(.caption)
                    }
                    .frame(maxWidth: .infinity)
                    .liquidGlassCard(cornerRadius: .radiusMedium)

                    VStack {
                        Text("Large")
                            .font(.caption)
                    }
                    .frame(maxWidth: .infinity)
                    .liquidGlassCard(cornerRadius: .radiusLarge)
                }

                // Different padding
                sectionHeader("Padding Variations", subtitle: "Control internal spacing")

                VStack(spacing: 12) {
                    Text("Compact Padding (.spacing2)")
                        .font(.caption)
                        .liquidGlassCard(padding: .spacing2)

                    Text("Default Padding (.spacing4)")
                        .font(.caption)
                        .liquidGlassCard(padding: .spacing4)

                    Text("Spacious Padding (.spacing6)")
                        .font(.caption)
                        .liquidGlassCard(padding: .spacing6)
                }
            }
            .padding()
        }
        .background(Color(.systemGroupedBackground))
    }

    // MARK: - Interactive Examples Tab

    @available(iOS 18.0, *)
    private var interactiveExamplesTab: some View {
        ScrollView {
            VStack(spacing: 24) {
                sectionHeader("Interactive Glass", subtitle: "Touch feedback with .interactive()")

                Text("Tap these cards to feel the interactive feedback!")
                    .font(.subheadline)
                    .foregroundColor(.secondary)

                // Basic interactive
                VStack(alignment: .leading, spacing: 8) {
                    Text("Interactive Glass")
                        .font(.headline)
                    Text("Tap me! Scales and bounces on touch")
                        .font(.caption)
                        .foregroundColor(.secondary)
                }
                .liquidGlassCard(isInteractive: true)
                .onTapGesture {
                    print("Basic interactive tapped!")
                }

                // Interactive with blue tint
                VStack(alignment: .leading, spacing: 8) {
                    Text("Blue Tinted Glass")
                        .font(.headline)
                    Text("Interactive with custom tint color")
                        .font(.caption)
                        .foregroundColor(.secondary)
                }
                .liquidGlassCard(isInteractive: true, tintColor: .blue)
                .onTapGesture {
                    print("Blue tinted tapped!")
                }

                // Interactive with purple tint
                VStack(alignment: .leading, spacing: 8) {
                    Text("Purple Tinted Glass")
                        .font(.headline)
                    Text("Try different tint colors")
                        .font(.caption)
                        .foregroundColor(.secondary)
                }
                .liquidGlassCard(isInteractive: true, tintColor: .purple)
                .onTapGesture {
                    print("Purple tinted tapped!")
                }

                // Interactive with green tint
                VStack(alignment: .leading, spacing: 8) {
                    Text("Green Tinted Glass")
                        .font(.headline)
                    Text("Tints adapt to surrounding content")
                        .font(.caption)
                        .foregroundColor(.secondary)
                }
                .liquidGlassCard(isInteractive: true, tintColor: .green)
                .onTapGesture {
                    print("Green tinted tapped!")
                }

                sectionHeader("Clear Glass Interactive", subtitle: "Transparent with touch feedback")

                VStack(alignment: .leading, spacing: 8) {
                    Text("Clear Interactive Glass")
                        .font(.headline)
                    Text("Combines transparency with interactivity")
                        .font(.caption)
                        .foregroundColor(.secondary)
                }
                .liquidGlassCard(glassVariant: "clear", isInteractive: true, tintColor: .orange)
                .onTapGesture {
                    print("Clear interactive tapped!")
                }
            }
            .padding()
        }
        .background(Color(.systemGroupedBackground))
    }

    // MARK: - Morphing Examples Tab

    @available(iOS 18.0, *)
    private var morphingExamplesTab: some View {
        ScrollView {
            VStack(spacing: 24) {
                sectionHeader("Morphing Animations", subtitle: "Fluid transitions between states")

                Text("Tap the cards below to see morphing animations!")
                    .font(.subheadline)
                    .foregroundColor(.secondary)

                // Simple expand/collapse
                AnimatedGlassContainer(spacing: 40.0) {
                    VStack(spacing: 12) {
                        VStack(alignment: .leading, spacing: 8) {
                            HStack {
                                Text(showDetails ? "Expanded Card" : "Collapsed Card")
                                    .font(.headline)
                                Spacer()
                                Image(systemName: showDetails ? "chevron.up" : "chevron.down")
                                    .font(.caption)
                                    .foregroundColor(.secondary)
                            }

                            Text("Tap to toggle")
                                .font(.caption)
                                .foregroundColor(.secondary)

                            if showDetails {
                                Divider()
                                    .padding(.vertical, 4)

                                Text("Additional details revealed!")
                                    .font(.body)

                                Text("Watch how the glass morphs smoothly between states using GlassEffectContainer and .glassID()")
                                    .font(.caption)
                                    .foregroundColor(.secondary)
                            }
                        }
                        .liquidGlassCard()
                        .glassID("mainCard", in: namespace)
                        .frame(height: showDetails ? nil : 80)
                        .onTapGesture {
                            withAnimation(.spring(response: 0.4, dampingFraction: 0.8)) {
                                showDetails.toggle()
                            }
                        }
                    }
                }

                sectionHeader("Multiple Cards Morphing", subtitle: "Smooth appearance transitions")

                Text("Tap to show/hide the second card")
                    .font(.subheadline)
                    .foregroundColor(.secondary)

                AnimatedGlassContainer(spacing: 40.0) {
                    VStack(spacing: 12) {
                        VStack(alignment: .leading, spacing: 8) {
                            Text("Header Card")
                                .font(.headline)
                            Text("Always visible")
                                .font(.caption)
                                .foregroundColor(.secondary)
                        }
                        .liquidGlassCard()
                        .glassID("header", in: namespace)

                        if showDetails {
                            VStack(alignment: .leading, spacing: 8) {
                                Text("Details Card")
                                    .font(.headline)
                                Text("Appears with morphing animation")
                                    .font(.caption)
                                    .foregroundColor(.secondary)
                            }
                            .liquidGlassCard()
                            .glassID("details", in: namespace)
                            .transition(.scale.combined(with: .opacity))
                        }

                        Button(action: {
                            withAnimation(.spring(response: 0.4, dampingFraction: 0.8)) {
                                showDetails.toggle()
                            }
                        }) {
                            Text(showDetails ? "Hide Details" : "Show Details")
                                .font(.subheadline)
                                .frame(maxWidth: .infinity)
                                .padding(.vertical, 8)
                        }
                        .liquidGlassCard(isInteractive: true, tintColor: .blue)
                    }
                }

                // Best practices
                VStack(alignment: .leading, spacing: 8) {
                    Text("💡 Best Practices")
                        .font(.headline)

                    VStack(alignment: .leading, spacing: 4) {
                        bulletPoint("Use AnimatedGlassContainer to group morphing cards")
                        bulletPoint("Assign unique .glassID() to each morphing element")
                        bulletPoint("Use @Namespace property wrapper")
                        bulletPoint("Combine with .spring() animation for best results")
                        bulletPoint("Spacing parameter (40.0) controls morph distance threshold")
                    }
                    .font(.caption)
                }
                .liquidGlassCard()
            }
            .padding()
        }
        .background(Color(.systemGroupedBackground))
    }

    // MARK: - Button Examples Tab

    @available(iOS 18.0, *)
    private var buttonExamplesTab: some View {
        ScrollView {
            VStack(spacing: 24) {
                sectionHeader("Glass Button Styles", subtitle: "iOS 26+ system button styles")

                // Standard glass buttons
                VStack(spacing: 16) {
                    Text("Standard Glass Button (.glass)")
                        .font(.subheadline)
                        .foregroundColor(.secondary)

                    BrandButton(title: "Glass Button", style: .glass) {
                        print("Glass button tapped")
                    }

                    BrandButton(title: "With Icon", icon: "star.fill", style: .glass) {
                        print("Glass button with icon tapped")
                    }
                }

                // Prominent glass buttons
                VStack(spacing: 16) {
                    Text("Prominent Glass Button (.glassProminent)")
                        .font(.subheadline)
                        .foregroundColor(.secondary)

                    BrandButton(title: "Prominent Glass", style: .glassProminent) {
                        print("Prominent glass button tapped")
                    }

                    BrandButton(title: "With Icon", icon: "checkmark", style: .glassProminent) {
                        print("Prominent glass button with icon tapped")
                    }
                }

                // Comparison with traditional styles
                sectionHeader("Compare with Traditional Styles", subtitle: "All button styles")

                VStack(spacing: 12) {
                    BrandButton(title: "Primary", style: .primary) {}
                    BrandButton(title: "Secondary", style: .secondary) {}
                    BrandButton(title: "Destructive", style: .destructive) {}
                    BrandButton(title: "Outline", style: .outline) {}
                    BrandButton(title: "Glass", style: .glass) {}
                    BrandButton(title: "Glass Prominent", style: .glassProminent) {}
                }

                // Button sizes
                sectionHeader("Button Sizes", subtitle: "Small, medium, large")

                VStack(spacing: 12) {
                    BrandButton(title: "Small Glass", style: .glass, size: .small) {}
                    BrandButton(title: "Medium Glass", style: .glass, size: .medium) {}
                    BrandButton(title: "Large Glass", style: .glass, size: .large) {}
                }
            }
            .padding()
        }
        .background(Color(.systemGroupedBackground))
    }

    // MARK: - Performance Tab

    @available(iOS 18.0, *)
    private var performanceTab: some View {
        ScrollView {
            VStack(spacing: 24) {
                sectionHeader("Performance Guidelines", subtitle: "Optimize for smooth 60fps")

                // Performance tips
                VStack(alignment: .leading, spacing: 8) {
                    Text("⚡ Performance Best Practices")
                        .font(.headline)

                    VStack(alignment: .leading, spacing: 8) {
                        performanceTip(
                            "1. Limit Glass Effects",
                            description: "Use 3-5 concurrent glass effects per screen maximum"
                        )

                        performanceTip(
                            "2. Group Effects",
                            description: "Use AnimatedGlassContainer to batch rendering"
                        )

                        performanceTip(
                            "3. Choose Wisely",
                            description: ".clear variant is lighter than .regular"
                        )

                        performanceTip(
                            "4. Profile Regularly",
                            description: "Use Instruments (Core Animation) to verify 60fps"
                        )

                        performanceTip(
                            "5. Monitor in Debug",
                            description: "Debug builds warn when >5 effects are active"
                        )
                    }
                }
                .liquidGlassCard()

                // Example: 3 effects (good)
                sectionHeader("Good: 3 Glass Effects", subtitle: "Optimal performance")

                AnimatedGlassContainer(spacing: 40.0) {
                    VStack(spacing: 12) {
                        cardExample(number: 1)
                        cardExample(number: 2)
                        cardExample(number: 3)
                    }
                }

                Text("✅ This uses 3 glass effects - excellent performance")
                    .font(.caption)
                    .foregroundColor(.green)

                // Example: 5 effects (acceptable)
                sectionHeader("Acceptable: 5 Glass Effects", subtitle: "Still performs well")

                AnimatedGlassContainer(spacing: 40.0) {
                    VStack(spacing: 12) {
                        cardExample(number: 1)
                        cardExample(number: 2)
                        cardExample(number: 3)
                        cardExample(number: 4)
                        cardExample(number: 5)
                    }
                }

                Text("⚠️ This uses 5 glass effects - maximum recommended")
                    .font(.caption)
                    .foregroundColor(.orange)

                // Debug monitoring info
                VStack(alignment: .leading, spacing: 8) {
                    Text("🔍 Debug Monitoring")
                        .font(.headline)

                    Text("In debug builds, GlassEffectCounter automatically tracks active glass effects and logs warnings when exceeding 5.")
                        .font(.caption)
                        .foregroundColor(.secondary)

                    Text("Check your Xcode console for warnings like:")
                        .font(.caption2)
                        .foregroundColor(.secondary)

                    Text("⚠️ GLASS EFFECT WARNING: 6 glass effects active")
                        .font(.caption2)
                        .fontDesign(.monospaced)
                        .foregroundColor(.orange)
                        .padding(8)
                        .background(Color(.systemGray6))
                        .cornerRadius(4)
                }
                .liquidGlassCard()
            }
            .padding()
        }
        .background(Color(.systemGroupedBackground))
    }

    // MARK: - Helper Views

    private func sectionHeader(_ title: String, subtitle: String) -> some View {
        VStack(alignment: .leading, spacing: 4) {
            Text(title)
                .font(.title2)
                .fontWeight(.bold)
            Text(subtitle)
                .font(.subheadline)
                .foregroundColor(.secondary)
        }
        .frame(maxWidth: .infinity, alignment: .leading)
    }

    private func bulletPoint(_ text: String) -> some View {
        HStack(alignment: .top, spacing: 6) {
            Text("•")
            Text(text)
        }
    }

    private func performanceTip(_ title: String, description: String) -> some View {
        VStack(alignment: .leading, spacing: 4) {
            Text(title)
                .font(.subheadline)
                .fontWeight(.semibold)
            Text(description)
                .font(.caption)
                .foregroundColor(.secondary)
        }
    }

    private func cardExample(number: Int) -> some View {
        HStack {
            Text("Card \(number)")
                .font(.subheadline)
            Spacer()
            Image(systemName: "\(number).circle.fill")
                .foregroundColor(.blue)
        }
        .liquidGlassCard()
    }
}

