//
//  DesignSystem.swift
//  Generated from CSS OKLCH tokens -> sRGB (Light/Dark)
//  Font: San Francisco (system fonts)
//
//  Usage:
//  let theme = DesignTheme.defaultLight
//  Text("Hello").font(theme.font(.body)).foregroundStyle(theme.color(.foreground))
//
//  iOS 26+ Liquid Glass Support:
//  This design system supports iOS 26 Liquid Glass effects for modern, fluid UI experiences.
//  Liquid Glass provides depth, vibrancy, and interactive feedback through refraction, reflection,
//  and lensing effects.
//
//  ## New Components (iOS 26+)
//  - LiquidGlassCard: Modern glass cards with interactive feedback
//  - AnimatedGlassContainer: Groups multiple glass effects for performance and morphing animations
//  - BrandButton with .glass and .glassProminent styles
//
//  ## Usage Patterns
//
//  ### Basic Glass Card
//  ```swift
//  VStack {
//      Text("Hello")
//  }
//  .liquidGlassCard()  // iOS 26+, auto-fallback on older versions
//  ```
//
//  ### Interactive Glass Card
//  ```swift
//  VStack {
//      Text("Tap me")
//  }
//  .liquidGlassCard(isInteractive: true, tintColor: .blue)
//  .onTapGesture { }
//  ```
//
//  ### Multiple Cards with Morphing Animations
//  ```swift
//  @Namespace private var glassNamespace
//
//  AnimatedGlassContainer(spacing: 40.0) {
//      VStack {
//          Card1()
//              .liquidGlassCard()
//              .glassID("card1", in: glassNamespace)
//
//          if showDetails {
//              Card2()
//                  .liquidGlassCard()
//                  .glassID("card2", in: glassNamespace)
//                  .transition(.scale.combined(with: .opacity))
//          }
//      }
//  }
//  ```
//
//  ### Glass Buttons
//  ```swift
//  BrandButton(title: "Action", style: .glass) { }          // Standard glass button
//  BrandButton(title: "Primary", style: .glassProminent) { } // Prominent glass button
//  ```
//
//  ## Best Practices
//
//  1. **Limit Glass Effects**: Use 3-5 concurrent glass effects per screen for optimal performance
//  2. **Group Effects**: Use AnimatedGlassContainer to group multiple glass cards on the same screen
//  3. **Choose Correct Variant**:
//     - .regular: Most use cases, adaptive to background
//     - .clear: Transparency emphasis, shows content below
//  4. **Interactive Feedback**: Enable isInteractive for tappable/interactive cards
//  5. **Morphing Animations**: Use .glassID() with @Namespace for fluid state transitions
//
//  ## Performance Guidelines
//
//  - Debug builds monitor active glass effect count (warning at >5)
//  - GlassEffectContainer reduces GPU overhead by batching effects
//  - Use .clear variant for lighter rendering when transparency is key
//  - Profile with Instruments (Core Animation template) to verify 60fps
//
//  ## Migration from Legacy GlassCard
//
//  The legacy .glassCard() modifier is deprecated and automatically upgrades to Liquid Glass
//  on iOS 26+ when FeatureFlags.useLiquidGlass is enabled. For explicit control, migrate to:
//
//  ```swift
//  // Before (deprecated)
//  VStack { }.glassCard()
//
//  // After (recommended)
//  VStack { }.liquidGlassCard()
//  ```
//
//  ## Feature Flags
//
//  Use FeatureFlags.useLiquidGlass to control rollout:
//  - DEBUG: Toggle via UserDefaults key "dev.liquidGlass.enabled"
//  - RELEASE: Checks iOS version and remote config
//
//  See LiquidGlassCard.swift, GlassEffectHelpers.swift, and FeatureFlags.swift for implementation details.
//

import SwiftUI

public enum DSColorToken: String, CaseIterable {
  case background
  case foreground
  case card
  case cardforeground
  case popover
  case popoverforeground
  case primary
  case primaryforeground
  case secondary
  case secondaryforeground
  case muted
  case mutedforeground
  case accent
  case accentforeground
  case destructive
  case destructiveforeground
  case success
  case successforeground
  case warning
  case warningforeground
  case info
  case infoforeground
  case border
  case input
  case ring
  case chart1
  case chart2
  case chart3
  case chart4
  case chart5
  case sidebar
  case sidebarforeground
  case sidebarprimary
  case sidebarprimaryforeground
  case sidebaraccent
  case sidebaraccentforeground
  case sidebarborder
  case sidebarring}

public enum DSTextStyle {
  case largeTitle, title1, title2, title3, headline, subheadline, body, callout, footnote, caption1, caption2
}

public struct DesignTheme {
  public let colors: [DSColorToken: Color]
  public let colorScheme: ColorScheme
  public init(colors: [DSColorToken: Color], colorScheme: ColorScheme) {
    self.colors = colors
    self.colorScheme = colorScheme
  }
  // Color accessor
  public func color(_ token: DSColorToken) -> Color { colors[token] ?? .clear }
  // Font accessor using SF system fonts (Dynamic Type)
  public func font(_ style: DSTextStyle) -> Font {
    switch style {
    case .largeTitle: return .system(.largeTitle, design: .default)
    case .title1:     return .system(.title, design: .default)
    case .title2:     return .system(.title2, design: .default)
    case .title3:     return .system(.title3, design: .default)
    case .headline:   return .system(.headline, design: .default)
    case .subheadline:return .system(.subheadline, design: .default)
    case .body:       return .system(.body, design: .default)
    case .callout:    return .system(.callout, design: .default)
    case .footnote:   return .system(.footnote, design: .default)
    case .caption1:   return .system(.caption, design: .default)
    case .caption2:   return .system(size: 11, weight: .regular, design: .default)
    }
  }
}

extension Color {
  init(hex: String, colorSpace: Color.RGBColorSpace = .sRGB) {
    let hex = hex.trimmingCharacters(in: CharacterSet.alphanumerics.inverted)
    var int: UInt64 = 0
    Scanner(string: hex).scanHexInt64(&int)
    let r = Double((int >> 16) & 0xFF) / 255.0
    let g = Double((int >> 8) & 0xFF) / 255.0
    let b = Double(int & 0xFF) / 255.0
    self.init(colorSpace, red: r, green: g, blue: b)
  }
}

// MARK: - Theme Configuration

/// Global theme configuration for Display P3 gamut support
public class ThemeConfig {
  /// Use Display P3 color space for wider gamut on modern devices
  /// Defaults to true for modern iOS devices (iPhone 7+, iPad Pro 9.7"+)
  /// Most devices since 2016 support P3, so we default to true
  public static var useP3: Bool = true

  /// Helper to detect P3 support from a UITraitCollection
  /// Usage: Call this from a view context to update useP3 dynamically
  /// Example: ThemeConfig.updateGamutSupport(from: traitCollection)
  public static func updateGamutSupport(from traitCollection: UITraitCollection) {
    #if os(iOS)
    useP3 = traitCollection.displayGamut == .P3
    #endif
  }
}

// MARK: - Theme Factories

public extension DesignTheme {
  static let defaultLight = DesignTheme(colors: Self.makeColors(light: true), colorScheme: .light)
  static let defaultDark  = DesignTheme(colors: Self.makeColors(light: false), colorScheme: .dark)

  private static func makeColors(light: Bool) -> [DSColorToken: Color] {
    let map = light ? lightHex : darkHex
    let colorSpace: Color.RGBColorSpace = ThemeConfig.useP3 ? .displayP3 : .sRGB
    var out: [DSColorToken: Color] = [:]
    for (k, v) in map {
      // Convert hyphenated keys to camelCase to match enum cases
      let tokenKey = k.replacingOccurrences(of: "-", with: "")
      if let token = DSColorToken(rawValue: tokenKey) {
        out[token] = Color(hex: v, colorSpace: colorSpace)
      }
    }
    return out
  }

