                            .glassID("details", in: namespace)
                            .transition(.scale.combined(with: .opacity))
                        }

                        Button(action: {
                            withAnimation(.spring(response: 0.4, dampingFraction: 0.8)) {
                                showDetails.toggle()
                            }
                        }) {
                            Text(showDetails ? "Hide Details" : "Show Details")
                                .font(.subheadline)
                                .frame(maxWidth: .infinity)
                                .padding(.vertical, 8)
