                Text("✅ This uses 3 glass effects - excellent performance")
                    .font(.caption)
                    .foregroundColor(.green)

                // Example: 5 effects (acceptable)
                sectionHeader("Acceptable: 5 Glass Effects", subtitle: "Still performs well")

                AnimatedGlassContainer(spacing: 40.0) {
                    VStack(spacing: 12) {
                        cardExample(number: 1)
                        cardExample(number: 2)
                        cardExample(number: 3)
                        cardExample(number: 4)
                        cardExample(number: 5)
                    }
