                        .font(.caption2)
                        .foregroundColor(.secondary)

                    Text("⚠️ GLASS EFFECT WARNING: 6 glass effects active")
                        .font(.caption2)
                        .fontDesign(.monospaced)
                        .foregroundColor(.orange)
                        .padding(8)
                        .background(Color(.systemGray6))
                        .cornerRadius(4)
                }
                .liquidGlassCard()
            }
            .padding()
        }
        .background(Color(.systemGroupedBackground))
    }

