        HStack(alignment: .top, spacing: 6) {
            Text("•")
            Text(text)
        }
    }

    private func performanceTip(_ title: String, description: String) -> some View {
        VStack(alignment: .leading, spacing: 4) {
            Text(title)
                .font(.subheadline)
                .fontWeight(.semibold)
            Text(description)
                .font(.caption)
                .foregroundColor(.secondary)
        }
    }

