                        Text("Clear Glass")
                            .font(.headline)
                        Text("Transparent, shows content below.")
                            .font(.subheadline)
                            .foregroundColor(.secondary)
                    }
                }

                // Interactive with tint
                VStack(alignment: .leading, spacing: .spacing3) {
                    Text("Interactive Glass")
                        .font(.headline)
                    Text("Tap me! Has touch feedback.")
                        .font(.subheadline)
                        .foregroundColor(.secondary)
                }
                .liquidGlassCard(isInteractive: true, tintColor: .blue)
                .onTapGesture {
                    print("Tapped interactive card!")
                }

                // Modifier version
                VStack(alignment: .leading, spacing: .spacing3) {
                    Text("Modifier Version")
                        .font(.headline)
                    Text("Using .liquidGlassCard() modifier.")
                        .font(.subheadline)
                        .foregroundColor(.secondary)
                }
                .liquidGlassCard()
            }
            .padding()
            .background(Color(.systemGroupedBackground))
            .previewDisplayName("Light Mode")

            // Dark mode examples
            VStack(spacing: .spacing4) {
                LiquidGlassCard {
                    VStack(alignment: .leading, spacing: .spacing3) {
                        Text("Regular Glass")
                            .font(.headline)
                        Text("Dark mode example.")
                            .font(.subheadline)
                            .foregroundColor(.secondary)
                    }
                }

                LiquidGlassCard(glassVariant: "clear") {
                    VStack(alignment: .leading, spacing: .spacing3) {
                        Text("Clear Glass")
                            .font(.headline)
                        Text("With dark background.")
                            .font(.subheadline)
                            .foregroundColor(.secondary)
                    }
                }

                VStack(alignment: .leading, spacing: .spacing3) {
                    Text("Interactive Glass")
                        .font(.headline)
                    Text("With purple tint.")
                        .font(.subheadline)
                        .foregroundColor(.secondary)
                }
                .liquidGlassCard(isInteractive: true, tintColor: .purple)
                .onTapGesture {
                    print("Tapped purple card!")
                }

                // Custom corner radius
                VStack(alignment: .leading, spacing: .spacing3) {
                    Text("Custom Radius")
                        .font(.headline)
                    Text("Small corner radius example.")
                        .font(.subheadline)
                        .foregroundColor(.secondary)
                }
                .liquidGlassCard(cornerRadius: .radiusSmall)
            }
            .padding()
            .background(Color(.systemGroupedBackground))
            .preferredColorScheme(.dark)
            .previewDisplayName("Dark Mode")

            // Interactive examples
            VStack(spacing: .spacing4) {
                Text("Interactive Examples")
                    .font(.title2.bold())
                    .frame(maxWidth: .infinity, alignment: .leading)

                // Button-like interactive card
                HStack {
                    Image(systemName: "star.fill")
                        .foregroundColor(.yellow)
                    Text("Tap to favorite")
                        .font(.headline)
                    Spacer()
                    Image(systemName: "chevron.right")
                        .foregroundColor(.secondary)
                }
                .liquidGlassCard(isInteractive: true, tintColor: .orange)
                .onTapGesture {
                    print("Favorited!")
                }

                // Info card without interaction
                HStack {
                    Image(systemName: "info.circle.fill")
                        .foregroundColor(.blue)
                    VStack(alignment: .leading, spacing: .spacing1) {
                        Text("Information")
                            .font(.headline)
                        Text("Non-interactive content")
                            .font(.caption)
                            .foregroundColor(.secondary)
                    }
                    Spacer()
                }
                .liquidGlassCard(tintColor: .blue)
            }
            .padding()
            .background(Color(.systemGroupedBackground))
            .previewDisplayName("Interactive Examples")
        }
    }
}
