}
.gesture(
    MagnificationGesture()
        .onChanged { value in
            let newZoom = baseZoom * value
            let clampedZoom = min(max(newZoom, 1.0), 5.0)
            currentZoom = clampedZoom

            // Cancel previous zoom task to debounce
            zoomTask?.cancel()

            // Create new task with slight delay for debouncing
            zoomTask = Task {
                // Small delay to debounce rapid gesture changes
                try? await Task.sleep(nanoseconds: 50_000_000) // 50ms
                guard !Task.isCancelled else { return }
                await viewModel.setZoom(clampedZoom)
            }
        }
        .onEnded { value in
            baseZoom = currentZoom

            // Cancel debounce and apply final zoom immediately
            zoomTask?.cancel()
            Task {
                await viewModel.setZoom(currentZoom)
            }
        }
)
