import SwiftUI
import LiveKit

struct LiveStreamView: View {
    @Environment(\.designTheme) private var theme
    @Environment(\.dismiss) private var dismiss

    @StateObject private var viewModel = LiveStreamViewModel()

    let streamTitle: String
    let streamDescription: String
    let isCameraEnabled: Bool
    let isMicrophoneEnabled: Bool

    @State private var isCameraOn: Bool
    @State private var isMicrophoneOn: Bool
    @State private var showingEndConfirmation = false
    @State private var showScoreboard = false
    @State private var baseZoom: CGFloat = 1.0
    @State private var currentZoom: CGFloat = 1.0
    @State private var zoomTask: Task<Void, Never>?

    // Scoreboard state
    @State private var homeTeamName = "Home"
    @State private var awayTeamName = "Away"
    @State private var homeScore = 0
    @State private var awayScore = 0
    @State private var homeSets = 0
    @State private var awaySets = 0

    init(streamTitle: String, streamDescription: String, isCameraEnabled: Bool, isMicrophoneEnabled: Bool) {
        self.streamTitle = streamTitle
        self.streamDescription = streamDescription
        self.isCameraEnabled = isCameraEnabled
        self.isMicrophoneEnabled = isMicrophoneEnabled
        _isCameraOn = State(initialValue: isCameraEnabled)
        _isMicrophoneOn = State(initialValue: isMicrophoneEnabled)
    }

    var body: some View {
        ZStack {
            // Background - Video Feed
            theme.color(.muted)
                .ignoresSafeArea()

            // Video View
            if let videoTrack = viewModel.getLocalVideoTrack() {
                VideoViewWrapper(track: videoTrack)
                    .ignoresSafeArea()
            }

            // Top Bar - Live Badge
            VStack {
                HStack {
                    HStack(spacing: .spacing2) {
                        Circle()
                            .fill(theme.color(.destructive))
                            .frame(width: 8, height: 8)

                        Text("LIVE")
                            .font(theme.font(.caption2))
                            .fontWeight(.bold)
                            .foregroundStyle(theme.color(.foreground))

                        Text("\(viewModel.viewerCount)")
                            .font(theme.font(.caption2))
                            .foregroundStyle(theme.color(.foreground).opacity(0.9))
                    }
                    .padding(.horizontal, .spacing3)
                    .padding(.vertical, .spacing2)
                    .background(
                        Capsule()
                            .fill(theme.color(.muted).opacity(0.7))
                    )

                    Spacer()
                }
                .padding(.spacing4)

                Spacer()
            }
            .allowsHitTesting(false)

            // Right Side Controls
            VStack {
                Spacer()

                VStack(spacing: .spacing4) {
                    // End Stream Button
                    Button(action: {
                        showingEndConfirmation = true
                    }) {
                        Image(systemName: "xmark.circle.fill")
                            .font(theme.font(.largeTitle))
                            .foregroundStyle(theme.color(.foreground))
                            .background(
                                Circle()
                                    .fill(theme.color(.muted).opacity(0.3))
                                    .frame(width: 64, height: 64)
                            )
                    }

                    // Flip Camera
                    Button(action: {
                        Task {
                            await viewModel.flipCamera()
                        }
                    }) {
                        Image(systemName: "arrow.triangle.2.circlepath.camera")
                            .font(theme.font(.title1))
                            .foregroundStyle(theme.color(.foreground))
                            .frame(width: 64, height: 64)
                            .background(
                                Circle()
                                    .fill(theme.color(.muted).opacity(0.5))
                            )
                    }

                    // Camera Toggle
                    Button(action: {
                        isCameraOn.toggle()
                        Task {
                            await viewModel.toggleCamera()
                        }
                    }) {
                        Image(systemName: isCameraOn ? "video.fill" : "video.slash.fill")
                            .font(theme.font(.title1))
                            .foregroundStyle(theme.color(.foreground))
                            .frame(width: 64, height: 64)
                            .background(
                                Circle()
                                    .fill(isCameraOn ? theme.color(.primary) : theme.color(.destructive))
                            )
                    }

                    // Microphone Toggle
                    Button(action: {
                        isMicrophoneOn.toggle()
                        Task {
                            await viewModel.toggleMicrophone()
                        }
                    }) {
                        Image(systemName: isMicrophoneOn ? "mic.fill" : "mic.slash.fill")
                            .font(theme.font(.title1))
                            .foregroundStyle(theme.color(.foreground))
                            .frame(width: 64, height: 64)
                            .background(
                                Circle()
                                    .fill(isMicrophoneOn ? theme.color(.primary) : theme.color(.destructive))
                            )
                    }
                }
                .padding(.trailing, .spacing5)
                .padding(.bottom, 100)
            }
            .frame(maxWidth: .infinity, alignment: .trailing)

            // Bottom Scoreboard
            VStack {
                Spacer()

                if showScoreboard {
                    HStack(spacing: .spacing3) {
                        // Home Team
                        VStack(spacing: .spacing1) {
                            Text(homeTeamName)
                                .font(theme.font(.caption1))
                                .fontWeight(.bold)
                                .foregroundStyle(theme.color(.foreground))

                            HStack(spacing: .spacing3) {
                                // Score digits
                                Text("\(homeScore / 10)")
                                    .font(theme.font(.largeTitle))
                                    .fontWeight(.bold)
                                    .foregroundStyle(theme.color(.foreground))
                                    .frame(width: 30)
                                    .background(theme.color(.primary))
                                    .clipShape(RoundedRectangle(cornerRadius: .radiusSmall))

                                Text("\(homeScore % 10)")
                                    .font(theme.font(.largeTitle))
                                    .fontWeight(.bold)
                                    .foregroundStyle(theme.color(.foreground))
                                    .frame(width: 30)
                                    .background(theme.color(.primary))
                                    .clipShape(RoundedRectangle(cornerRadius: .radiusSmall))
                            }

                            // Score controls
                            HStack(spacing: .spacing2) {
                                Button(action: {
                                    if homeScore > 0 { homeScore -= 1 }
                                }) {
                                    Image(systemName: "minus.circle.fill")
                                        .font(theme.font(.body))
                                        .foregroundStyle(theme.color(.foreground).opacity(0.8))
                                }

                                Button(action: { homeScore += 1 }) {
                                    Image(systemName: "plus.circle.fill")
                                        .font(theme.font(.body))
                                        .foregroundStyle(theme.color(.foreground).opacity(0.8))
                                }
                            }
                        }

                        // Set indicator
                        VStack(spacing: .spacing1) {
                            Circle()
                                .fill(theme.color(.foreground))
                                .frame(width: 32, height: 32)
                                .overlay(
                                    Text("\(homeSets + awaySets + 1)")
                                        .font(theme.font(.headline))
                                        .fontWeight(.bold)
                                        .foregroundStyle(theme.color(.background))
                                )

                            Text("Set")
                                .font(theme.font(.caption2))
                                .foregroundStyle(theme.color(.foreground).opacity(0.8))

                            // Set controls
                            HStack(spacing: .spacing2) {
                                Button(action: {
                                    homeSets += 1
                                    homeScore = 0
                                    awayScore = 0
                                }) {
                                    Image(systemName: "arrowtriangle.left.fill")
                                        .font(theme.font(.caption2))
                                        .foregroundStyle(theme.color(.primary))
                                }

                                Button(action: {
                                    awaySets += 1
                                    homeScore = 0
                                    awayScore = 0
                                }) {
                                    Image(systemName: "arrowtriangle.right.fill")
                                        .font(theme.font(.caption2))
                                        .foregroundStyle(theme.color(.destructive))
                                }
                            }
                        }
                        .padding(.horizontal, .spacing2)

                        // Away Team
                        VStack(spacing: .spacing1) {
                            Text(awayTeamName)
                                .font(theme.font(.caption1))
                                .fontWeight(.bold)
                                .foregroundStyle(theme.color(.foreground))

                            HStack(spacing: .spacing3) {
                                // Score digits
                                Text("\(awayScore / 10)")
                                    .font(theme.font(.largeTitle))
                                    .fontWeight(.bold)
                                    .foregroundStyle(theme.color(.foreground))
                                    .frame(width: 30)
                                    .background(theme.color(.destructive))
                                    .clipShape(RoundedRectangle(cornerRadius: .radiusSmall))

                                Text("\(awayScore % 10)")
                                    .font(theme.font(.largeTitle))
                                    .fontWeight(.bold)
                                    .foregroundStyle(theme.color(.foreground))
                                    .frame(width: 30)
                                    .background(theme.color(.destructive))
                                    .clipShape(RoundedRectangle(cornerRadius: .radiusSmall))
                            }

                            // Score controls
                            HStack(spacing: .spacing2) {
                                Button(action: {
                                    if awayScore > 0 { awayScore -= 1 }
                                }) {
                                    Image(systemName: "minus.circle.fill")
                                        .font(theme.font(.body))
                                        .foregroundStyle(theme.color(.foreground).opacity(0.8))
                                }

                                Button(action: { awayScore += 1 }) {
                                    Image(systemName: "plus.circle.fill")
                                        .font(theme.font(.body))
                                        .foregroundStyle(theme.color(.foreground).opacity(0.8))
                                }
                            }
                        }
                    }
                    .padding(.vertical, .spacing3)
                    .padding(.horizontal, .spacing5)
                    .background(
                        RoundedRectangle(cornerRadius: .radiusMedium)
                            .fill(theme.color(.muted).opacity(0.8))
                    )
                    .padding(.bottom, .spacing10)
                }
            }

            // Scoreboard toggle button (bottom left)
            VStack {
                Spacer()
                HStack {
                    Button(action: {
                        showScoreboard.toggle()
                    }) {
                        Image(systemName: showScoreboard ? "chart.bar.fill" : "chart.bar")
                            .font(theme.font(.title3))
                            .foregroundStyle(theme.color(.foreground))
                            .frame(width: 50, height: 50)
                            .background {
                                if showScoreboard {
                                    Circle()
                                        .fill(theme.color(.success))
                                } else {
                                    Circle()
                                        .fill(.ultraThinMaterial)
                                }
                            }
                            .overlay(
                                Circle()
                                    .stroke(theme.color(.border), lineWidth: 1)
                            )
                    }
                    .padding(.leading, .spacing5)
                    .padding(.bottom, .spacing10)

                    Spacer()
                }
            }

            // Error Message
            if let error = viewModel.errorMessage {
                VStack {
                    Spacer()

                    Text(error)
                        .font(theme.font(.caption2))
                        .foregroundStyle(theme.color(.foreground))
                        .padding(.spacing4)
                        .background(
                            RoundedRectangle(cornerRadius: .radiusSmall)
                                .fill(theme.color(.destructive).opacity(0.8))
                        )
                        .padding(.spacing4)

                    Spacer()
                }
            }
        }
        .gesture(
            MagnificationGesture()
                .onChanged { value in
                    let newZoom = baseZoom * value
                    let clampedZoom = min(max(newZoom, 1.0), 5.0)
                    currentZoom = clampedZoom

                    // Cancel previous zoom task
                    zoomTask?.cancel()

                    // Apply zoom immediately without debounce for real-time feel
                    zoomTask = Task {
                        await viewModel.setZoom(clampedZoom)
                    }
                }
                .onEnded { value in
                    baseZoom = currentZoom

                    // Apply final zoom to ensure it's set
                    zoomTask?.cancel()
                    Task {
                        await viewModel.setZoom(currentZoom)
                    }
                }
        )
        .task {
            // Create session and start publishing when view appears
            await viewModel.createAndConnect(
                title: streamTitle,
                participantName: "Athlete",
                description: streamDescription
            )
            await viewModel.startPublishing(enableCamera: isCameraEnabled, enableMicrophone: isMicrophoneEnabled)
        }
        .confirmationDialog("End Stream", isPresented: $showingEndConfirmation, titleVisibility: .visible) {
            Button("End Stream", role: .destructive) {
                Task {
                    await viewModel.disconnect()
                    dismiss()
                }
            }
            Button("Cancel", role: .cancel) {}
        } message: {
            Text("Are you sure you want to end your live stream?")
        }
        .persistentSystemOverlays(.hidden)
        .statusBar(hidden: true)
        .supportedOrientations([.portrait, .landscapeLeft, .landscapeRight])
    }
}

// Add extension to support orientation changes
extension View {
    func supportedOrientations(_ orientations: UIInterfaceOrientationMask) -> some View {
        self.onAppear {
            AppDelegate.orientationLock = orientations
        }
        .onDisappear {
            AppDelegate.orientationLock = .portrait
        }
    }
}

class AppDelegate: NSObject, UIApplicationDelegate {
    static var orientationLock = UIInterfaceOrientationMask.portrait

    func application(_ application: UIApplication, supportedInterfaceOrientationsFor window: UIWindow?) -> UIInterfaceOrientationMask {
        return AppDelegate.orientationLock
    }
}

