import Foundation
import Combine
import LiveKit
import SwiftUI
import AVFoundation

@MainActor
class LiveStreamViewModel: ObservableObject {
    @Published var isConnected = false
    @Published var isPublishing = false
    @Published var errorMessage: String?
    @Published var viewerCount = 0
    @Published var currentSessionId: String?

    private var room: Room?

    /// Generate a URL-safe room name from a title
    private func generateRoomName(from title: String) -> String {
        // Convert to lowercase, replace spaces and special chars with hyphens
        let normalized = title.lowercased()
            .replacingOccurrences(of: " ", with: "-")
            .replacingOccurrences(of: "[^a-z0-9-]", with: "", options: .regularExpression)
            .replacingOccurrences(of: "-+", with: "-", options: .regularExpression)
            .trimmingCharacters(in: CharacterSet(charactersIn: "-"))

        // Add timestamp to ensure uniqueness
        let timestamp = Int(Date().timeIntervalSince1970)
        return "\(normalized)-\(timestamp)"
    }

    /// Connect to an existing session using the session ID
    func connectToSession(sessionId: String, participantName: String) async {
        do {
            print("🔵 [LiveKit] Joining session: \(sessionId)")
            print("   Participant: \(participantName)")

            // Get session and join to get token/server URL from production API
            let joinResponse = try await LivestreamService.shared.joinSession(
                sessionId: sessionId,
                participantName: participantName
            )

            print("✅ [LiveKit] Join response received")
            print("   Room: \(joinResponse.roomName)")
            print("   Server: \(joinResponse.serverUrl)")

            // Store session ID
            currentSessionId = sessionId

            // Connect to LiveKit room
            print("🔵 [LiveKit] Connecting to room...")
            let room = Room()
            self.room = room
            try await room.connect(url: joinResponse.serverUrl, token: joinResponse.token)

            isConnected = true
            print("✅ [LiveKit] Connected to room successfully")
        } catch {
            print("❌ [LiveKit] Failed to connect: \(error.localizedDescription)")
            errorMessage = "Failed to connect: \(error.localizedDescription)"
        }
    }

    /// Create a new session and connect to it (for streamers)
    func createAndConnect(title: String, participantName: String, description: String? = nil) async {
        do {
            // Generate room name from title
            let roomName = generateRoomName(from: title)
            print("🔴 [LiveKit] Creating session with title: '\(title)'")
            print("   Generated room name: '\(roomName)'")

            // Create session via production API - this returns token and serverUrl directly
            let session = try await LivestreamService.shared.createSession(
                title: title,
                roomName: roomName,
                description: description
            )

            print("✅ [LiveKit] Session created successfully")
            print("   Session ID: \(session.id)")
            print("   Room Name: \(session.roomName)")
            print("   Token received: \(String(session.token.prefix(20)))...")
            print("   Server URL: \(session.serverUrl)")

            // Store session ID
            currentSessionId = session.id

            // Connect to LiveKit room using the token from create response
            print("🔵 [LiveKit] Connecting to room...")
            let room = Room()
            self.room = room
            try await room.connect(url: session.serverUrl, token: session.token)

            isConnected = true
            print("✅ [LiveKit] Connected to room successfully")

            // Mark session as active (started)
            do {
                print("🔵 [LiveKit] Starting session (marking as active)...")
                let startedSession = try await LivestreamService.shared.startSession(sessionId: session.id)
                print("✅ [LiveKit] Session marked as active")
                print("   Status: \(startedSession.status)")
            } catch {
                print("❌ [LiveKit] Failed to start session: \(error.localizedDescription)")
                // Don't throw - we're already connected, just log the error
                errorMessage = "Warning: Connected but failed to mark session as active"
            }

        } catch {
            print("❌ [LiveKit] Failed to create session: \(error.localizedDescription)")
            errorMessage = "Failed to create session: \(error.localizedDescription)"
        }
    }

    func startPublishing(enableCamera: Bool, enableMicrophone: Bool) async {
        guard let room = room else {
            print("❌ [LiveKit] Cannot start publishing - no room")
            return
        }

        do {
            print("🎥 [LiveKit] Starting publishing...")
            print("   Camera: \(enableCamera ? "ON" : "OFF")")
            print("   Microphone: \(enableMicrophone ? "ON" : "OFF")")

            // Enable camera if requested with custom dimensions matching screen aspect ratio
            if enableCamera {
                // Calculate device screen aspect ratio
                let screenBounds = UIScreen.main.bounds
                let screenWidth = screenBounds.width
                let screenHeight = screenBounds.height
                let screenAspectRatio = screenHeight / screenWidth

                print("📱 [LiveKit] Screen dimensions: \(screenWidth) x \(screenHeight)")
                print("📐 [LiveKit] Screen aspect ratio: \(screenAspectRatio)")

                // Use custom dimensions to match screen aspect ratio
                // Calculate height for 720p width maintaining screen aspect ratio
                let captureWidth = 720
                let captureHeight = Int(Double(captureWidth) * Double(screenAspectRatio))
                let customDimensions = Dimensions(width: Int32(captureWidth), height: Int32(captureHeight))

                print("🎥 [LiveKit] Camera capture dimensions: \(captureWidth) x \(captureHeight)")

                let cameraOptions = CameraCaptureOptions(
                    dimensions: customDimensions,
                    fps: 30
                )

                try await room.localParticipant.setCamera(enabled: true, captureOptions: cameraOptions)
                print("✅ [LiveKit] Camera enabled with custom aspect ratio")
            }

            // Enable microphone if requested
            if enableMicrophone {
                try await room.localParticipant.setMicrophone(enabled: true)
                print("✅ [LiveKit] Microphone enabled")
            }

            isPublishing = true
            print("✅ [LiveKit] Publishing started")
        } catch {
            print("❌ [LiveKit] Failed to start publishing: \(error.localizedDescription)")
            errorMessage = "Failed to start publishing: \(error.localizedDescription)"
        }
    }

    func toggleCamera() async {
        guard let room = room else { return }

        do {
            let isEnabled = room.localParticipant.isCameraEnabled()
            try await room.localParticipant.setCamera(enabled: !isEnabled)
        } catch {
            errorMessage = "Failed to toggle camera: \(error.localizedDescription)"
        }
    }

    func toggleMicrophone() async {
        guard let room = room else { return }

        do {
            let isEnabled = room.localParticipant.isMicrophoneEnabled()
            try await room.localParticipant.setMicrophone(enabled: !isEnabled)
        } catch {
            errorMessage = "Failed to toggle microphone: \(error.localizedDescription)"
        }
    }

    func flipCamera() async {
        guard let room = room else { return }

        // Find the camera video track
        for publication in room.localParticipant.videoTracks {
            guard publication.source == .camera,
                  let videoTrack = publication.track as? LocalVideoTrack,
                  let cameraCapturer = videoTrack.capturer as? CameraCapturer else {
                continue
            }

            do {
                try await cameraCapturer.switchCameraPosition()
                return
            } catch {
                errorMessage = "Failed to flip camera: \(error.localizedDescription)"
                return
            }
        }
    }

    func setZoom(_ zoom: CGFloat) async {
        guard let room = room else { return }

        // Find the camera video track
        for publication in room.localParticipant.videoTracks {
            guard publication.source == .camera,
                  let videoTrack = publication.track as? LocalVideoTrack,
                  let cameraCapturer = videoTrack.capturer as? CameraCapturer,
                  let device = cameraCapturer.device else {
                continue
            }

            // Move to background queue to avoid blocking main thread
            await withCheckedContinuation { continuation in
                DispatchQueue.global(qos: .userInitiated).async {
                    do {
                        // Validate zoom is within acceptable range
                        let minZoom = device.minAvailableVideoZoomFactor
                        let maxZoom = device.maxAvailableVideoZoomFactor
                        let clampedZoom = min(max(zoom, minZoom), maxZoom)

                        // Lock device for configuration
                        try device.lockForConfiguration()

                        // Use direct videoZoomFactor for instant response during pinch
                        // This provides real-time feedback as user pinches
                        device.videoZoomFactor = clampedZoom

                        device.unlockForConfiguration()
                        continuation.resume()
                    } catch {
                        // Update error on main thread
                        Task { @MainActor in
                            self.errorMessage = "Failed to set zoom: \(error.localizedDescription)"
                        }
                        continuation.resume()
                    }
                }
            }
            return
        }
    }

    func disconnect() async {
        // End the session if we have one
        if let sessionId = currentSessionId {
            do {
                let _ = try await LivestreamService.shared.endSession(sessionId: sessionId)
            } catch {
                errorMessage = "Failed to end session: \(error.localizedDescription)"
            }
        }

        await room?.disconnect()
        room = nil
        isConnected = false
        isPublishing = false
        currentSessionId = nil
    }

    func getLocalVideoTrack() -> VideoTrack? {
        guard let room = room else { return nil }

        return room.localParticipant.videoTracks.first?.track as? VideoTrack
    }
}

