   301‚Üí      </UserDispatchContext.Provider>
   302‚Üí    </UserContext.Provider>
   303‚Üí  );
   304‚Üí}
   305‚Üí
   306‚Üí// Usage in components
   307‚Üífunction UserMenu() {
   308‚Üí  const user = useUser();
   309‚Üí  const { logout } = useUserDispatch();
   310‚Üí
   311‚Üí  return (
   312‚Üí    <div>
   313‚Üí      <span>{user.name}</span>
   314‚Üí      <button onClick={logout}>Logout</button>
   315‚Üí    </div>
